stepTemplates.template:
const axios = require('axios');
const htmlMinifierTerser = require('html-minifier-terser');
const linkCheck = require('link-check');
const joi = require('@hapi/joi');
const Storage = require('or-sdk/storage');
const Request = require('or-sdk/request');
const timestring = require('timestring');
const validator = require('html-validator');
const cheerio = require('cheerio');
const striptags = require('striptags');
const { v4: uuid } = require('uuid');
const moment = require('moment');
const planer = require('planer');
const { JSDOM } = require('jsdom');
const dom = new JSDOM();
const emailjs = require('emailjs');
const htmlToText = require('html-to-text');
const mimeTypes = require('mime-types');
const fetch = require('node-fetch');

const request = new Request(this);
const storage = new Storage(this);

let key = emailConfig.id;
let emailRecord = emailConfig;
const iterationCount = this.session.get(`iterations_${this.currentStepId}${this.config.flowId}`) || 0;
const iterationDepth = 1;

const emailRegex = /(mailto:)|[a-zA-Z0-9.!#$%&'*+\/=?^_`{|}~-]+@[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?(?:\.[a-zA-Z0-9](?:[a-zA-Z0-9-]{0,61}[a-zA-Z0-9])?)*/i;
const linkRegex = /(?:(?:https?|ftp):\/\/|\b(?:[a-z\d]+\.))(?:(?:[^\s()<>]+|\((?:[^\s()<>]+|(?:\([^\s()<>]+\)))?\))+(?:\((?:[^\s()<>]+|(?:\(?:[^\s()<>]+\)))?\)|[^\d\s`!()\[\]{};:'".,<>?""'']))?/i;
const plainLinkRegex = /((((((http|ftp|https):\/{2})|www\.)+(([0-9a-z_-]+\.))|([0-9a-z_-]+@[0-9a-z_-]+\.)|([0-9a-z_-]+\.))+(aero|asia|biz|cat|com|coop|edu|gov|info|int|jobs|mil|mobi|museum|name|net|org|pro|tel|travel|ac|ad|ae|af|ag|ai|al|am|an|ao|aq|ar|as|at|au|aw|ax|az|ba|bb|bd|be|bf|bg|bh|bi|bj|bm|bn|bo|br|bs|bt|bv|bw|by|bz|ca|cc|cd|cf|cg|ch|ci|ck|cl|cm|cn|co|cr|cu|cv|cx|cy|cz|cz|de|dj|dk|dm|do|dz|ec|ee|eg|er|es|et|eu|fi|fj|fk|fm|fo|fr|ga|gb|gd|ge|gf|gg|gh|gi|gl|gm|gn|gp|gq|gr|gs|gt|gu|gw|gy|hk|hm|hn|hr|ht|hu|id|ie|il|im|in|io|iq|ir|is|it|je|jm|jo|jp|ke|kg|kh|ki|km|kn|kp|kr|kw|ky|kz|la|lb|lc|li|lk|lr|ls|lt|lu|lv|ly|ma|mc|md|me|mg|mh|mk|ml|mn|mn|mo|mp|mr|ms|mt|mu|mv|mw|mx|my|mz|na|nc|ne|nf|ng|ni|nl|no|np|nr|nu|nz|nom|pa|pe|pf|pg|ph|pk|pl|pm|pn|pr|ps|pt|pw|py|qa|re|ra|rs|ru|rw|sa|sb|sc|sd|se|sg|sh|si|sj|sj|sk|sl|sm|sn|so|sr|st|su|sv|sy|sz|tc|td|tf|tg|th|tj|tk|tl|tm|tn|to|tp|tr|tt|tv|tw|tz|ua|ug|uk|us|uy|uz|va|vc|ve|vg|vi|vn|vu|wf|ws|ye|yt|yu|za|zm|zw|arpa)(:[0-9]+)?((\/([~0-9a-zA-Z\#\+\%@\.\/_-]+))?(\?[0-9a-zA-Z\+\%@\/&\[\];=_-]+)?)?))\b/gi
const wysiwygRegex = /<span class="or-mf">((((((http|ftp|https):\/{2})|www\.)+(([0-9a-z_-]+\.))|([0-9a-z_-]+@[0-9a-z_-]+\.)|([0-9a-z_-]+\.))+(aero|asia|biz|cat|com|coop|edu|gov|info|int|jobs|mil|mobi|museum|name|net|org|pro|tel|travel|ac|ad|ae|af|ag|ai|al|am|an|ao|aq|ar|as|at|au|aw|ax|az|ba|bb|bd|be|bf|bg|bh|bi|bj|bm|bn|bo|br|bs|bt|bv|bw|by|bz|ca|cc|cd|cf|cg|ch|ci|ck|cl|cm|cn|co|cr|cu|cv|cx|cy|cz|cz|de|dj|dk|dm|do|dz|ec|ee|eg|er|es|et|eu|fi|fj|fk|fm|fo|fr|ga|gb|gd|ge|gf|gg|gh|gi|gl|gm|gn|gp|gq|gr|gs|gt|gu|gw|gy|hk|hm|hn|hr|ht|hu|id|ie|il|im|in|io|iq|ir|is|it|je|jm|jo|jp|ke|kg|kh|ki|km|kn|kp|kr|kw|ky|kz|la|lb|lc|li|lk|lr|ls|lt|lu|lv|ly|ma|mc|md|me|mg|mh|mk|ml|mn|mn|mo|mp|mr|ms|mt|mu|mv|mw|mx|my|mz|na|nc|ne|nf|ng|ni|nl|no|np|nr|nu|nz|nom|pa|pe|pf|pg|ph|pk|pl|pm|pn|pr|ps|pt|pw|py|qa|re|ra|rs|ru|rw|sa|sb|sc|sd|se|sg|sh|si|sj|sj|sk|sl|sm|sn|so|sr|st|su|sv|sy|sz|tc|td|tf|tg|th|tj|tk|tl|tm|tn|to|tp|tr|tt|tv|tw|tz|ua|ug|uk|us|uy|uz|va|vc|ve|vg|vi|vn|vu|wf|ws|ye|yt|yu|za|zm|zw|arpa)(:[0-9]+)?((\/([~0-9a-zA-Z\#\+\%@\.\/_-]+))?(\?[0-9a-zA-Z\+\%@\/&\[\];=_-]+)?)?))\b<\/span>/gmi

//---------------- report Transcript Event ----------------
// generate / read transcript id
const transcriptId = this.get(`${this.currentStepId}.transcriptId`) || uuid();
this.set(`${this.currentStepId}.transcriptId`, transcriptId);

const report = (direction, params) => {
  const { transcriptId, previousTranscriptId, botIdentifier, endUserIdentifier } = params;
  const mediaUrl = _.get(params, 'attachements');

  let reportingObject = {
    eventId            : transcriptId,
    botIdentifier      : botIdentifier,
    channel            : 'Email',
    toIdentifier         : endUserIdentifier,
    fromIdentifier       : botIdentifier,
    fromIdentifierType   : 'Email',
    fromId               : this.config.flowId,
    fromType             : 'Bot',
    toType               : 'Visitor',
    toIdentifierType     : 'Email',
    reportingSettingsKey : 'transcript',
    message            : _.get(params, 'message.text'),
    media              : _.map(mediaUrl, file => ({
      fileUrl  : file.location,
      fileType : 'File'
    })),
    previousTranscriptId
  };

  return this.reporter.reportTranscriptEvent(reportingObject);
};
//----------------------------------------------------------

const getCurrentMode = () => {
  if (mode.value) {
    return mode.value;
  } else {
    return mode;
  }
}

const getStepTimeout = () => {
  try {
    const timeout = _.isObject(timeoutLeng) ? timestring(_.get(timeoutLeng, 'input'), _.get(timeoutLeng, 'outputMode')) : timeofutLeng;

    const ranges = {
        low: 10000,
        up: 2629800000
    }

    if (timeout > ranges.up || timeout < ranges.low) {
        throw 'invalid timeout: Must be a timestring duration value between 10 s and 1 month'
    }

    return timeout;
  } catch (error) {
    const err = {
      name: "Invalid timeout duration",
      code: null,
      message: `${error}`
    }
    throw err
  }
}

const getRemainingTimeout = () => {
  const stepTimeout = getStepTimeout()

  if (this.session.timeout >= stepTimeout) {
    return stepTimeout - (new Date().getTime() - new Date(this.session.startDate).getTime());
  } else {
    return this.session.timeout - (new Date().getTime() - new Date(this.session.startDate).getTime());
  }
}

const getSenderAddress = () => {
  const subDomain = senderSubDomain ? `${senderSubDomain}.` : '';
  if (emailConfig.type !== 'advanced') return emailConfig.address;
  return `${senderAddress}@${subDomain}${emailConfig.domain}`;
};

const getReplyAddress = () => {
  const replySubDomain = replyToSubDomain ? `${replyToSubDomain}.` : '';
  if (emailConfig.type !== 'advanced') return emailRecord.address;
  return `${replyToAddress}@${replySubDomain}${emailConfig.domain}`;
};

const getRecipientsArray = () => {
  return toList.concat(ccList).concat(bccList)
    .map(r => r.replace(/.*\s*<(.+)>/, (match, email) => email))
    .map(r => r.trim());
};

const getFirstRef = () => {
  const firstRef =  Array.isArray(references) ? references[0] : references.split(' ')[0];
  return firstRef ? firstRef.replace(/[<>]/g, '') : null;
};

const isHtmlContent = () => {
  if (!mode) return;
  if (mode.value) {
    if (mode.value === 'plain') return false;
  } else {
    if (mode === 'plain') return false;
  }
  return true;
};

const isTemplateString = () => {
  if (mode.value) return mode.value === 'template';
  return mode === 'template';
}

const isHtmlMarkdown = () => {
  if (mode.value) return mode.value === 'visual';
  return mode === 'visual';
}

const checkAtt = (arr) => {
  if (arr) {
    const filtered = arr.filter(el => el.alternative)
    return filtered.length === arr.length
  }
  return false
}

const checkFileExtension = (file) => {
  var extension = file.substr((file.lastIndexOf('.') +1));
  // blocked by amazon
  if (!/(ade|adp|app|asp|bas|bat|cer|chm|cmd|com|cpl|crt|csh|der|exe|fxp|gadget|hlp|hta|inf|ins|isp|its|js|jse|ksh|lib|lnk|mad|maf|mag|mam|maq|mar|mas|mat|mau|mav|maw|mda|mdb|mde|mdt|mdw|mdz|msc|msh|msh1|msh2|mshxml|msh1xml|msh2xml|msi|msp|mst|ops|pcd|pif|plg|prf|prg|reg|scf|scr|sct|shb|shs|sys|ps1|ps1xml|ps2|ps2xml|psc1|psc2|tmp|url|vb|vbe|vbs|vps|vsmacros|vss|vst|vsw|vxd|ws|wsc|wsf|wsh|xnk)$/ig.test(extension)) {
    return {
      isValid: true,
      message: file
    }
  } else {
    return {
      isValid: false,
      message: "Invalid file type: " + extension + " please use allowed file type"
    }
  }
}

const withHttp = url => !/^https?:\/\//i.test(url) ? `http://${url}` : url;

const validateAttachments = async (arrOfAttachments) => {
  const isOutlookAtt = checkAtt(arrOfAttachments)
  if (arrOfAttachments && arrOfAttachments.length && !isOutlookAtt) {
      return new Promise( async function (res, rej) {
        for (const att of arrOfAttachments) {
          if(!att.alternative) {

            const file = att.filename || att.name
            const path = withHttp(att.path || att.href || att.location)

            const validationRes = checkFileExtension(file)

            if (validationRes.isValid) {
              linkCheck(path, function (err, result) {
                if (err) {
                    rej({
                        message: 'cant check attachment link health',
                    })
                } else {
                  if (result.status === 'dead') {
                    rej({
                      message: 'invalid attachment link',
                      code: '404',
                      url: path
                    })
                  }
                  if(result.err){
                    rej({
                      message: 'invalid attachment link',
                      code: 'ENOTFOUND',
                      url: path
                    })
                  }
                  res(result)
                }
            });
            } else {
              rej({
                message: validationRes.message,
              })
            }
          }
        }
    })
  }
}

const pipeLine = pipes => {
  const _pipes = pipes || [];
  const execute = async (data) => {
    let result = data;
    for (let p of _pipes) {
      result = await p(result)
    }
    return result;
  };
  return { execute };
};

const emailServer = async () => {
  if (emailRecord.type !== 'mail-server') throw new Error('Can not create email server');

  const config = { user: emailRecord.address };

  const gmailConfig = async () => {
    const url = this.helpers.gatewayUrl('email/mail-server/gmail/auth/xoauth2/v2', this.helpers.providersAccountId);
    const options = { headers: { authorization: emailRecord.userToken }, params: { id: key, emailAddress: config.user } };

    let token;
    try {
      token = _.get(
        await axios.get(url, options),
        'data.accessToken.access_token'
      );
    } catch(error) {
      this.log.error('error gmailConfig', error )
    }

    return {
      ...config,
      host: 'smtp.gmail.com',
      ssl: true,
      port: 465,
      password: token,
      authentication: ['XOAUTH2']
    }
  };

  const outlookConfig = () => {
    return {
      ...config,
      password: emailRecord.password,
      host: 'smtp-mail.outlook.com',
      port: 587,
      tls: { ciphers: 'SSLv3' }
    }
  };

  const customServiceConfig = () => {
    const { address: user, password, smtp: { host, port, tls, cipher } } = emailRecord;
    const config = { user, password, host, port: parseInt(port), ssl: parseInt(port) === 465 ? true : false };
    const tlsConfig = cipher ? { tls: { ciphers: cipher } } : tls ? { tls: true } : {};

    return _.assign(config, tlsConfig);
  };

  const server = config => {
    const { SMTPClient } = emailjs;
    const client = new SMTPClient(config);

    return client;
  }

  switch (emailRecord.service) {
    case 'gmail-xoauth': return server(await gmailConfig());
    //case 'outlook': return server(outlookConfig());
    default: return server(customServiceConfig());
  }
};

const attachDynamicAttachments = () => {
  if (!Array.isArray(dynamicAttachments)) { 
    return;
  }
  attachments.push(...dynamicAttachments
    .map(att => {
      if (typeof att === 'string') {
        return { location: att };
      } else {
        if (!att.location) return null;
        return { location: att.location, name: att.name }
      }
    })
    .filter(item => item)
  );
};

const prepareAttachments = async (cb) => {


  if (hasDynamicAttachments) {
    attachDynamicAttachments();
  }

  const ext = str => _.get(/\.([a-z]+)$/.exec(str.trim()), '[1]', '');
  const nameFromUrl = str => _.get(/\/([^\/]+?\.[a-z]+)$/.exec(str.trim()), '[1]', '');

  return cb(mimeTypes, fetch, ext, nameFromUrl);
};

const emailServerMessage = async () => {
  const { decode } = htmlEntities();
  const dec = str => execWithTextMask(str, /(?:&lt;|&gt;)/g, str => decode(str));

  try {
    await validateAttachments(attachments)
  } catch(error) {
      if (processError) {
        this.exitStep('error', error)
      }
      throw error
    }

  const prepareAttachmentsMailServer = async () => {
    return await prepareAttachments(async (mimeTypes, fetch, ext, nameFromUrl) => {
      const promisedAttachments = attachments.map(att => new Promise(async (res, rej) => {
        const url = att.location.trim();
        const buffer = await fetch(url).then(resp => resp.buffer());
        const name = (att.name || nameFromUrl(url)).trim();
        const type = mimeTypes.lookup(ext(name) || ext(url) || 'application/octet-stream');

        return res({ name, type, data: buffer });
      }));

      return await Promise.all(promisedAttachments);
    });
  };

  const arrayToAddressString = arr => arr
    .filter(item => item && (item !== 'undefined'))
    .join(',');

  const message = {
    subject: subject,
    attachment: await prepareAttachmentsMailServer(),
    'Message-ID': `<${uuid(51)}@onereach.ai>`, // XXX Why do we have '@onereach.ai' ? 
  };

  if (isReply) {
    if (replyTo) message['in-reply-to'] = replyTo;
    if (references.length) {
      const strToReferencesHeader = rs => {
        return rs.split(',').join(' ').split(' ').map(r => r.trim()).join(' ');
      };
      message.references = Array.isArray(references) ? strToReferencesHeader(references.join()) : strToReferencesHeader(references);
    }
  }

  message.from = `${nameFrom} <${emailRecord.address}>`;

  switch(getCurrentMode()) {
    case 'visual':
      message.text = dec(wysiwygText);
      message.attachment.push({ data: dec(wysiwygText), type: 'text/html', alternative: true });
      break;
    case 'plain':
      message.text = yourMessage;
      break;
    case 'html':
      message.text = striptags(yourMessageHtml);
      message.attachment.push({ data: yourMessageHtml, type: 'text/html', alternative: true });
      break;
    case 'template':
      message.text = await htmlMinifierTerser.minify(emailTemplateMergeFieldsStr, { maxLineLength: 20 })
      message.attachment.push({ data: await htmlMinifierTerser.minify(emailTemplateMergeFieldsStr, { maxLineLength: 20 }), type: 'text/html', alternative: true });
      break;
    default:
      break;
  }

  message.to = arrayToAddressString(toList);

  const cc = arrayToAddressString(ccList);
  const bcc = arrayToAddressString(bccList);

  if (ccList.length > 0) message.cc = cc;
  if (bccList.length > 0) message.bcc = bcc;

  try {
    validateMailServerRecipients({ to: message.to,  cc: message.cc, bcc: message.bcc})
  } catch(error) {
    if (processError) {
      this.exitStep('error', error)
    }
    throw error
  }

  const { execute } = pipeLine([
    addTrackableLinksSmtp,
    addEmailOpenBeaconSmtp,
  ]);

  return execute(message);
};

