const botmailMessage = async () => {
  const { decode } = htmlEntities();
  const dec = str => execWithTextMask(str, /(?:&lt;|&gt;)/g, str => decode(str));

  const prepareAttachmentsBotmail = async () => {
    let mailAttachments = [...attachments , ...dynamicAttachments]
    return prepareAttachments((mimeTypes, fetch, ext, nameFromUrl) => {
      return mailAttachments.map(att => {
        const path = att.location.trim();
        const filename = att.name && att.name !== 'undefined'? att.name : nameFromUrl(path).trim() || '';
        return { path, filename };
      });
    });
  };

  const mailOptions = {
    from: dec(nameFrom).length && nameFrom !== 'undefined' ? `${dec(nameFrom)} <${getSenderAddress()}>` : getSenderAddress(),
    to: toList,
    subject: subject !== 'undefined' ?  dec(subject) : '',
  };

  if (differentReplyTo) mailOptions.replyTo = getReplyAddress();

  if (isHtmlContent() && !isTemplateString() &&!isHtmlMarkdown()) {

    _.assign(mailOptions, {
      text: striptags(dec(yourMessageHtml)),
      html: dec(yourMessageHtml)
    });

  } else if (isTemplateString()) {

    _.assign(mailOptions, {
      text: emailTemplateMergeFieldsStr,
      html: dec(emailTemplateMergeFieldsStr)
    });

  } else if (isHtmlMarkdown()) {

    _.assign(mailOptions, {
      text: wysiwygText, 
      html: dec(wysiwygText)
    });

  } else {
    mailOptions.text = dec(yourMessage);
    mailOptions.html = dec(yourMessage).replace(/\n/gmu, "<br>");
  }

  if (ccList.length) mailOptions.cc = ccList;
  if (bccList.length) mailOptions.bcc = bccList;

  if (attachments.length || dynamicAttachments) mailOptions.attachments = await prepareAttachmentsBotmail();

  if (isReply) {
    if (replyTo) mailOptions.inReplyTo = replyTo;
    if (references.length) {
      const strToReferencesArray = str => {
        return str.split(' ').join().split().map(r => r.trim());
      };
      mailOptions.references = Array.isArray(references) ? strToReferencesArray(references.join()) : strToReferencesArray(references);
    }
  }

  const { execute } = pipeLine([
    addTrackableLinksBotmail,
    addEmailOpenBeaconBotmail,
  ]);

  return execute(mailOptions);
};

const validateMailServerRecipients = (recipients) => {
  if (recipients) {
    if ('to' in recipients && !_.isEmpty(toList)) {
      if(!emailRegex.test(recipients.to)){
        throw { type: 'RecipientsError', message: `recipient in 'to' expected to be a valid address but got ${recipients.to}` }
      }
    }
    if ('cc' in recipients && !_.isEmpty(ccList)) {
      if(!emailRegex.test(recipients.cc)){
        throw { type: 'RecipientsError',  message: `recipient in 'cc' expected to be a valid address but got ${recipients.cc}` }
      }
    }
    if ('bcc' in recipients && !_.isEmpty(bccList)) {
      if(!emailRegex.test(recipients.bcc)){
         throw { type: 'RecipientsError', message: `recipient in 'bcc' expected to be a valid address but got ${recipients.bcc}` }
      }
    }
  } else {
    throw { type: 'RecipientsError', message: `empty recipient, at least one 'to' recipient is required` }
  }
}

const validateRecipients = (recipients) => {
    _.forIn(recipients, (list, key) => {
      _.map(list, (val) => {
        if (!emailRegex.test(val)) {
          switch(key) {
            case 'to':  throw { type: 'RecipientsError', message: `recipient in 'to' expected to be a valid address but got ${val}` }
            case 'cc':  throw { type: 'RecipientsError',  message: `recipient in 'cc' expected to be a valid address but got ${val}` }
            case 'bcc':  throw { type: 'RecipientsError', message: `recipient in 'bcc' expected to be a valid address but got ${val}` }
            default:
              break
          }
        }
      })
    })
};

const outlookServerMessage = async () => {
  const { decode } = htmlEntities();
  const dec = str => execWithTextMask(str, /(?:&lt;|&gt;)/g, str => decode(str));

  const prepareAttachmentsMailServer = async () => {
    return await prepareAttachments((mimeTypes, fetch, ext, nameFromUrl) => {
      return attachments.map(att => {
        const path = att.location.trim();
        const filename = att.name && att.name !== 'undefined'? att.name : nameFromUrl(path).trim() || '';
        return { href: path, filename };
      });
    });
  };

  const arrayToAddressString = arr => arr
    .filter(item => item && (item !== 'undefined'))
    .join(',');

  const message = {
    subject: subject,
    attachment: await prepareAttachmentsMailServer(),
    'Message-ID': `<${uuid(20)}@onereach.ai>`,
  };

  if (isReply) {
    if (replyTo) message['in-reply-to'] = replyTo;
    if (references.length) {
      const strToReferencesHeader = rs => {
        return rs.split(',').join(' ').split(' ').map(r => r.trim()).join(' ');
      };
      message.references = Array.isArray(references) ? strToReferencesHeader(references.join()) : strToReferencesHeader(references);
    }
  }

  message.from = `${nameFrom} <${emailRecord.address}>`;

  switch(getCurrentMode()) {
    case 'visual':
      message.text = dec(wysiwygText);
      message.attachment.push({ data: dec(wysiwygText), type: 'text/html', alternative: true });
      break;
    case 'plain':
      message.text = yourMessage;
      break;
    case 'html':
      message.text = striptags(yourMessageHtml);
      message.attachment.push({ data: yourMessageHtml, type: 'text/html', alternative: true });
      break;
    case 'template':
      message.text = emailTemplateMergeFieldsStr;
      message.attachment.push({ data: emailTemplateMergeFieldsStr, type: 'text/html', alternative: true });
      break;
    default:
      break;
  }

  message.to = arrayToAddressString(toList);

  const cc = arrayToAddressString(ccList);
  const bcc = arrayToAddressString(bccList);

  if (cc) message.cc = cc;
  if (bcc) message.bcc = bcc;

  const { execute } = pipeLine([
    addTrackableLinksSmtp,
    addEmailOpenBeaconSmtp,
  ]);
  return execute(message);
};

const sendByOutlook = async () => {
  try {
    const message = await outlookServerMessage();
    validateMailServerRecipients({ to: message.to,  cc: message.cc, bcc: message.bcc})
    await validateAttachments(message.attachment)
    const { data } = getHtmlAttachmentSmtp(message);

    message.html = data

    if (message.attachment[0].alternative) {
      message.attachment.shift()
    }

    const resp = await this.eventManager.emit({
      target: this.config.accountId,
      name: 'email/events/smtp/send-message-smtp',
      params: {
        auth: {
          user: _.get(emailRecord, 'address'),
          pass: _.get(emailRecord, 'password'),
        },
        address: getSenderAddress(),
        token: _.get(emailRecord, 'access.token'),
        email: message,
      },
      reporting: {
          sessionId: this.session.get('reporting.sessionId')
      }
      }, {
          invocationType: 'sync',
          timeout: 30000
      }
    );


    if (resp.error) {
      throw resp.error
    }
    const messageId = _.get(resp, 'data.MessageId');

    return messageId;
  } catch (error) {
    if (processError) {
      this.exitStep('error', error)
    }

    throw error;
  }
}

const sendByBotmail = async () => {
  try {
    const message = await botmailMessage();

    await validateAttachments(message.attachments)
    validateRecipients({ to: message.to,  cc: message.cc, bcc: message.bcc})

    const resp = await this.eventManager.emit({
      target: this.helpers.providersAccountId,
      name: 'email/events/botmail/send',
      params: {
        address: getSenderAddress(),
        token: _.get(emailRecord, 'access.token'),
        email: message,
        ignoreOtherBounces: ignoreOtherBounces,
        customMessageId: customMessageId,
        iterationKey: this.currentStepId,
        iterationCount: iterationCount
      },
      reporting: {
          sessionId: this.session.get('reporting.sessionId')
      }
      }, {
          invocationType: 'sync',
          timeout: 30000
      }
    );

    if (resp.error) {
      throw resp.error
    }

    if ( !resp.data || !resp.data.MessageId) {
      this.log.warn("can't send message: can't genereate messageId or there is unhadled bounce")
    }

    return resp.data.MessageId

  } catch (error) {

    if (error.details && _.isArray(error.details) && error.details[0]) {
      throw {
        type: "invalid address",
        message: error.details[0].message
      }
    }

    if (processError) {
      if (error.syscall) {
        const message = {
          message: `${error.syscall} ${error.hostname}`,
          code: error.code,
          stack: error.syscall
        }

        throw message

      }

      throw error;
    }

    throw error
  }
};

