const createEmailBeacon = async (id, payload) => {
  try {
    const url = this.helpers.gatewayUrl('email/beacon', this.helpers.providersAccountId);
    const key = 'beacons';
    let beaconLink;

    if (!url) {
      this.log.error('cant get url for open beacon generation');
      throw {
        name: "open beacon link generation failed",
        message: "can't get gateway url"
      }
    }

    if (hasShortUrl) {
      beaconLink = await generateShortUrl(`${url}?id=${id}`)
    } else {
      beaconLink = `${url}?id=${id}`
    }

    const beacon = {
      id,
      beaconLink,
      ...payload
    };

    const beacons = thisStep.get(this.process.main, key)

    if (beacons) {
       thisStep.set(this.process.main, key, [...beacons, beacon])
    } else {
       thisStep.set(this.process.main, 'beacons', [ beacon ])
    }

    return beacon;
  } catch (error) {
    this.log.error('createEmailBeacon error', error)
  }
};

const beaconImage = (link) => {
