const getHtmlAttachmentSmtp = (message) => {
  const htmlAttQuery = { type: 'text/html', alternative: true };
  const att = _.chain(message)
    .get('attachment')
    .find(htmlAttQuery)
    .value();
  return _.cloneDeep(att) || _.assign({}, htmlAttQuery, { data: _.get(message, 'text') });
};

const addHtmlAttachmentSmtp = (message, htmlAttachment) => {
  const m = _.cloneDeep(message);
  const attachments = _.filter((m.attachment || []), item => !_.isMatch(item, { type: 'text/html', alternative: true }));
  m.attachment = [htmlAttachment, ...attachments];
  return m;
};

