const addTrackableLinks = async mailBody => {
  let beaconsAnchors = []
  let bodyWithBeaconsAnchors = ''

  const validateHtml = async (options) => {
    try {
      const result = await validator(options)
      return result
    } catch (error) {
      this.log.error('addTrackableLinks html validation error', error)
    }
  }

  const validateUrl = (url) => {
    return linkRegex.test(url)
  }

  const validateEmailAddress = (address) => {
    return emailRegex.test(address)
  }

  const parseHtml = (html) => {
    try {
      const result = cheerio.load(html, { xmlMode: true })
      return result
    } catch (error) {
      this.log.error('addTrackableLinks html parse error', error)
    }
  }

  const createUrl = (url) => {
    try {
     const link = new URL(url)
     return link
    } catch(error) {
      try {
        const link = new URL(`http://${url}`)
        return link
      } catch(error) {
        throw error
      }
    }
  }

  const getDomainNameFromURL = (url)=> {
    const hostNameArr = url.host.split('.')
    return _.last(hostNameArr)
  }

  const validateDomainName = (name) => {

    const schema = joi.string().domain({ minDomainSegments: 1 })
    const res = schema.validate(name)

    if (res.error) {
      const { details } = res.error
      throw {
        name: details.type,
        code: null,
        message: details.message
      }
    }
  }

  const processLinks = async (parsed) => {
    try{
    const self = this;
    parsed('a').each(function() {
      const hrefVal = parsed(this).attr('href') || '#'
      const isEmailAddr = validateEmailAddress(hrefVal)
      const isTrackable = validateUrl(hrefVal)

      if (!isEmailAddr && isTrackable) {
        const url = createUrl(hrefVal)
        parsed(this).attr('href', url.href)

        const domainName = getDomainNameFromURL(url)
        validateDomainName(domainName)
        const anchor = uuid();

        const tagData = {
          attributes: _.omit(parsed(this)[0].attribs, ['style']),
          children: _.pick(parsed(this)[0].children[0], ['type', 'data', 'name'])
        }

        beaconsAnchors = [...beaconsAnchors, { link: parsed(this).attr('href'), value: anchor, tagData }]
        parsed(this).attr('href', anchor)
      }
    })
    return parsed
    } catch (err) {
        err = {
          name: "Link processing error",
          code: null,
          message: "Invalid href value in html template"
        }
      throw err
    }
  }

  const processWysiwygLinks = (html) => {
    const res = html.replace( wysiwygRegex, (link) => {
      const plainLink = link.match(plainLinkRegex)
      const processedLink = processPlain(plainLink[0])
      return processedLink
    })
    return res
  }

  const processHtml = async (html) => {
   // empty messsage
   if (!html) return ''

    const processedHtml = await validateHtml({
      data: html,
      validator: 'WHATWG',
      isFragment: true
    })

    if (!processedHtml.isValid) {
      this.log.info('ivalid html template', processedHtml)
    }

    try {
      const parsedHtml = parseHtml(html)
      const processedLinks = await processLinks(parsedHtml)
      return processedLinks.html()
    } catch (error) {
      throw error
    }
  }

  const processWysiwygHtml = async (html) => {
   if (!html) return ''

    const processedHtml = await validateHtml({
      data: html,
      validator: 'WHATWG',
      isFragment: true
    })

    if (!processedHtml.isValid) {
      this.log.info('ivalid html template', processedHtml)
    }

    try {
      const processedWysiwygLinks = processWysiwygLinks(html);
      const parsedHtml = parseHtml(processedWysiwygLinks)
      const processedLinks = await processLinks(parsedHtml)
      return processedLinks.html()
    } catch (error) {
      throw error
    }
  }

  const processPlain = (plain) => {
    const processed = plain.replace(plainLinkRegex, (link) => {
      const isEmailAddress = validateEmailAddress(link)
      if (isEmailAddress) {
        return `<a href="mailto:${link}">${link}</a>`
      }
      return `<a href="${link}">${link}</a>`
    })
    return processed
  }

  switch(getCurrentMode()) {
    case 'plain':
      bodyWithBeaconsAnchors = await processHtml(processPlain(mailBody));
      break;
    case 'visual':
      bodyWithBeaconsAnchors = await processWysiwygHtml(mailBody);
      break;
    default:
      bodyWithBeaconsAnchors = await processHtml(mailBody);
      break;
  }

  const beacons = await beaconsAnchors.reduce((acc, anchor) => {
    return acc.then(async beacons => { 
      const id = anchor.value;
      const beacon = await createEmailBeacon(id, { type: 'click', link: anchor.link.trim() });
      return [...beacons, { beacon, anchor: anchor.value }];

    });
  }, Promise.resolve([]));

  thisStep.set(this.process.main, 'beacons_tags_data', beaconsAnchors)
  return beacons.reduce((str, { beacon, anchor }) => str.replace(anchor, beacon.beaconLink), bodyWithBeaconsAnchors);
};

