const execWithTextMask = (str, regex, callback) => {
  const masksMap = new Map();
  const masked = str.replace(regex, match => {
    const mask = `_____MASK____${Math.random()}_${Math.random()}_${Math.random()}____MASK____`;
    masksMap.set(mask, match);
    return mask;
  });
  const cbResult = callback(masked);
  return Array.from(masksMap.keys()).reduce((result, mask)=> {
    return result.replace(mask, masksMap.get(mask));
  }, cbResult);
};

