// Get outgoing message fields content helpers

const replaceBreakLinesHtml = (html) => {
  return html.replace(/\n/gmu, "<br>");
}

const processHtmlToPlain = (html) => {
  const params = {
    wordwrap: 130
  }
  return htmlToText.fromString(html, params)
}

const convert = {
  html: (targetType) => {
    switch(targetType) {
      case 'html':
        return replaceBreakLinesHtml(yourMessageHtml)
      case 'plain':
        return processHtmlToPlain(yourMessageHtml)
      case 'trimmedHtml':
        return planer.extractFrom(yourMessageHtml, 'text/html', dom.window.document)
      case 'trimmedText':
        return planer.extractFrom(processHtmlToPlain(yourMessageHtml), 'text/plain')
    }
  },
  plain: (targetType) => {
    switch(targetType) {
      case 'html':
        return replaceBreakLinesHtml(yourMessage)
      case 'plain':
        return yourMessage
      case 'trimmedHtml':
        return replaceBreakLinesHtml(planer.extractFrom(yourMessage, 'text/plain'))
      case 'trimmedText':
        return planer.extractFrom(yourMessage, 'text/plain')
    }
  },
  visual: (targetType) => {
    switch(targetType) {
      case 'html':
        return wysiwygText
      case 'plain':
        return processHtmlToPlain(wysiwygText)
      case 'trimmedHtml':
        return planer.extractFrom(wysiwygText, 'text/html', dom.window.document)
      case 'trimmedText':
        return planer.extractFrom(processHtmlToPlain(wysiwygText), 'text/plain')
    }
  },
  template: (targetType) => {
    switch(targetType) {
      case 'html':
        return emailTemplateMergeFieldsStr
      case 'plain':
        return  processHtmlToPlain(emailTemplateMergeFieldsStr)
      case 'trimmedHtml':
        return planer.extractFrom(emailTemplateMergeFieldsStr, 'text/html', dom.window.document)
      case 'trimmedText':
        return planer.extractFrom(processHtmlToPlain(emailTemplateMergeFieldsStr), 'text/plain')
    }
  }
}

const getMessageContent = (mode, type) => {
  const _mode = mode.value || mode
  return convert[_mode](type)
}

const messageToOutput = async () => {
  try {
    const now = moment();
    const subjectRegex = /^([rR][eE]|[fF][wW][dD]|[fF][wW]):\s/;
    const currentMessageMode = mode

    const messageId = thisStep.get(this.process.main, 'messageId')
    const latestResponse = thisStep.get(this.process.main, 'latest_response') || {}
    const allResponses = thisStep.get(this.process.main, 'response_list') || []
    const beacons = thisStep.get(this.process.main, 'beacons')


    const message = {
      latestResponse: latestResponse,
      allResponses: allResponses,
      outgoingMessage: {
        messageId,
        timestamp: Date.now(),
        datetime: now.format(),
        sender: {
          name: nameFrom,
          address: getSenderAddress()
        },
      	subject: {
    			text: subject,
    			trimmed: subject.replace(subjectRegex,'')
    		},
    		recipients: {
    			to: toList,
    			cc: ccList,
    			bcc: bccList
    		},
    		content: {
    			plain: getMessageContent(currentMessageMode, 'plain'),
    			html: getMessageContent(currentMessageMode, 'html'),
	   			trimmedText: getMessageContent(currentMessageMode, 'trimmedText'),
    			trimmedHtml: getMessageContent(currentMessageMode, 'trimmedHtml')
    		},
    		inReplyTo: replyTo,
	      references: Array.isArray(references) ? references : [],
        attachments,
        beacons
      }
    }

    return message
  } catch (error) {
    this.log.error('messageToOutput error', error)
  }
};

const attachDynamicRecipients = () => {
  if (!Array.isArray(dynamicRecipients)) return;

  dynamicRecipients.forEach(r => {
    if (typeof r === 'string') {
      toList.push(r);
      return;
    }

    if (typeof r === 'object') {
      switch (_.get(r, 'type', '').toLowerCase()) {
        case 'to': toList.push(r.recipient); break;
        case 'cc': ccList.push(r.recipient); break;
        case 'bcc': bccList.push(r.recipient); break;
      }
    }
  });
};

// EVENTS HANDLERS

const updateSessionTimeout = () => {
  const remainingTime = this.session.timeout - (new Date().getTime() - new Date(this.session.startDate).getTime());
  const neededTime = getStepTimeout();
  if (neededTime > remainingTime) this.session.set('timeout', neededTime);
};

const eventsTriggersNames = (email, inReplyTo, firstRef, sender) => {
  return [
    `in/email/reaction/${inReplyTo}`,
  ].filter(e => e);
};

const exit = (data = null) => { 
  this.exitFlow(data);
}

const exitStep = async (leg, data) => {
  const callback = this.takeCallback();
  const eventManagerResult = await this.eventManager.callbackResolve(callback, { caught: true });
  // await removeStoredMessageData();
  return this.exitStep(leg, data);
};

const exitThread = async (type, data) => {

  const callback = this.takeCallback();
  const eventManagerResult = await this.eventManager.callbackResolve(callback, { caught: true });

  const id = `${type}_${uuid()}`

  this.process.newThread(id, thread => {
      thread.currentStep = thisStep.getExitStepId(type);
      thread.activate({ type: data });
  });

  await this.mergeFields[this.currentStep.dataOut.name].set(data);
  return exit(data);
}

const resolveEventHandler = ({
  email, inReplyTo, firstRef,
  senders, bounceHandleType,
  ignoreOtherBounces, beacons, hasOpenEvent,
  hasUrlClickTracking
}) => async event => {
  const sender = _.get(event,'params.sender');
  const references = _.get(event, 'params.message.references', '');
  const threadRegex = new RegExp(`${inReplyTo}|${firstRef}`, 'g');
  const type = _.get(event, 'params.type');

  const getStringEmailFromArray = (arr) => {
    const result = _.reduce(arr, (stringData, item) => {
      return `${stringData ? stringData + ' | ' : ''}${item.name ? '<' + item.name + '>' : ''} ${item.address}`;
    }, '');

    return result;
  }

  // if (type !== 'delivery') {
  //   let toIdentifier, fromIdentifier;	

  //   if (type === 'bounce' || type === 'complaint') {	
  //     toIdentifier = emailConfig.label;	
  //     fromIdentifier = _.get(event, 'sender');	
  //   } else if (type === 'beacon') {	
  //     toIdentifier = emailConfig.label;	
  //     fromIdentifier = _.get(event, 'params.beacon.sender');	
  //   } else {	
  //     const toData = _.get(event, 'params.message.to.value');	
  //     const fromData = _.get(event, 'params.message.from.value');	

  //     toIdentifier = getStringEmailFromArray(toData);	
  //     fromIdentifier = getStringEmailFromArray(fromData);	
  //   }

  //   report('in', _.assign({}, event.params, {
  //     transcriptId         : uuid(),
  //     previousTranscriptId : this.get(`${this.currentStepId}.transcriptId`), 
  //     botIdentifier        : toIdentifier, 
  //     endUserIdentifier    : fromIdentifier
  //   })); 
  // }

  const messageEventHandler = async event => {
    const handleOmitCondition = async condition => {
      if (condition) return exit();
      return exitStep('next', await messageToOutput());
    };

    switch (event) {
      case 'thread': return handleOmitCondition(!threadRegex.test(references));
      case 'to': return handleOmitCondition(false);
      case 'recipient': return handleOmitCondition(!_.includes(senders, sender));
      default: throw new Error('Wrong handling case in message handler')
    }
  };

  const bounceEventHandler = async event => {
    try {
      const messageId = thisStep.get(this.process.main, 'messageId')
      const { sender } = event.params

      const now = moment()
      const message = await messageToOutput();
      const { type } = event.params.bounce
      const isOptOut = event.params.isOptOut || false

      message.latestResponse = { 
        messageId,
        timestamp: Date.now(),
        datetime: now.format(),
      	eventSender: {
    		  email: sender,
    	  },
      	type: type,
      	isOptOut
      }

      if (bounceHandleType === 'ignore') return exit(event);

      if (ignoreOtherBounces) {
        if (sender !== _.get(senders, '[0]')) return exit(event);
      }

      if (bounceHandleType === 'error') {
        const bounceResp = messageToOutput(event)

        const message = {
          message: bounceResp.latestResponse,
          code: "bounce",
          stack: "sendByBotmail -> bounce"
        }

        throw message
      };

      if (bounceHandleType !== 'separately') return exitStep('bounce', message);

      switch (type) {
        case 'hard-bounce': return exitStep('hard-bounce', message);
        case 'soft-bounce': return exitStep('soft-bounce', message);
      }
    } catch (error) {
      throw error
    }
  };

  const beaconEventHandler = async event => {
    const beaconData = _.get(event, 'params.beacon');
    const type = _.get(event, 'params.beacon.type');
    const key = 'beacons'

    const handleClick = async () => {

      if (!hasUrlClickTracking) {
        return exit(event);
      }

      const beacons = thisStep.get(this.process.main, key)

      const responseData = {
        event,
        beacons
      }

      await resolveReponseData(responseData)
      let message = await messageToOutput()

      return exitThread('click', message)
    }

    const handleOpen = async  () => {
      if (!hasOpenEvent) {
        return exit(event);
      }

      const responseData = {
        event,
        beacons: null
      }

      await resolveReponseData(responseData)
      let message = await messageToOutput()

      return exitThread('open', message);
    };

    switch (type) {
      case 'click': return handleClick();
      case 'open': return handleOpen();
    }
  };

  const complaintEventHandler = async event => {
    const messageId = thisStep.get(this.process.main, 'messageId')
    const { type } = event.params
    const { complainedRecipient } = event.params.complaint
    const now = moment()
    // const isOptOut = event.params.isOptOut || false
    const isOptOut = _.get(event, 'params.isOptOut') || false
    const message = await messageToOutput();

    message.latestResponse = { 
      messageId,
      timestamp: Date.now(),
      datetime: now.format(),
    	eventSender: {
  		  email: complainedRecipient,
  	  },
    	type: type,
    	isOptOut
    }

    if (!handleComplaints) return exit(event);
    if (bounceHandleType === 'ignore') return exit(event);
    if (bounceHandleType === 'error') return exitStep('error', message);
    if (bounceHandleType !== 'separately') return exitStep('bounce', message);

    return exitStep('hard-bounce', message)
  };

  const eventType = _.get(event, 'params.type');

  switch (eventType) {
    case 'bounce': return bounceEventHandler(event);
    case 'beacon': return beaconEventHandler(event);
    case 'complaint': return complaintEventHandler(event);
    // case 'delivery': return deliveryNotificationhandler(event);
    default: return exit();
  }
};

const triggers = async (thread, messageId, timeout) => {
  const email = differentReplyTo ? getReplyAddress() : getSenderAddress();
  const inReplyTo = messageId;
  const firstRef = getFirstRef();
  const senders = getRecipientsArray();
  const beacons = thisStep.get(this.process.main, 'beacons') || [];

  const resolverOptions = {
    email,
    inReplyTo,
    firstRef,
    senders,
    bounceHandleType,
    ignoreOtherBounces,
    beacons,
    hasOpenEvent,
    hasUrlClickTracking
  };

  if (useThreads) {
    thread.triggers.once(`in/email/reaction/${messageId}`, event => {
      return resolveEventHandler(resolverOptions)(event);
    });

    thread.triggers.timeout(timeout, async () => exitStep('timeout', await messageToOutput()))
  }

  return thread.triggers
    .otherwise(async () => {
      return useThreads ?
        exitThread('next', await messageToOutput()) :
        exitStep('next', await messageToOutput());
    });
};

const validateEmailConfig = async (selectedAddress, mailOptions) => {

  let opts = _.isArray(mailOptions) ? mailOptions : mailOptions.mailOptions

  const res = opts.filter(account => {
    if (account.type === 'mail-server' || account.type === 'botmail'){
      return account.address === selectedAddress
    } else {
      return account.domain === selectedAddress
    }
  })

  if (!res.length) {
    const error = {
      name: "invalid sender address",
      message: "Unknown sender address",
      stack: new Error().stack
    }
    throw error
  } else {
    emailConfig = res[0]
    key = emailConfig.id;
    emailRecord = emailConfig;
  }
}

const main = async () => {
  let messageId = thisStep.get(this.process.main, 'messageId')

  if (hasDynamicRecipients) {
    attachDynamicRecipients();
  }

  if (iterationCount && iterationCount >= iterationDepth) {
    return exitStep('next')
  }

  if (useManualMode) {
    await validateEmailConfig(manualEmailConfig, emailConfig)
  }

  if (messageId) {
    const timeout = getRemainingTimeout()
    return triggers(this, messageId, timeout);
  } else {
    const timeout = getStepTimeout()
    const params = {
      message: {
        text: yourMessage || yourMessageHtml,
      },
    };
    const endUserIdentifier = _.concat(toList, ccList, bccList).join(' ');
    report('out', _.assign({}, params, {
      transcriptId         : uuid(),
      previousTranscriptId : this.get(`${this.currentStepId}.transcriptId`), 
      botIdentifier        : emailConfig.label, 
      endUserIdentifier,
    }));

    messageId = await sendMessage();
    thisStep.set(this.process.main, 'messageId', messageId);

    if (useThreads) {
      const beacons = await registerBeacons(messageId);
      thisStep.set(this.process.main, 'registered_beacons', beacons);
    }

    updateSessionTimeout()

    return triggers(this, messageId, timeout);
  }
};

return main();

