function toJson(data) {

  if (typeof data === 'string') {
    return data;
  }
  if (_.isArray(data)) { 
    return `[${_.map(data, toJson).join(',')}]`;
  }
  if (_.isObject(data)) {
    return `{${_.map(data, (value, key) => `${key}: ${toJson(value)}`).join(',')}}`;
  }
  return JSON.stringify(data);
}

return _.mapValues(data, toJson);

data.validators:
{
  replyToAddress: {
    required(value) {
      if (!this.schema.differentReplyTo) return true;
      const validator = createValidatorRequired('Reply to address is required');
      return validator.call(this, value);
    }
  },
  senderAddress: {
    required(value) {
      if (this.schema.emailConfig.type !== 'advanced') return true;

      if (!this.schema.senderAddress) return true;
      const validator = createValidatorRequired('The Sender email address is required.');
      return validator.call(this, value);
    }
  }
}

function createValidatorRequired(errorMessage) {
  return function (value = '``') {
    if (this.isNew) return true;
    const val = value[0] === '`' && value[value.length - 1] === '`' ? value.slice(1, -1) : value;
    return validators.helpers.withParams(
      { type: 'required', error: errorMessage },
      validators.required
    )(val);
  }
}

