data.componentLogic:
{
  props : {
    steps: {
      type: Array,
      default() {
        return [];
      },
    },
    readonly: {
      type: Boolean
    },
    nameFrom: {
      type: String,
      default: ``,
    },
    isSelectedStep:{
      type:Boolean,
      default: false
    },
    subject: {
      type: String,
    },
    yourMessageHtml: {
      type: String
    },
    selectedStepId: {
      type: String
    },
    isReply: {
      type: Boolean
    },
    toList: {
      type: String
    },
    stepId: {
      type: String
    },
    references: {
      type: Array
    },
    replyTo: {
      type: String
    },
    mode: {
      type: Object
    },
    isNew: {
      type: Boolean
    }
  },
  computed: {
    _isReply: {
      get(){
        return this.isReply;
      },
      set(value){
        if (value && !this.selectedStepId) {
          this.$emit('update:selectedStepId', _.get(this.parents, '[0].id'));
        }

        if (!value) {
          this._selectedStepId = null
          this.isSelectedStepExists = true
        }

        this.$emit('update:isReply', value);
      }
    },
    _selectedStepId: {
      get(){ return this._isReply ? this.selectedStepId : null; },
      set(value){ this.$emit('update:selectedStepId', value); }
    },
    parents() {
      const index = _.findIndex(this.steps, { id: this.stepId });
      if (index === -1) return [];
      const parents = id => {
        const parent = _.find(
          this.steps, 
          { data: { exits: [{stepId: id}] } }
        );
        return parent ? [parent, ...parents(parent.id)] : [];
      };

      return parents(this.steps[index].id)
        .filter(step => _.get(step, 'data.isEmailInheritable', false));
    },
    options() {
      return _.chain(this.parents)
        .map(step => ({ label: step.label, value: step.id }))
        .filter(step => step.label)
        .sort()
        .value();
    },
    selectedStepMergeField() {
      let mergeData = _.chain(this.steps)
        .find({ id: this.selectedStepId })
        .get('data')
        .value();

      mergeData = Object.assign({ mfCode: 'await this.mergeFields' }, mergeData);

      return mergeData;
    },
    isSelectedStepExists: {
      set(val) {
        if (val) {
          this.$emit('update:isSelectedStep', true)
          return false
        }
      },
      get() {
       if (this._isReply) {
         if (!this.selectedStepId) {
          // this.$emit('update:errorMessage', 'Destination step is required.')
          this.errorMsg = 'Destination step is required.'
           this.$emit('update:isSelectedStep', false)

           return true
         } else if (_.isEmpty(this.options)) {
          // this.$emit('update:errorMessage', 'Broken reference: the selected step is detached from the main flow tree.')
           this.errorMsg = 'Broken reference: the selected step is detached from the main flow tree.'
           this.$emit('update:isSelectedStep', false)

           return true
         } else if (this.selectedStepId && !_.isEmpty(this.options)) {

           this.$emit('update:isSelectedStep', true)
           return false
         }
       } else {

         this.$emit('update:isSelectedStep', true)
         return false
       }
      }
    }
  },
  watch: {
    _selectedStepId(stepId, oldStepId){
      this.highlightToggle(stepId, oldStepId);
    },
  },
  data(){
    return {
      errorMsg: 'Destination step is required.',
      isSelectedStep: this.isSelectedStep,
      propsToPopulate: {
        nameFrom: mf => mf.waitForEmailStep ? "``" : "`${" + this.createMergeField(mf, 'outgoingMessage.sender.name') + "}`",
        subject: mf => "`${" + this.createMergeField(mf, mf.waitForEmailStep ? 'message.subject.text' : 'outgoingMessage.subject.text') + "}`",
        mode: mf => ({
          "label": "HTML (raw)",
          "value": "html",
        }),
        yourMessageHtml: mf => "${" + this.createMergeField(mf, mf.waitForEmailStep ? 'message.content.html' : 'latestResponse.content.html') + "}",
        toList: mf => [ "`${" + this.createMergeField(mf, mf.waitForEmailStep ? 'message.recipient.address' : 'outgoingMessage.recipients.to') + "}`"],
        replyTo: mf => "`${" + this.createMergeField(mf, mf.waitForEmailStep ? 'message.messageId' : 'outgoingMessage.messageId') + "}`",
        references: mf => "`${" + this.createMergeField(mf, mf.waitForEmailStep ? 'message.references' : 'outgoingMessage.references') + ", " + this.createMergeField(mf, mf.waitForEmailStep ? 'message.messageId' : 'outgoingMessage.messageId') + "}`",
      }
    }
  },
  methods: {
    createMergeField(mfData, field) {
      if (!mfData) return "``";

      return mfData.mfCode + "['" + mfData.dataOut.name + "']" + ".get({path: '" + field + "'})";
    },
    highlight(stepId, state, inNextTick){
      if (!stepId) return;
      const opts = { stepId, highlightClass : 'hover' };
      if (state) {
        this.$flow.highlightStepOn(opts);
      } else {
        this.$flow.highlightStepOff(opts);
      }
    },
    highlightToggle(stepId, oldStepId){
      if (oldStepId) {
        this.highlight(oldStepId, false);
      }
      this.highlight(stepId, true);
    },
    handlePopulateClick(){
      if (this.isPropsEmpty()) {
        return this.populate();
      }
      this.showConfirmation();
    },
    handleSelectMouseOut(stepId){
      if (this.selectedStepId === stepId) return;
      this.highlight(stepId, false);
    },
    showConfirmation(){
      this.$refs.confirmation.open();
    },
    populate(){
      if(!this.selectedStepMergeField) return;

      const keys = _.keys(this.propsToPopulate);
      _.forEach(keys, prop => {
        this.$emit(`update:${prop}`, this.propsToPopulate[prop](this.selectedStepMergeField));
      });
    },
    isPropsEmpty(){
      return _.without(
        _.keys(this.propsToPopulate),
        'htmlFlag'
      ).reduce((acc, key) => {
        if (!acc) return acc;
        const value = this[key];

        const checkString = val => {
          if(!val) return true;
          const str = (val + '').trim();
          if(/^`.*`$/.test(str)) {
            return !str.slice(1, -1);
          }
          return !str;
        };

        if (_.isArray(value)) {
          return checkString(value[0]);
        }

        return checkString(value);
      }, true);
    },
    setIsReply(){
      if (this.isNew && this.parents.length) {
        this._isReply = true;
      }
    },
    validateSelected() {
      if(!this._isReply) {
        return true
      } else {
        return this.isSelectedStepExists
      }
    },
    validateSelectedStep(options, selected) {
      const found = options.filter((option) => {
          return option.value === selected
      })
      return _.isEmpty(found)
    },
  },
  mounted(){
    this.highlight(this._selectedStepId, true);
    this.setIsReply();
  },
  beforeDestroy(){
    this.highlight(this._selectedStepId, false);
  }
}

