//function that describe how to serialize wildcard data 
const replaceComments = str => str
      .replace(/\/\/[\s\S]+?.*(\n || $)/g, '')
      .replace(/\/\*[.\s\S\n]*\*\//g, '')
      .trim();

function toJson(data, key) {

  if (key === 'dynamicRecipients') {
    return replaceComments(data) || '[]';
  }

  if (typeof data === 'string') {
    return data;
  }

  if (_.isArray(data)) {
    return `[${_.map(data, toJson).join(',')}]`;
  }
  if (_.isObject(data)) {
    return `{${_.map(data, (value, key) => `${key}: ${toJson(value)}`).join(',')}}`;
  }

  return JSON.stringify(data);
}
return _.mapValues(data, toJson);

data.validators:
{
  toList: {
    $each: {
      required
    }
  },
  ccList: {
    $each: {
      required
    }
  },
  bccList: {
    $each: {
      required
    }
  }
}

function required(model, field, type){
  if (this.isNew) return true;
  const val = model.replace(/^(`).*(`)$/, (m, f, s) => (f && s) ? m.slice(1, -1) : m );
  return validators.helpers.withParams(
    { type: 'required', error: 'The Recipient address is required.' },
    validators.required
  )(val);
}

function email(model){
  if (this.isNew) return true;
  const emailRegex = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  const mergeFieldRegex = /\$\{[^\}]*\}|this\.get\(['"`].*?['"`]\)/g;
  const val = model
    .replace(/^(`).*(`)$/, (m, f, s) => (f && s) ? m.slice(1, -1) : m )
    .replace(/.*\s*<(.+)>/, (match, email) => email)
    .replace(mergeFieldRegex, ' ')
    .trim();
  return validators.helpers.withParams(
    { type: 'email', error: 'Invalid email' },
    v => v ? emailRegex.test(v) : true
  )(val);
}

function validMergeField (value = '') {
  if (this.isNew) return true;
  const containMergeFields = /\$\{[^\}]*\}|this\.get\(['"`].*?['"`]\)/g.test(value);
  const isMergeFieldsValid = /\$\{this\.get\(['"`][a-zA-Z0-9_.\[\]]+?['"`]\)\}/g.test(value);
  return validators.helpers.withParams(
    {type: 'validMergeField', error: 'Invalid merge field.'},
    (v) => containMergeFields ? isMergeFieldsValid : true
  )(value);
}

