      type: Array,
      default () {
        return [];
      }
    }
  },
  computed : {
    toArr : {
      get () { return this.toList; },
      set (value) { this.$emit('update:toList', value); }
    },
    ccArr : {
      get () { return this.ccList; },
      set (value) { this.$emit('update:ccList', value); }
    },
    bccArr : {
      get () { return this.bccList; },
      set (value) { this.$emit('update:bccList', value); }
    },
    addRecipientOpts () {
      return [
        { label: "To", disabled: false },
        { label: "Cc", disabled: false },
        { label: "Bcc", disabled: false },
        { label: "Dynamic", disabled: this.hasDynamicRecipients }
      ]
    }
  },
  watch: {
    hasDynamicRecipients (newVal) {
      this.$emit('update:hasDynamicRecipients', newVal);
    },
    dynamicRecipients (newVal) {
      this.$emit('update:dynamicRecipients', newVal);
    }
  },
  methods: {
    isInvalid(index, listLabel){
      const list = _.get(this, `v.schema.${listLabel}.$each`, []);
      const current = list[index] || null;
      return _.get(current, '$invalid', true);
    },
    errorMessage(index, listLabel) {
      const list = _.get(this, `v.schema.${listLabel}.$each`, []);
      const current = list[index] || {};
      if(!current.$invalid) return '';
      const errorsKeys = Object.getOwnPropertyNames(current).filter(p => p[0] !== '$');
      const error = _.find(errorsKeys, key => !current[key]);
      return _.get(current, `$params.${error}.error`, 'NO MESSAGE');
    },
    addField (arr) {
      arr.push('``');
    },
    removeField (arr, index) {
      arr.splice(index, 1)
    },
    addRecipientOptSelect(selection) {
      switch (selection.label.toLowerCase()) {
        case 'to': 
          this.addField(this.toArr);
          break;
        case 'cc':
          this.addField(this.ccArr);
          break;
        case 'bcc':
          this.addField(this.bccArr)
          break;
        case 'dynamic':
          this.hasDynamicRecipients = true;
          break;
        default:
          console.error('Impossible recipient menu selection!', selection);
      }
    }
  }
}

