            }
          ]
        }
      ];

      const dataOutMergeTags = [
        {
          title     : 'Flow',
          variables : this.convertStepsToMergeTags(this.steps, this.stepId, 'session')
        }
      ];
      const additionalMergeFields = _.cloneDeep(this.mergeFields) || [];

      _.forEach(additionalMergeFields, titledMergeFields => {
        let flowAdditionalMergeTags = [];
        switch (titledMergeFields.title) {
          case 'Shared':
            flowAdditionalMergeTags = this.convertStepsToMergeTags(this.steps, this.stepId, 'shared', `${SYSTEM_PREFIX}shared.`);
            break;
          case 'Global':
            flowAdditionalMergeTags = this.convertStepsToMergeTags(this.steps, this.stepId, 'global', `${SYSTEM_PREFIX}global.`);
            break;
          default:
            break;
        }
        titledMergeFields.variables = _.concat(titledMergeFields.variables, flowAdditionalMergeTags);
      });

      return _.concat(dataOutMergeTags, additionalMergeFields, systemMergeTags);
    }
  },

  methods: {
    handleIframeEvents(e) {
      const data = _.get(e, 'data') || null;

      if (data === 'get-initial-data') {
        let htmlData = '';
        let cssData = ''

        if (_.has(this.localEmailTemplateData, 'gjs-components')) {
          try {
            htmlData = JSON.parse(_.get(this.localEmailTemplateData, 'gjs-components', ''));
            cssData = JSON.parse(_.get(this.localEmailTemplateData, 'gjs-styles', ''));
          } catch(e) {
            console.error('error parseing localEmailTemplateData', e)
          }
        }

        document.querySelector('#emailBuilderIframe').contentWindow.postMessage({
          eventType: 'set-initial-data',
          html: htmlData, 
          css: cssData,
          authToken: this.authToken,
          dataOuts: this.dataOuts,
          mergeFields: this.mergeFields,
          accountId: this.accountId
        }, "*");
      } else {
        _.forEach(data, item => {
          _.set(this.schema, item.key, item.value);
        });
      }
    },

    closeEmailBuilderHandler() {
      if (this.localEmailTemplateStr) {
        this.createEmailPreview();
      } else {
        this.localEmailPreviewLink = '';
      }
    },

    flattenSubDataOuts(data, mergeTag) {
      const result = {};
      const recurse = (cur, prop, root) => {
        if (_.isArray(cur) && !_.isEmpty(cur)) {
          result[prop] = [];
          _.forEach(cur, (value, key) => {
            recurse(value, prop || _.isNumber(prop) ? `${prop}[${key}]` : `[${key}]`);
          });
        } else if (_.isObject(cur) && !_.isEmpty(cur)) {
          if (!root) {
            result[prop] = cur;
          }
          _.forEach(cur, (value, key) => {
            recurse(value, prop || _.isNumber(prop) ? `${prop}.${key}` : key);
          });
        } else if (_.isArray(cur) && _.isEmpty(cur)) {
          result[prop] = [];
        } else if (_.isObject(cur) && _.isEmpty(cur) && !root) {
          result[prop] = {};
        } else if (!root) {
          result[prop] = cur;
        }
      };
      recurse(data, mergeTag, true);
      return result;
    },

    async createEmailPreview() {
      this.emailPreviewError = false;
      this.emailPreviewLoading = true;
      this.localEmailPreviewLink = '';

      const successCallbackUrl = await this.$http.post(`${this.$flow.toolboxUrl}/convert/html`, {
      // const successCallbackUrl = await this.$http.post(`https://toolbox-staging.onereach.ai/convert/html`, {
        src: this.localEmailTemplateStr,
        filename: `emailPreview${uuid.v4()}.png`,
        path: 'email/previews/',
        expireTime: 2592000,
        browser: {
          cookies: [],
          javaScriptEnabled: true,
          loadWaitingTime: 3000,
          defaultNavigationTimeout: 20000,
          offlineMode: false,
          userAgent: "Mozilla/5.0 (Linux; Android 6.0; Nexus 5 Build/MRA58N) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/70.0.3538.110 Mobile Safari/537.36",
          viewport: {
            width: 1280,
            height: 720,
            deviceScaleFactor: 1,
            mobile: false
          }
        },
        screenshot: {
          clip: {
            x: 0,
            y: 0,
            width: 1280,
            height: 720
          }
        },
        resize: {
          width: 1280,
          height: 720
        },
      },
      {
        headers: {
          authorization: this.authToken
        }
      }).then(response => {
        return response.body.pollUrl;
      }, err => {
        console.error('error', err)
        this.emailPreviewLoading = false;
        this.emailPreviewError = true;
      });

      let startExecution = new Date();
      const pollingResult = (uri) => {
        return this.$http.get(uri, {
            headers: {
              authorization: this.authToken
            },
            json: true
        }).then(response => {
          // this.emailPreviewLoading = false;
          if (response.status === 202) {
            let endExecution = new Date() - startExecution;

            if (endExecution + 3000 > 20000) {
              this.emailPreviewLoading = false;
              this.emailPreviewError = true;
              console.error('Timeout error. Try again later.')
            }

            return Promise.delay(2000)
              .then(() => pollingResult(uri))
          } else {
            this.emailPreviewLoading = false;
            return response
          }
        }, err => {
          console.error('error', err)
          // this.emailPreviewLoading = false;
          this.emailPreviewError = true;
          return false
        });
      }

      const pollResult = await pollingResult(successCallbackUrl);

      if (pollResult) 
        this.localEmailPreviewLink = _.get(pollResult, 'body.url') || '';

      this.emailPreviewLoading = false;
    },

    openModal(ref) {
      this.$refs[ref].open();
    },

    iframeLoadHandler() {
      this.iframeLoading = false;
    },

    convertStepsToMergeTags (steps, stepId, type = 'session', variablePrefix = '') { // eslint-disable-line max-params
      return _.chain(steps)
      .reject({type : 'empty'})
      .reject({id : stepId})
      .map(step => {
        const dataOut = _.isObject(step.data.dataOut) ? step.data.dataOut.name : step.data.dataOut;
        return {
          stepLabel     : step.label,
          variableName  : dataOut ? `${variablePrefix}${dataOut}` : null,
          type          : _.get(step, 'data.dataOut.type', 'session'),
          outputExample : step.outputExample || null
        };
      })
      .reject({variableName : null})
      .filter({type})
      .uniqBy('variableName')
      .sortBy('variableName')
      .flatMap(dataOut => {
        let subData = [];
        if (_.isObject(dataOut.outputExample) || _.isArray(dataOut.outputExample)) {
          const dataOutObj = {};
          dataOutObj[dataOut.variableName] = dataOut.outputExample;
          const flattenedSubOuts = this.flattenSubDataOuts(dataOut.outputExample, dataOut.variableName);
          subData = _.chain(flattenedSubOuts)
            .keys()
            .sortBy()
            .map(subDataOut => ({
              stepLabel: dataOut.stepLabel,
              variableName: subDataOut,
              subObject: true,
              outputExample: flattenedSubOuts[subDataOut]
            }))
            .value();
        }
        return [dataOut].concat(subData);
      })
      .value();
    },
  },

  mounted() {
    console.log(this.$flow);
    if (!this.isStepInTree) {
      this.iframeLoading = false;
    }

    window.addEventListener("message", this.handleIframeEvents, false);

  },

  beforeDestroy() {
    window.removeEventListener("message", this.handleIframeEvents);
  },

  props : {
    schema: {
      type: Object,
      default() {
        return {};
      }
    },
    emailTemplateData: {
