//function that describe how to serialize wildcard data 
const replaceComments = str => str
      .replace(/\/\/[\s\S]+?.*(\n || $)/g, '')
      .replace(/\/\*[.\s\S\n]*\*\//g, '')
      .trim();

function toJson(data, key) {
  if(key === 'dynamicAttachments') {
    return data || '[]'
  }

  if(key === 'attachments') {
    return `[${_.map(data, (val, index) => {
      if (val.attType === '`link`') return toJson(val);
      return JSON.stringify(val);
    }).join(',')}]`;
  }

  if(key === 'attachmentsFD') {
    return `[${_.map(data, (val, index) => {
      if (val.attType === '`link`') return toJson(val);
      return JSON.stringify(val);
    }).join(',')}]`;
  }

  if(key === 'attachmentsF') {
    return `[${_.map(data, (val, index) => {
      if (val.attType === '`link`') return toJson(val);
      return JSON.stringify(val);
    }).join(',')}]`;
  }

  if (key === 'yourMessageHtml') {
    return `\`${data}\``;
  }

  if (key === 'mode') {
    return JSON.stringify(data);
  }

  if (typeof data === 'string') {
    return data;
  }
  if (_.isArray(data)) { 
    return `[${_.map(data, toJson).join(',')}]`;
  }
  if (_.isObject(data)) {
    return `{${_.map(data, (value, key) => `${key}: ${toJson(value)}`).join(',')}}`;
  }
  return JSON.stringify(data);
}

return _.mapValues(data, toJson);

data.validators:
{
  attachments: {
    isJsAttached(model){
      return !_.find(model, { ext: 'js' });
    },
    $each: {
      location: {
        required: createValidatorRequired('file location is required')
      },
      ext: {
        required: (val, model) => {
          if (model.location.indexOf('${this.') !== -1 || !val) return true;
          const validator = createValidatorRequired('file extension is required');
          return validator(val);
        }
      } 
    }
  }
}
function createValidatorRequired(errorMessage) {
  return function (value = '``') {
    if (this.isNew) return true;
    const val = value[0] === '`' && value[value.length - 1] === '`' ? value.slice(1, -1) : value;
    return validators.helpers.withParams(
      { type: 'required', error: errorMessage },
      validators.required
    )(val);
  }
}

