          type: Object,
          default: {}
        },
        index: Number,
        mergeFields: {
          type: Array,
          default () {
            return [];
          }
        }
      },
      computed: {
        item: {
          get() { return this.value; },
          set(value) { this.$emit('input', value) }
        },
        location() {
          return this.item.location.slice(1, -1).trim();
        },
        name() {
          return this.item.name.slice(1, -1).trim();
        }
      },
      watch: {
        'item.name'(){
          this.updateFileExtension();
        },
        'item.location'() {
          this.updateFileExtension();
        },
      },
      methods: {
        updateFileExtension() {
          const ext = str => _.get(/\.([a-z]+)$/.exec(str.trim()), '[1]', '');
          const nameFromUrl = str => _.get(/\/([^\/]+?\.[a-z]+)$/.exec(str.trim()), '[1]', '');

          if (!this.name) {
            this.item.name = `\`${nameFromUrl(this.location)}\``;
          }

          if (this.name.indexOf('${') === -1) {
            this.item.ext = `\`${ext(this.name) || ext(this.location)}\``;
          } else {
            this.item.ext = `\`${this.name}\``;
          }
        },
        isInvalid(field){
          return _.get(this.v, `schema.attachments.$each[${this.index}]['${field}'].$error`);
        },
        errorMessage(field){
          const chain = _.chain(this.v).get(`schema.attachments.$each[${this.index}]['${field}']`, {});
          return chain
            .reduce((acc, item, key) => {
              if(!/^\$/.test(key) && !item) {
                acc += `${chain.get(`$params.${key}.error`).value()}\n`;
              }
              return acc;
            }, '')
            .value();
        }
      }
    }
  },
  props: {
    yourMessage: {
      type: String,
      default: "``"
    },
    yourMessageHtml: {
      type: String,
      default: ""
    },
    mode: {
      type: Object,
      default() {
        return { 
          label: 'Plain text', 
          value: 'plain',
        };
      }
    },
    attachments: {
      type: Array,
      default: []
    },
    attachmentsFD: {
      type: Array,
      default: []
    },
    attachmentsF: {
      type: Array,
      default: []
    },
    dynamicAttachments: {
      type: String,
      default: ""
    },
    hasDynamicAttachments: {
      type: Boolean,
      default: false
    },
    useSource: {
      type: Boolean,
      default: false
    },
    readonly: {
      type: Boolean,
      default: false
    },
    steps: '',
    stepId: '',
    v: {
      type: Object,
      default: {}
    },
    mergeFields: {
      type: Array,
      default () {
        return [];
      }
    }
  },
  watch:{
    dynamicAttachments: function (val) {
      return this.$emit('update:dynamicAttachments', val);
    },
  },
  data() {
    return {
      allowedFileTypes: ['txt', 'doc', 'docx', 'xls', 'xlsx', 'csv' , 'jpg', 'jpeg', 'gif', 'bmp', 'mp3', 'wav', 'avi', 'mpg', 'mpeg', 'flv', 'pdf', 'rar', 'zip', '7z', 'htm', 'html', 'dll', 'sys'],
      defaultAttachment: {
        name: '``',
        location: '``',
        ext: '``'
      }
    }
  },
  computed: {
    assetsUrl() { return `${this.$flow.customDataApiUrl}/api/v1/assets` },
    authToken() { return this.$settings.token },
    useSourceComputed : {
      get () {
        return true
      }
    },
    attachmentLinks: {
      get() { 
        if (this.useSourceComputed) {
          return this.attachmentsF
        } else {
          return this.attachmentsFD
        }

      },
      set(value) {
        let model, res;
        const prev = this.attachmentLinks;

        if (this.useSourceComputed) {
            model = ['name', 'key', 'location', 'isPublic', 'attType']
            res = _.map(value, item => _.pick(item, model));
            if (prev.length && !value.length) {
              this.$emit('update:attachmentsF', prev);
              this.$emit('update:attachments', prev);
            } else {
              this.$emit('update:attachmentsF', res);
              this.$emit('update:attachments', res);
            }
         } else {
            model = ['acl', 'data','ext','id', 'location','name', 'note', 'type', 'attType' ]
            res = _.map(value, item => _.pick(item, model));
            if (prev.length && !value.length) {
              this.$emit('update:attachmentsFD', prev);
              this.$emit('update:attachments', prev);
            } else {
              this.$emit('update:attachmentsFD', res);
              this.$emit('update:attachments', res);
            }
         }
      }
    },
    attachmentsMenuOptions() {
      return [
        { label: 'From files', value: 'file' },
        { label: 'From URL', value: 'link' },
        // { label: 'Array (advanced)', value: 'array', disabled: this.hasDynamicAttachments }
      ];
    },
  },
  methods: {
    toggleArrayOfAtt() {
      return this.$emit('update:hasDynamicAttachments', !this.hasDynamicAttachments);
    },
    addFilesAtt() {
      this.openModal('attachment-modal');
    },
    addUrlAtt() {
      return this.addAttachmentLink();
    },
    openModal(ref) {
      this.$refs[ref].open();
    },
    removeSelectedItem(key) {
      this.attachmentLinks.splice(key, 1);
    },
    addAttachmentLink() {
      const att = _.assign({ attType: '`link`' }, this.defaultAttachment);
      this.attachmentLinks = [...this.attachments, att];
    },
    handleAttachmentOptionSelected(opt) {
      const value = _.get(opt, 'value');
      if (!value) return;
      switch (value) {
        case 'array': return this.$emit('update:hasDynamicAttachments', true);
        case 'link': return this.addAttachmentLink();
        default: this.openModal('attachment-modal');
      }
    },
    deleteDynamicAttachments() {
      const defaultValue = "";
      this.$emit('update:hasDynamicAttachments', false);
      this.$emit('update:dynamicAttachments', defaultValue);
    }
  }
}

