data.componentLogic:
{
  data(){
    return {
      childrenValidatros: []
    }
  },
  computed : {
    state : {
      get () { return this.value; },
      set (value) { this.$emit('input', value); }
    },
    collapsible(){
      return this.$parent.$parent.$parent.$parent;
    },
    invalidSchemaValidators(){
      return _.keys(this.state._schemaValidators || [])
        .reduce((invalid, key) => {
          if (invalid) return invalid;
          const schemaValue = this.schema[key];
          const validator = new Function('value', `return ${this.state._schemaValidators[key]}`);
          return !validator(schemaValue);
        }, false);
    },
    invalid(){
      return this.childrenValidatros.reduce((inv, validator) => {
        if (inv) return inv;
        return validator.$invalid;
      }, false) || this.invalidSchemaValidators;
    }
  },
  props : {
    value : {
      type : Object,
      default : {}
    },
    isNew : Boolean,
    readonly: {
      type : Boolean 
    },
    schema: {
      type: Object,
      default: {}
    }
  },
  watch: {
    invalid(value){
      this.collapsible.invalid = value;
      if(value) {
        this.collapsible.isOpen = true;
      }
    },
  },
  methods: {
    findChildrenValidators(){
      return new Promise(res => {
        const validators = [];

        const findValidators = (children, validators) => {
          children.forEach(child => {
            if(child.$v) {
              validators.push(child.$v);
            } else {
              findValidators(child.$children, validators);
            }
          });
        };

        this.$nextTick(() => {
          findValidators(this.collapsible.$children, validators);
          res(validators);
        });
      });
    },
    setWatchers(){
      _.keys(this.state).filter(k => k[0] !== '_').forEach(key => {
        this.$watch(`collapsible.${key}`, value => {
          if (this.invalid) return;
          this.state = _.assign(this.state, { [key]: value });
        });
      });
    },
    setCollapsibleState(){
      if (this.invalid) {
        this.collapsible.isOpen = true;
        this.collapsible.invalid = true;
        return;
      }
      _.keys(this.state).filter(k => k[0] !== '_').forEach(key => {
        this.collapsible[key] = this.state[key];
      });
    },
  },
  mounted(){
    this.collapsible.preserveInvalid = true;
    this.setWatchers();
    this.setCollapsibleState();
    this.findChildrenValidators().then(validators => {
      this.childrenValidatros = validators;
    });
  }
}

