stepTemplates.template:
//---------------- report Transcript Event ----------------
const report = ({
  message, 
  mediaUrl, 
  transcriptId, 
  previousTranscriptId, 
  botIdentifier, 
  endUserIdentifier
}) => {
  const reportingObject = {
    botIdentifier,
    message,
    previousTranscriptId,
    eventId              : transcriptId,
    channel              : _.isEmpty(mediaUrl) ? 'SMS' : 'MMS',
    reportingSettingsKey : 'transcript',
    media                : _.map(mediaUrl, fileUrl => ({fileUrl, fileType : 'File'})),
    fromId               : this.config.flowId,
    fromType             : 'Bot',
    toType               : 'Visitor',
    toIdentifier         : endUserIdentifier,
    fromIdentifier       : botIdentifier
  };

  this.reporter.reportTranscriptEvent(reportingObject);
};
//----------------------------------------------------------

//---------------- Random and condtion logic ----------------
const makeCompare = (rule) => {
  let firstValue = rule.valueType === 'other' ? rule.firstValue : rule.firstValue.trim();
  let secondValue = rule.valueType === 'other' ? rule.secondValue : rule.secondValue.trim();

  if (rule.valueType === 'number') {
    firstValue = isNaN(rule.firstValue) ? NaN : parseFloat(rule.firstValue);
    secondValue = isNaN(rule.secondValue) ? NaN : parseFloat(rule.secondValue);
  }

  if (rule.valueType === 'advanced') {
    const flowContext = this;
    return new Function('_', `return ${rule.codeValue}`).bind(flowContext)(_)
  } else {
    let compareMethod = rule.compareMethod;
    return compareMethod(firstValue, secondValue) 
  }
}

const getRandomMessage = (messages) => {
  const weights = _.map(messages, condition => {
    if (isNaN(parseFloat(condition.weight)))
      throw new Error(`Weight value ${condition.weight} is invalid.`);

    if (parseFloat(condition.weight) <= 0)
      throw new Error(`Weight value should be a positive number.`);

    return parseFloat(condition.weight);
   });

  const selectionIndex = weightedRandom(weights);

  return messages[selectionIndex];
};

const getConditionsMessage = (messages) => {
  let conditionMessage = {};
  let isMatch = false;
  try {
    _.forEach(messages, message => {
      let isRule = (message.trueValue === 'any') ? _.some(message.rules, rule => makeCompare(rule)) : _.every(message.rules, rule => makeCompare(rule))

      if (isRule) {
        conditionMessage = message;
        isMatch = true;
        return false;
      }
    })

    return isMatch ? conditionMessage : { "message": noConditionMessage, "medias": noConditionMedias };
  } catch (error) {
    throw error
  }
};

const getMessage = () => {
  switch (activeTabId) {
    case 'single':
      return singleMessage;
    case 'random':
      return getRandomMessage(randomGroups);
    case 'condition':
      return getConditionsMessage(conditionGroups);
  }
};
//----------------------------------------------------------

//-------------------- exitStep helper ---------------------
const exitStep = async exit => {
  return this.exitStep(exit, {
    transcriptId,
    ... _.pick(conversation, ['botNumber', 'endUserNumber', 'lastTranscriptId'])
  });
};
//----------------------------------------------------------

// dependencies
const uuid = require('uuid');
const Request = require('or-sdk/request');
const request = new Request(this);
const timestring = require('timestring');
const weightedRandom = require('weighted-random');

// read/create conversation
let conversation;
if (continueConversation) {
  conversation = await this.mergeFields[conversationMeta].get();
} else {
  const normalizedBotNumber = _.isNumber(botNumber) ? `+${botNumber}` : botNumber; // when step updated from older version, bot number data could be type of number
  const botNumberCountryCode = this.helpers.detectIdentifierCountry(normalizedBotNumber);
  const normalizedUserNumber = this.helpers.normalizePhoneNumber(_.toString(endUserNumber), botNumberCountryCode);
  conversation = {
    botNumber        : normalizedBotNumber,
    endUserNumber    : normalizedUserNumber,
    lastTranscriptId : undefined
  };
}

// main logic
if (_.isEmpty(conversation.botNumber) && !continueConversation)
  throw new Error('Bot number is not allowed to be empty');

if (_.isEmpty(conversation.endUserNumber) && !continueConversation)
  throw new Error('End user number is not allowed to be empty');

// parse timeout
let timeoutTime;
try {
  timeoutTime = timestring(String(timeout), 'ms');
} catch(e) {
  timeoutTime = timestring('24h', 'ms');
};

// generate / read transcript id
const transcriptId = await thisStep.getset(this, 'transcriptId', uuid.v4());

this.triggers.once(`status/text/${transcriptId}`, event => {
  if (event.params.status === 'delivered') {
    return checkStatus ? exitStep('next') : this.exitFlow();
  } else if (_.includes(['failed', 'undelivered', 'optout'], event.params.status)) {
    throw new Error(event.params.status);
  } else if (checkStatus) {
    return this.exitFlow();
  } else {
    return exitStep('next');
  }
});

if (checkStatus) {
  this.triggers.timeout(timeoutTime, () => exitStep('next'));
}

return this.triggers.otherwise(async () => {
  if (/^status\/text\/.*/.test(this.event.name) && !this.event.processed){
    return this.exitFlow();
  }

  const messageItem = getMessage();
  const message = messageItem.message;

  const event = {
    name    : 'out/text',
    target  : this.helpers.providersAccountId,
    params  : {
      channel : {
        id   : transcriptId,
        from : conversation.botNumber,
        to   : conversation.endUserNumber
      },
      message
    }
  };

  const mediaLocations = _.chain(messageItem.medias)
    .map('location')
    .compact()
    .value();

  event.params.mediaUrl = _.compact(
    await Promise.map(mediaLocations, async url => {
      try {
        const response = await request.head({
          url,
          timeout                 : 2000,
          resolveWithFullResponse : true,
          reporting               : {
            disable : true
          }
        });

        if (response.statusCode === 200) return url;

        this.log.error(response);
        this.log.error(`Error: Image ${url} Status Code: ${response.statusCode}`);
      } catch (error) {
        this.log.error(error);
      }
    })
  );

  if (_.isEmpty(message) && _.isEmpty(event.params.mediaUrl))
    throw new Error('Text or media is required');

  await this.emitAsync(event);

  report({
    ... event.params, 
    transcriptId, 
    previousTranscriptId : conversation.lastTranscriptId, 
    botIdentifier        : conversation.botNumber, 
    endUserIdentifier    : conversation.endUserNumber
  });

  conversation.lastTranscriptId = transcriptId;
  if (continueConversation) conversation = await this.mergeFields[conversationMeta].set(conversation);

  return this.exitFlow();
});

data.toJson:
function isExpression(path, data) {
  return (/:(message)/g).test(path);
}

function convertMedias(medias) {
  let filteredMedias = _.filter(medias, media => (_.has(media, 'linkUrl') && media.linkUrl.length && media.linkUrl !== "``") || _.has(media, 'location'));
  let finalMedias = _.map(filteredMedias, media => {
    return media.linkUrl ? `{ location: ${media.linkUrl} }` : `{ location: ${JSON.stringify(media.location)} }`
  })

  return finalMedias.length ? `[${finalMedias}]` : `[]`;
}

//function that describe how to serialize wildcard data 
function toJson(data, key = '', parentPath = '') { 
  const path = `${parentPath}:${key}`;

  if ((/:medias/g).test(path))
    return convertMedias(data)

  if (_.isArray(data)) 
    return `[${_.map(data, (value, index) => toJson(value, index, path)).join(',')}]`;

  if (_.isObject(data))
    return `{${_.map(data, (value, key) => `${key}: ${toJson(value, key, path)}`).join(',')}}`;

  if (isExpression(path, data)) 
    return data;

  return JSON.stringify(data);
}

return _.mapValues(data, toJson);

