return _.mapValues(data, toJson);

data.validators:
randomGroups : {
  $each : {
    message : {
      custom : (value, ctx) => {
        return value.slice(1,-1) || (ctx.medias.length && _.filter(ctx.medias, media => _.get(media, 'linkUrl') ? media.linkUrl.slice(1, -1) : true).length);
      }
    },
    medias : {
      custom : (value, ctx) => {
        return (value.length && _.filter(value, media => _.get(media, 'linkUrl') ? media.linkUrl.slice(1, -1) : true).length) || ctx.message.slice(1,-1);
      }
    },
    weight : {
      custom : (value, ctx, some) => { 
        const isMergeField = value => {
          return /this.*/.test(value) || /await this.*/.test(value);
        }

        const isCodeMode = value => {
          return !/^\`.*\`$/.test(value);
        }

        if (isMergeField(value) && validators.jsExpression(value))
          return true

        if (!isCodeMode(value))
          return parseFloat(value.slice(1, -1)) > 0 && validators.jsExpressionNonEmptyString(value);

        return validators.jsExpressionNonEmptyString(value) && validators.jsExpression(value);
      }
    }
  }
}

data.componentLogic:
{
  data() {
    return {
    }
  },

  props: {
    randomGroups: {
      type: Array,
      default () {
        return [];
      }
    },
    mergeFields: {
      type: Array,
      default () {
        return [];
      }
    },
    useFiles: Boolean,
    activeTabId: String,
    isNew: Boolean,
    schema: Object,
    steps: '',
    stepId: '',
    readonly: {
      type: Boolean,
      default: false
    },
    v : {
      type: Object,
      default() {
        return {};
      }
    }
  },

  computed: {
    randomGroupsLocal : {
      get () {
        return this.randomGroups;
      },
      set (newValue) {
        this.$emit('update:randomGroups', newValue);
        this.$nextTick(() => {
          if (this.$refs['message-weight-0'])
            this.updateProbabilityInPercent(newValue);
        })
      }
    },
    useFilesComputed : {		
      get () {		
        return this.useFiles; 		
      },		
      set (value) {		
        this.$emit('update:useFiles', value);		
      }
    }
  },

  watch: {
    activeTabId (newValue) {
      if (newValue === 'random' && this.randomGroupsLocal.length < 2)
        this.$refs.randomList.addItem();
    }
  },

  mounted () {
    if (this.activeTabId === 'random' && this.$refs.randomList && this.randomGroupsLocal.length < 2) {
      this.$refs.randomList.addItem();

      if (!this.isNew && this.v) {
        this.$nextTick(() => {
          this.v.schema.$touch();
        })
      }
    }
  },

  methods: {
    newMessage () {
      return {
        message          : '``',
        medias           : [],
        weight           : "`1`",
        weightLabel      : "Weight"
      };
    },
    updateProbabilityInPercent(messages) {
      let isInvalidWeight = false;
      let weightArr = [];
      let weightSum = messages.reduce((sum, message, index) => {
        let weight;
        if (this.$refs['message-weight-' + index].mode == 'code') {
          weight = parseFloat(message.weight);
        } else {
          weight = parseFloat(message.weight.slice(1, -1));
        }

        if (isNaN(weight) || weight <= 0 || this.isMergeFieldWeight(index)) {
          isInvalidWeight = true;
          return false
        }

        weightArr.push(weight);
        return sum + weight
      }, 0);

      _.forEach(messages, (message, index) => {
        if (isNaN(weightSum) || isInvalidWeight) {
          message.weightLabel = "Weight";
        } else {
          let calculatedResult = (weightArr[index] / weightSum * 100).toFixed(0);

          if (calculatedResult == 0) {
            calculatedResult = 1;
          } else if (calculatedResult == 100) {
            calculatedResult = 99;
          }

          message.weightLabel = `Weight (${(weightArr[index] / weightSum * 100).toFixed(0)}%)`
        }
      })
    },
    errorText (weight) {
      let text = '';

      if (!weight || weight === '``') {
        text = 'Weight is required';
      } else if (!validators.jsExpression(weight)) {
        text = 'Please enter a valid JavaScript expression.';
      } else {
        text = 'Only positive number can be used as a value';
      }

      return text;
    },
    isMergeFieldWeight (index) {
      const isMergeField = (value) => {
        return /this.*/.test(value) || /await this.*/.test(value);
      }

      return isMergeField(this.randomGroups[index].weight);
    }
  },

  components: {
    messageItem: {
      template: `<%= messageItem %>`,
      props : {
        value : {
          type : Object,
          default () {
            return {
              medias: [],
              message: '``'
            };
          }
        },
        mergeFields: {
          type: Array,
          default () {
            return [];
          }
        },
        useFiles : Boolean,
        isNew : Boolean,
        readonly : {
          type : Boolean,
          default : false
        },
        steps: Array,
        stepId: String,
        v : {
          type: Object,
          default () {
            return {};
          }
        }
      },

      data () {
        return {
          iconPosition: 'left',
          allowedFileTypes: ['jpg', 'jpeg', 'png', 'gif', 'wav', 'mp3', 'mp4'],
          maxFiles: 10,
          localMessage: this.value,
          messageInfoText: 'An SMS text message is limited by up to 1600 characters in standard GSM encoding (up to 670 in Unicode). A message longer than 160 characters is parsed into several messages but is received uniformly by the end user. The field can be defined manually and using merge fields (click on the `{x}` icon). Note that using merge field values makes the length of a message a variable, so make sure that it is below the limit.\n\nThe cumulative size of the media files is limited by 5 Mb. Supported formats include jpeg, gif and png, other formats (i.e. bnp, mpeg, mp4, waw) are also acceptable but the content may not be modified for device compatibility from telecom provider side.\n\nTo add files from your OneReach storage, click on the respective option, then choose the necessary media in the modal window of `Files & Data` or `Files`. Note that any operations with data (renaming, deleting, etc.) are done throughout your OneReach.ai account and may affect other flows. To use a link, click *URL* and specify it manually or using merge fields.\n\nMultiple files or links can be chosen via *add media* button or removed by a click on `X` next to it in the step details panel. A text message will be sent first, and then the selected files, in order from top to bottom.'

        }
      },

      computed : {
        assetsUrl () { return `${this.$flow.customDataApiUrl}/api/v1/assets`},
        authToken () { return this.$settings.token },
        medias : {
          get () {
            return this.value.medias
          },
          set (value) {
            const model = this.useFiles
              ? ['name', 'key', 'location', 'isPublic', 'linkUrl']
              : [
                  'acl',
                  'data',
                  'ext',
                  'id',
                  'location',
                  'name',
                  'note',
                  'type',
                  'linkUrl',
                ];
            const res = _.map(value, item => _.pick(item, model));
            this.localMessage.medias = res;
          }
        },
        useFilesComputed : {
          get () {
            return this.useFiles;
          },
          set (value) {
            this.$emit('update:useFiles', value);
          }
        }
      },

      methods: {
        openModal (ref) {
          this.$refs[ref].open();
        },
        removeSelectedItem (key) {
          this.localMessage.medias.splice(key, 1);
        },
        addMedia () {
          if (_.has(this.localMessage.medias[0], 'linkUrl')) {
            this.localMessage.medias.push(this.addNewUrl());
          } else {
            this.openModal('select-media');
          }
        },
        addNewUrl () {
          return {
            linkUrl: "``"
          }
        }
      },

      watch: {
        localMessage: {
          handler(newValue) {
            this.$emit('input', newValue);
          },
          deep: true
        }
      }
    },
  }
}

