data.toJson:
function isExpression(path, data) {
  return (/:(message|weight)/g).test(path);
}

function convertMedias(medias) {
  let filteredMedias = _.filter(medias, media => (_.has(media, 'linkUrl') && media.linkUrl.length && media.linkUrl !== "``") || _.has(media, 'location'));
  let finalMedias = _.map(filteredMedias, media => {
    return media.linkUrl ? `{ location: ${media.linkUrl} }` : `{ location: ${JSON.stringify(media.location)} }`
  })

  return finalMedias.length ? `[${finalMedias}]` : `[]`;
}

//function that describe how to serialize wildcard data 
function toJson(data, key = '', parentPath = '') { 
  const path = `${parentPath}:${key}`;

  if ((/:medias/g).test(path))
    return convertMedias(data)

  if (_.isArray(data)) { 
    return `[${_.map(data, (value, index) => toJson(value, index, path)).join(',')}]`;
  }

  if (_.isObject(data)) {
    return `{${_.map(data, (value, key) => `${key}: ${toJson(value, key, path)}`).join(',')}}`;
  }

  if (isExpression(path, data)) return data;

  return JSON.stringify(data);
}

return _.mapValues(data, toJson);

