{
  props: {
    continueConversation: {
      type : Boolean,
      default : true
    },
    readonly : {
      type : Boolean,
      default : false
    },
    stepId : {
      type: String,
      default: ''
    },
    steps : {
      type: Array,
      default: []
    }
  },
  mounted() {
    this.toggleConversation();
  },
  computed: {
    localContinueConversation: {
      get() {
        return this.continueConversation;
      },
      set(value) {
        if (this.continueConversation !== value) {
          this.$emit('update:continueConversation', value);
          this.$nextTick(() => this.toggleConversation());
        }
      }
    }
  },
  methods: {
    toggleConversation() {
      const step = _.find(this.steps, {id : this.stepId});
      if (!_.isObject(_.get(step, 'data.dataOut'))) {
        return;
      }

      if (this.continueConversation && _.get(step, 'data.dataOut.meta.conversationStarter')) {
        _.unset(step, 'data.dataOut.meta');
      } else if (!this.continueConversation && !_.get(step, 'data.dataOut.meta.conversationStarter')) {
        _.set(step, 'data.dataOut.meta', {
          conversationType: 'sms',
          conversationStarter: true
        });
      }
    }
  }
}

data.validators:
conversationMeta: {
  custom (value, schema) {
    if (!this.schema.continueConversation)
      return true

    const flowMergeTags =  _.chain(mergeFields).find({title : 'Flow'}).get('variables').map(tag => ({
      name  : _.get(tag, 'name'),
      meta  : _.get(tag, 'meta')
    })).filter('name').value();

    const sharedMergeTags =  _.chain(mergeFields).find({title : 'Shared'}).get('variables').map(tag => ({
      name  : _.get(tag, 'name'),
      meta  : _.get(tag, 'meta')
    })).filter('name').value();

    const globalMergeTags =  _.chain(mergeFields).find({title : 'Global'}).get('variables').map(tag => ({
      name  : _.get(tag, 'name'),
      meta  : _.get(tag, 'meta')
    })).filter('name').value();

    const options = _.chain([])
      .concat(flowMergeTags, sharedMergeTags, globalMergeTags)
      .filter(tag => _.get(tag, 'meta.conversationStarter') && _.get(tag, 'meta.conversationType') === 'sms')
      .map('name')
      .uniq()
      .value();

    return _.includes(options, value);
  }
}

data.componentLogic:
{
  props : {
    continueConversation : {
      type : Boolean,
      default : true
    },
    mergeFields : {
        type    : Array,
        default () {
            return [];
        }
    },
    conversationMeta : {
      type : String,
      default : ''
    },
    stepId : String,
    steps : {
      type : Array,
      default () {
        return [];
      }
    },
    readonly : {
      type : Boolean,
      default : false
    },
    v : {
      type: Object,
      default() {
        return {
          schema: {
            conversationMeta: {}
          }
        };
      }
    }
  },

  data() {
    return {
      options : this.fillOptions(),
      conversationRequiredMessage: 'Conversation is required.'
    }
  },

  computed: {
    selectedStepId () {
        return this._findStepIdByDataOut(this.conversationMeta);
    },

    selectedConversation : {
      get () {
        return {name : this.conversationMeta};
      },
      set (value) {
        this.$emit('update:conversationMeta', value.name);
      }
    }
  },

  methods : {
    _getDataOutName (dataOut) {
      return _.get(dataOut, 'name', dataOut);
    },

    _findStepIdByDataOut (mergeFieldMeta) {
      return _.chain(this.steps)
      .find(step => this._getDataOutName(_.get(step, 'data.dataOut')) === this._getDataOutName(mergeFieldMeta))
      .get('id')
      .value();
    },

    highlightConversationOn(mergeFieldMeta) {
      const stepId = this._findStepIdByDataOut(mergeFieldMeta);
      if (stepId) {
        this.$flow.highlightStepOn({stepId, highlightClass : 'hover'});
      }
    },

    highlightConversationOff(mergeFieldMeta) {
      const stepId = this._findStepIdByDataOut(mergeFieldMeta);
      if (stepId) {
        this.$flow.highlightStepOff({stepId, highlightClass : 'hover'});
      }
    },

    highlightConversationOffOnMouseout(mergeFieldMeta) {
      const stepId = this._findStepIdByDataOut(mergeFieldMeta);
      if (stepId && stepId !== this.selectedStepId) {
        this.$flow.highlightStepOff({stepId, highlightClass : 'hover'});
      }
    },

    zoomInOnStep () {
      if (this.selectedStepId) {
        this.$flow.zoomInOnStep(this.selectedStepId);
      }
    },

    fillOptions () {
      const flowMergeTags = _.chain(this.mergeFields).find({title : 'Flow'}).get('variables').map(tag => ({
        name  : _.get(tag, 'name'),
        meta  : _.get(tag, 'meta')
      })).filter('name').value();

      const sharedMergeTags = _.chain(this.mergeFields).find({title : 'Shared'}).get('variables').map(tag => ({
        name  : _.get(tag, 'name'),
        meta  : _.get(tag, 'meta')
      })).filter('name').value();

      const globalMergeTags = _.chain(this.mergeFields).find({title : 'Global'}).get('variables').map(tag => ({
        name  : _.get(tag, 'name'),
        meta  : _.get(tag, 'meta')
      })).filter('name').value();

      // options value has to be an object ({name : tag.name}), otherwise - weird behaviour
      const options = _.chain([])
        .concat(flowMergeTags, sharedMergeTags, globalMergeTags)
        .filter(tag => _.get(tag, 'meta.conversationStarter') && _.get(tag, 'meta.conversationType') === 'sms')
        .uniqBy('name')
        .sortBy('name')
        .map(tag => ({value : {name : tag.name}, label : tag.name}))
        .value();

      return options;
    }
  },

  created () {
    if (_.isEmpty(this.conversationMeta) && this.options && this.options.length === 1) {
      console.log('this.selectedConversation =');
      this.selectedConversation = _.get(this.options, '0.value');
    }
    this.$nextTick(() => {
      this.highlightConversationOn(this.conversationMeta);
    });
  },

  beforeDestroy () {
    this.highlightConversationOff(this.conversationMeta);
  }
}

