timeout : {
  required: validators.required,
  custom(value) {
    const regexMergefield = /\${this\.get\(\'(.{1,})\'\)}$/;
    const timeout = value.slice(1,-1);
    const timeoutToNumber = _.toNumber(timeout);
    let parsedTimeout;

    try {
      parsedTimeout = libs.timestring(_.isNaN(timeoutToNumber) ? timeout : `${timeout} ms`, 'ms');
      return (parsedTimeout >= 1000 && parsedTimeout <= 172800000) || regexMergefield.test(timeout);
    } catch(e) {
      return false;
    }
  },
