const short = require('short-uuid');
const translator = short();
const ts = require('timestring');
const TimeoutError = require('@onereach/flow-sdk/errors/timeout');

flowId = isUseCustomSubflow ? customSubFlowName.toLowerCase() : flowIdToTrigger;
let timeout = isAsync ? timeoutAsync : timeoutSync;
timeout = ts(timeout.input, timeout.outputMode);

if ((isAsync && (timeout < 60 * 1000 || timeout > 72 * 60 * 60 * 1000)) || (!isAsync && (timeout < 1000 || timeout > 24 * 60 * 60 * 1000))) {
  throw new Error(`Invalid timeout: ${timeout}`);
}

if (isUseCustomSubflow && paramsProcessType === 'manually') {
  flowParameters = customFlowParameters
}

const params = {
  flowId,
  isSync: !isAsync
};

// check assigment

_.assign(params, flowParameters);

const target = isUseCustomSubflow ? customTriggerTargetAccountId.toLowerCase() : targetAccountId

const event = {
  name: `try/${flowId}`,
  params,
  ...(target && { target })
};

if (isAsync) {
  let eventId = thisStep.get(this, ['eventId'], false);

  // eventId will be truthy if this execution is for result/error/timeout
  if (!eventId) {
    eventId = translator.new();
    thisStep.set(this, ['eventId'], eventId);
  }

  return this.triggers
    .once(`result/${eventId}`, (event) => {
      // const exitName = event.params.selectedExit || 'result';
      const exitName = event.params.selectedExit;
      if (exitName == 'error') throw new Error(JSON.stringify(event.params.error));
      return this.exitStep(exitName, event.params.data);
    })
    .timeout(timeout, () => {
      if (processTimeout) {
        throw new TimeoutError('timeout');
      }
      throw new Error('Timeout appear');
    })
    .otherwise(async () => {
      await this.triggers.flush()
      event.params.eventId = eventId;
      const result = await this.emitAsync(event);

      if (_.isNil(result)) {
        const errorText = 'Unable to find the flow you\'ve tried to trigger. Re-check your subflow name.';
        await this.setDataOut({
          errorText,
          event
        });
        throw new Error(errorText);
      }

      return this.exitFlow();
    })
} else {
  return this.emitSync(event, null, timeout)
    .then(result => {
      const responseResult = _.get(result, 'result', {});
      // this.log.warn('SYNC RUN RESULT', responseResult)

      if (_.isEmpty(responseResult) || responseResult.error) {
        throw new Error(JSON.stringify(_.get(responseResult, 'error', 'Unknow error')));
      } else if (responseResult === 'Invalid timeout') {
        throw new Error('Invalid timeout in subflow');
      }

      // const exitName = responseResult.selectedExit || 'result';
      const exitName = responseResult.selectedExit;
      return this.exitStep(exitName, responseResult.data);
    }).catch(error => {
      if (processTimeout && error.message.indexOf('timeout') >= 0) {
        throw new TimeoutError('timeout');
      }
      throw new Error(error.message);
    });
}

