let makeCompare = (rule) => {
  let firstValue = rule.valueType === 'string' ? rule.firstValue.trim() : rule.firstValue;
  let secondValue = rule.valueType === 'string' ? rule.secondValue.trim() : rule.secondValue;

  if (!isCaseSensitive && rule.valueType === 'string') {
    firstValue = firstValue.toLowerCase();
    secondValue = secondValue.toLowerCase();
  }

  if (rule.valueType === 'number') {
    firstValue = isNaN(rule.firstValue) ? NaN : parseFloat(rule.firstValue);
    secondValue = isNaN(rule.secondValue) ? NaN : parseFloat(rule.secondValue);
  }

  if (rule.valueType === 'advanced') {
    const flowContext = this;
    return new Function('_', `let codeValue = arguments[0]; return codeValue`).bind(flowContext)(rule.codeValue, _)
  } else {
    let compareMethod = rule.compareMethod;
    return compareMethod(firstValue, secondValue) 
  }
}

let exitId;
let isCondition = false;
_.forEach(conditionExits, exit => {
  let isRule = (exit.trueValue === 'any') ? _.some(exit.rules, rule => makeCompare(rule)) : _.every(exit.rules, rule => makeCompare(rule))

  if (isRule) {
    isCondition = true;
    exitId = exit.id;
    return false
  }
})

try {
  return isCondition ? this.exitStep(exitId) : this.exitStep('else');
} catch(error) {
  throw error
}

