const moment = require('moment-timezone');
const dateFormat = 'YYYY-MM-DD HH:mm';
const dateFormatWithoutHour = 'YYYY-MM-DD';
const timeZoneValue = _.trim(timeZone.value, '`') || '';

if (_.isNil(moment.tz.zone(timeZoneValue))) {
  this.log.error(`Invalid timezone: ${timeZoneValue}`);
  return processError ? this.exitStep('error', { invalidTimezone: timeZoneValue }) : this.exitFlow();
}

const nowMoment = moment(new Date()).tz(timeZoneValue);
const nowDate = nowMoment.format(dateFormat);
const nowDateInMS = nowMoment.format('x');
const nowDateWithoutHours = nowMoment.format(dateFormatWithoutHour);
const nowWeekDayNumber = String(nowMoment.day());
const nowTime = moment(nowDate).format('HH:mm');
const specificDatesDimensions = [];
const weeklyDimensions = [];
let periodOfTime = [];
let weeklyPeriodOfTime = [];
_.map(routingLegsList, routingItem => {

  _.map(routingItem.specificDatesList, item => {

  _.map(item.periodOfTime, period => { 
      periodOfTime.push({
        from:  _.get(period, 'from') || '00:00',
        to: _.get(period, 'to') || '23:59'
      });
    });

    specificDatesDimensions.push({
      exitId: routingItem.vforkey,
      periodOfDays: {
        from: item.periodOfDays.from,
        to: item.periodOfDays.to
      },
      periodOfTime: _.clone(periodOfTime)
    });

    periodOfTime.length = 0;
  });

  _.map(routingItem.weeklyList, item => {

    _.map(item.periodOfTime, period => {
      weeklyPeriodOfTime.push({
        from: _.get(period, 'from') || '00:00',
        to: _.get(period, 'to') || '23:59'
      });
    });

    weeklyDimensions.push({
      exitId: routingItem.vforkey,
      weekDays: _.map(item.weekDays, day => day.value),
      periodOfTime: _.clone(weeklyPeriodOfTime)
    });

    weeklyPeriodOfTime.length = 0;
  });
}); 

const checkTimePeriod = (periodOfTimeArr, nowTime) => {
  let flag = false;
  _.forEach(periodOfTimeArr, period => {
    const fromSplited = period.from.split(':');
    const fromHours = fromSplited[0];
    const fromMinutes = fromSplited[1];
    const formatedTimeFrom = moment(new Date(null, null, null, fromHours, fromMinutes)).format('HH:mm');
    const toSplited = period.to.split(':');
    const toHours = toSplited[0];
    const toMinutes = toSplited[1];
    const formatedTimeTo = moment(new Date(null, null, null, toHours, toMinutes)).format('HH:mm');

    if (nowTime >= formatedTimeFrom && nowTime <= formatedTimeTo) {
      flag = true;
      return false;
    }
  })

  return flag;
}

/* Checking Specific Dates Dimensions*/
for (let i = 0; i <= specificDatesDimensions.length - 1; i++) {
  const dayFrom = specificDatesDimensions[i].periodOfDays.from;
  const dayTo = specificDatesDimensions[i].periodOfDays.to;
  const periodOfTime = specificDatesDimensions[i].periodOfTime;
  const exitId = specificDatesDimensions[i].exitId;

  if (moment(nowDateWithoutHours).isSameOrAfter(dayFrom) 
      && moment(nowDateWithoutHours).isSameOrBefore(dayTo) 
      && checkTimePeriod(periodOfTime, nowTime)) {
    return this.exitStep(exitId, { nowDate, nowDateInMS });
  }
}

/* Checking Weekly Dimensions*/

for (let i = 0; i <= weeklyDimensions.length - 1; i++) {
  const periodOfTime = weeklyDimensions[i].periodOfTime;
  const weekDays = weeklyDimensions[i].weekDays;
  const exitId = weeklyDimensions[i].exitId;

  if (_.indexOf(weekDays, nowWeekDayNumber) !== -1 && checkTimePeriod(periodOfTime, nowTime)) {
    return this.exitStep(exitId, { nowDate, nowDateInMS });
  }
}

return this.exitStep('else', { nowDate, nowDateInMS });

