                {label: 'Mon', value: '1'}, {label: 'Tue', value: '2'}, {label: 'Wed', value: '3'},
                {label: 'Thu', value: '4'}, {label: 'Fri', value: '5'}, {label: 'Sat', value: '6'},
                {label: 'Sun', value: '7'}
              ]
            }
          },
          
          watch: {
            weeklyLocal: {
              handler (newValue) {
                this.$emit('update:weekly', newValue);
              },
              deep: true
            },
            
            periodOfTime: {
              handler (newValue) {
                _.set(this.weeklyLocal, 'periodOfTime', newValue);
              },
              deep: true
            }
          },
      
          methods: {
            addPeriodOfTime () {
              this.periodOfTime.push({
                from: '',
                to: ''
              })
            },
            
            deletePeriodOfTime (index) {
              this.periodOfTime.splice(index, 1);
            },
            
            toggleWeeklyDays (day) {
              const index = _.findIndex(this.weeklyLocal.weekDays, weekDay => {
                return weekDay.value === day.value;
              });
              
        
              if (index !== -1) {
                this.weeklyLocal.weekDays.splice(index, 1);
              } else {
                // initialize if undefined
                if (!this.weeklyLocal.weekDays) {
                  this.weeklyLocal.weekDays = [];
                }
        
                this.weeklyLocal.weekDays.push(day);
                
              }
            },
        
            isWeekBtnActive (day) {
              return _.find(this.weekly.weekDays, weekDay => {
                return weekDay.value === day.value;
              })
            }
          },
    
          template: `<%= weeklyItem %>`
        },
        
        specificDatesItem: {
          props: {
            specificDates: {
              type: Object,
              default () {
                return {};
              }
            },
            readonly: {
              type: Boolean,
              default: false
            }
          },
        
          data () {
            return {
              specificDatesLocal: this.specificDates,
              periodOfTime: _.get(this.specificDates, 'periodOfTime') || [],
              timePeriodType: ['All Day (24 Hours)', 'Specific Time']
            };
          },
          
          computed : {
            timePeriodTypeOfDay: {
              get () {
                return this.specificDatesLocal.timePeriodTypeOfDay;
              },
              
              set (newValue) {
                if (newValue === 'All Day (24 Hours)') {
                  this.periodOfTime = [{
                    from: '',
                    to: ''
                  }];
                }
                _.set(this.specificDatesLocal, 'timePeriodTypeOfDay', newValue);
              }
            },
            
            startDate: {
              get () {
                const date = _.get(this.specificDatesLocal, 'periodOfDays.from');
                return date ? new Date(`${date} 00:00:00`) : null;
              }, 
              
              set (newValue) {
                const timeZone = libs.moment.tz.guess();
                const date = libs.moment(newValue).tz(timeZone).format('YYYY-MM-DD');
                _.set(this.specificDatesLocal, 'periodOfDays.from', date);
              }
            },
            
            endDate: {
              get () {
                const date = _.get(this.specificDatesLocal, 'periodOfDays.to');
                return date ? new Date(`${date} 00:00:00`) : null;
              }, 
              
              set (newValue) {
                const timeZone = libs.moment.tz.guess();
                const date = libs.moment(newValue).tz(timeZone).format('YYYY-MM-DD')
                _.set(this.specificDatesLocal, 'periodOfDays.to', date);
              }
            }
          },
          
          watch: {
            specificDatesLocal: {
              handler (newValue) {
                this.$emit('update:specificDates', newValue);
              },
              deep: true
            },
            
            periodOfTime: {
              handler (newValue) {
                _.set(this.specificDatesLocal, 'periodOfTime', newValue);
              },
              deep: true
            }
          },
      
          methods: {
            addPeriodOfTime () {
              this.periodOfTime.push({
                from: '',
                to: ''
              })
            },
            
            deletePeriodOfTime (index) {
              this.periodOfTime.splice(index, 1);
            },
            
            formatDate (date) {
              const timeZone = libs.moment.tz.guess();
              return libs.moment(date).tz(timeZone).format('YYYY-MM-DD');
            }
          },
    
          template: `<%= specificDatesItem %>`
        }
      },

      props: {
        weeklyList : {
          type: Array,
          default() {
            return [];
          }
        },
        specificDatesList : {
          type: Array,
          default() {
            return [];
          }
        },
        routingLegLabel: {
          type: String,
          default: ''
        },
        readonly: {
          type: Boolean,
          default: false
        }
      },
      
      computed: {
        routingLegLabelLocal: {
          get () {
            return this.routingLegLabel;
          },
          
          set (newValue) {
            this.$emit('update:routingLegLabel', newValue);
          }
        },
        
        collapsibleItemLabel() {
          return this.routingLegLabelLocal || 'No name';
        },
      },
          
      data () {
        return {
          weeklyListLocal: this.weeklyList,
          specificDatesListLocal: this.specificDatesList,
          newWeeklyListItem () {
            return {
              weekDays: [],
              periodOfTime: [
                {
                  from: '',
                  to: ''
                }
              ],
              weeklyTimePeriodTypeOfDay: 'All Day (24 Hours)'
            };
          },
          newSpecificDatesListItem () {
            return {
              periodOfDays: {
                from: '',
                to: ''
              },
              periodOfTime: [
                {
                  from: '',
                  to: ''
                }
              ],
              timePeriodTypeOfDay: 'All Day (24 Hours)'
            };
          }
        };
      },
      
      watch: {
        weeklyListLocal: {
          handler (newValue) {
            this.$emit('update:weeklyList', newValue);
          },
          deep: true
        },
        
        specificDatesListLocal: {
          handler (newValue) {
            this.$emit('update:specificDatesList', newValue);
          },
          deep: true
        }
      },

      template: `<%= routingLegItem %>`
    }
  },
  
  computed: {
    displayTimeZone: {
      get () {
        const timeZone = libs.moment.tz.guess();
        return _.isEmpty(this.timeZone) 
          ? `\`${timeZone}\``
          : _.get(this.timeZone, 'value');
      }, 
      set (newValue) {
        this.$emit('update:timeZone', {value: newValue});
      }
    }
  },
  
  methods: {
    calculateExitLabel (item) {
      if(_.isEmpty(item.routingLegLabel)) return 'No name';
      
      const label = item.routingLegLabel;
      return `${label.length > 8 ? label.slice(0,8)+'...' : label}`;
    },
    
    updateExits (params) {
      if (_.isEmpty(this.stepId)) return false;
      if (_.isEmpty(params.oldItems) && _.isEmpty(params.newItems)) {
          // Items reordered
          const newDynamicExits = _.map(params.allItems, item => {
              const exit = _.find(this.exitsLocal, {id : item.vforkey});
              return {
                  id : item.vforkey,
                  label : this.calculateExitLabel(item),
                  stepId : exit ? exit.stepId : ''
              };
          });
          try {
           _.forEach(newDynamicExits, (item, index) => {
              const exitIndex = index;
              this.exitsLocal[exitIndex].id = item.id;
              this.exitsLocal[exitIndex].label = item.label;
              this.exitsLocal[exitIndex].stepId = item.stepId;
          }); 
          } catch(e) {
          }
      } else if (_.isEmpty(params.oldItems) && !_.isEmpty(params.newItems)) {
          // Item added
          const newItem = params.newItems[0];
          const lastDynamicExitIndex = _.findLastIndex(this.exitsLocal, {dynamic : true});
          const index = lastDynamicExitIndex === -1 ? 0 : lastDynamicExitIndex + 1;
          this.exitsLocal.splice(index, 0, {
              label    : this.calculateExitLabel(newItem),
              id       : newItem.vforkey,
              stepId   : '',
              dynamic  : true
          });
      } else if (!_.isEmpty(params.oldItems) && _.isEmpty(params.newItems)) {
          // Item removed
          const removedItem = params.oldItems[0];
          this.exitsLocal.splice(_.findIndex(this.exitsLocal, {id : removedItem.vforkey}), 1);
      } else if (!_.isEmpty(params.oldItems) && !_.isEmpty(params.newItems)) {
          // Item updated
          const oldItemValue = params.oldItems[0];
          const newItemValue = params.newItems[0];
          const changedItemIndex = _.findIndex(this.exitsLocal, {id : oldItemValue.vforkey});
          this.exitsLocal[changedItemIndex].id = newItemValue.vforkey;
          this.exitsLocal[changedItemIndex].label = this.calculateExitLabel(newItemValue);
      }
    }
  },
  
  mounted () {
    if (_.isEmpty(this.timeZone)) {
      const timeZone = libs.moment.tz.guess();
      this.$emit('update:timeZone', {value: `\`${timeZone}\``, label: timeZone});
    }
    
    if (_.isEmpty(this.routingLegsList)) {
      
      this.$refs['routingLegsList'].addItem();
      
      // This line 
      // this.$refs['routingLegsList'].value[0].routingLegLabel = '';
      // throw error: TypeError: Cannot set property 'routingLegLabel' of undefined
    }
  },
  
  watch: {
    routingLegsList: {
      handler (newValue) {
        this.$emit('input', newValue);
      },
      deep: true
    },
    
    exitsLocal: {
      handler (newValue) {
        this.$emit('update:exits', newValue);
      },
      deep: true
    }
  },

  data () {
    // TODO: get better option to get regions.
    // One of posible option is get contry and its regions
    // https://github.com/moment/moment-timezone/issues/227
    // return only canonical zones
    const timeZones = libs.moment.tz._zones;
    const regions = _.chain(timeZones)
      .keys()
      .map(key => {
        // due to mutation in moment we need check if it's object
        // mutation is caused when invoke moment.tz()
        const zone = timeZones[key];
        return _.isObject(zone) ? zone.name : zone.split('|')[0]
      })
      .filter(zone => zone.indexOf('/') >= 0)
      .sort()
      .map(value => ({ label: value, value: `\`${value}\`` }))
      .value();
    return {
      regions,
      routingLegsList: this.value,
      exitsLocal: this.exits,
      newRoutingLeg () {
        return {
          stepId: this.stepId,
          routingLegLabel: '',
          weeklyList: [ 
            {
              weekDays: [],
              periodOfTime: [
                {
                  from: '',
                  to: ''
                }
              ],
              weeklyTimePeriodTypeOfDay: "All Day (24 Hours)"
            }
          ],
          specificDatesList: [
            {
              periodOfDays: {
                from: "",
                to: ""
              },
              periodOfTime: [
                {
                  from: '',
                  to: ''
                }
              ],
              timePeriodTypeOfDay: "All Day (24 Hours)"
            }
          ]
        };
      }
    };
  },
  
  props: {
    value: {
      type: Array,
      default() {
        return [
          {
            stepId: '',
            routingLegLabel: 'New Exit',
            weeklyList: [ 
              {
                weekDays: [],
                periodOfTime: [
                  {
                    from: '',
                    to: ''
                  }
                ],
                weeklyTimePeriodTypeOfDay: "All Day (24 Hours)"
              }
            ],
            specificDatesList: [
              {
                periodOfDays: {
                  from: '',
                  to: ''
                },
                periodOfTime: [
                  {
                    from: '',
                    to: ''
                  }
                ],
                timePeriodTypeOfDay: "All Day (24 Hours)"
              }
            ]
          }
        ];
      }
    },
    timeZone: {
      type: Object,
      default () {
        return {}
      }
    },
    mergeFields: {
      type: Array,
      default () {
        return [];
      }
    },
    exits: {
      type: Array,
      default () {
        return []
      }
    },
    readonly: {
      type: Boolean,
      default: false
    },
    steps: '',
    stepId: ''
  }
}

