const data = step.data;

const newData = {
  "routingLegsList": [],
  "timeZone": {}
};

newData.routingLegsList = data.routingLegsList;

if (data.timeZone.value) {
  newData.timeZone.value = `\`${data.timeZone.value}\``;
}

if (data.timeZone.label) {
  newData.timeZone.label = data.timeZone.label;
}



Object.assign(data, newData);
delete data;

console.log('migration 0.0.1 -> 1.0.0', step);

stepTemplates.template:
function getDataout(varName, varType, steps){
  for(const [key, value] of Object.entries(steps)){
    if(_.isObject(value.dataOut)){
      if(_.isEqual(value.dataOut.name, varName.split('.')[0]) && _.isEqual(value.dataOut.type, varType)){
        return {...value.dataOut, name : varName};
      }
    }else{
      if(_.isEqual(value.dataOut, varName.split('.')[0])){
        return {name : value.dataOut, type : 'session'};
      }
    }
  }
  if(!_.find(Object.values(steps), step => step.dataOut && _.isEqual(step.dataOut.name, varName))){
    return {name : varName, type : varType};
  }
}

let vars = _.map(variables, variable => {
  if (!variable.variableName.label || variable.variableName.label === '' || variable.variableName.label === ``) throw new Error("Property name can't be empty.");

  return {...getDataout(variable.variableName.label, variable.variableName.type,  this.steps), valueType: variable.valueType, variableValue: variable.variableValue}
})

await Promise.each(vars, async item => {
  const name = item.name.split(/[.||[]+/)[0];
  const path = item.name.replace(name, "").replace(/^\./, '');

  if (item.valueType === 'string') {
    await this.mergeFields[name].set(String(item.variableValue), path);


  } else if (item.valueType === 'number') {
    if (_.isEmpty(item.variableValue) || _.isEmpty(item.variableValue.trim())) throw new Error("Value can't be empty.");

    if(!_.isFinite(_.toNumber(item.variableValue))){
      throw new Error(`Incorrect data type was used for ${item.name}`);
    }
    await this.mergeFields[name].set(Number(item.variableValue), path);
  } else if (item.valueType === 'boolean') {

    if(!_.isBoolean(item.variableValue)){
      throw new Error(`Incorrect data type was used for ${item.name}`);
    }
    await this.mergeFields[name].set(item.variableValue, path);
  } else if (item.valueType === 'null') {
    await this.mergeFields[name].set(null, path);
  } else {
    await this.mergeFields[name].set(item.variableValue, path);
  }
});
return this.exitStep('next');

data.toJson:
 function toJson(data, key) { 
 if (_.isArray(data)) { 
    return `[${_.map(data, toJson).join(',')}]`;
    }
    if (_.isObject(data)) {
        return `{${_.map(data, (value, key) => `${_.isEmpty(key) ? `""`: key}: ${toJson(value, key)}`).join(',')}}`;
    }
      if(_.isEqual(key,'variableValue')){
      return data;
    }
    return JSON.stringify(data);
}
 
return _.mapValues(data, toJson);

data.validators:
variables : {
  $each: {
    variableName: {
      custom (value, ctx) {
        return _.chain(mergeFields).reduce((acc, item) => acc.concat(item.variables), []).some(['variableName', value.label]).value();
      }
    }
  },
  custom (value, ctx) {
    let steps = _.filter(this.steps, step => !_.isEqual(step.type, "empty"));

    let dataOuts = _.map(steps, step => _.isObject(step.data.dataOut) ?  step.data.dataOut : {name:step.data.dataOut, type : 'session'});

    if(_.get(ctx, 'additionalMergeFields[0].variables') && _.get(ctx, 'additionalMergeFields[1].variables')){
      dataOuts = [...dataOuts, ...(_.get(ctx, 'additionalMergeFields[0].variables')), ...(_.get(ctx, 'additionalMergeFields[1].variables'))]
    }

    let res = true;

    for (let i = 0; i <= value.length - 1; i++) {
      if (_.isEmpty(_.trim(value[i].variableName.label))) {
        this.schema.isInvalidChangeDataStep = true;
        res = false;
      } else {
        for (let j = i + 1; j <= value.length - 1; j++) {
          if (value[i].variableName.label === value[j].variableName.label && value[i].variableName.type === value[j].variableName.type) {
            this.schema.isInvalidChangeDataStep = true;
             res = false;
          }
        }
      }

      let containsMergeField = _.isString(value[i].variableValue) && validators.validateExpression(value[i].variableValue);

      if(
        (value[i].valueType === 'number' && !value[i].variableValue.slice(1,-1).trim().match(/[\+\-]?\d+(\.\d+)?/) || 
        value[i].valueType === 'number' && !(value[i].variableValue.slice(1,-1) && !_.isEmpty(value[i].variableValue.slice(1,-1)) && (containsMergeField || _.isFinite(_.toNumber(value[i].variableValue.slice(1,-1))))) ||
        value[i].valueType === 'code' && !validators.jsExpression(value[i].variableValue)) && !/await|this/.test(value[i].variableValue)
      ){
        this.schema.isInvalidChangeDataStep = true;
        res = false;
      }
    }

    this.schema.isInvalidChangeDataStep = false;

    return res;
  }
}

data.componentLogic:
{
  components: {
    variableItem: {
      props: {
        variableName: {
          type: String,
          default: ''
        },
        variableValue: {
          default: ''
        },
        valueType: {
          type: String,
          default: 'string'
        },
        variables: {
          type: Array,
          default () {
            return [];
          }
        },
        isNew:{
          type: Boolean,
          default: false
        },
        steps: '',
        stepId: '',
        mergeFields: {
          type: Array,
          default () {
            return [];
          }
        },
        readonly: {
          type: Boolean,
          default: false
        }
      },

      computed: {
        invalidValue(){
          const invalid = (this.valueTypeLocal === 'number' && !this.isValidNumber) || 
            (this.valueTypeLocal === 'code' && !validators.jsExpression(this.variableValueLocal)) ||
            (this.valueTypeLocal === 'code' && this.variableValueLocal === '')

          if (invalid) {
            this.valueErrorText = this.valueTypeLocal === 'number' 
              ? 'Invalid number.' 
              : 'Please enter a valid JS expression.';
          }

          if (this.valueTypeLocal === 'number') {
            this.checkNumber()
          }

          return invalid
        },

        isValidNumber(){
          const checked =  this.checkNumber();

          let containsMergeField = validators.validateExpression(this.variableValue);

          return checked || _.isFunction(this.variableValue.slice) && 
            (this.variableValue.slice(1,-1) && !_.isEmpty(this.variableValue.slice(1,-1)) && (_.isFinite(_.toNumber(this.variableValue.slice(1,-1))) || containsMergeField) && 
            this.variableValue.slice(1,-1).trim().match(/[\+\-]?\d+(\.\d+)?/));
        },

        isNull () {
          return this.valueTypeLocal === 'null';
        },

        variableNameLocal: {
          get() {
            return this.variableName || ''
          },

          set(newValue) {
            this.$emit('update:variableName', newValue);
            if (this.$parent.$parent.mountedFlag) {
              this.validateVariableName(newValue || '');
            }
          }
        },

        variableValueLocal: {
          get() {
            return this.variableValue || '';
          },
          set(newValue) {
            this.$emit('update:variableValue', newValue);
            this.$nextTick(() => this.validateVariableValue(newValue));
          }
        },

        valueTypeLocal: {
          get() {
            return this.valueType;
          },

          set(newValue) {
            if (newValue === 'string' || newValue === 'number') {
              this.variableValueLocal = '``'
            } else if (newValue === 'boolean') {
              this.variableValueLocal =  _.isBoolean(this.variableValueLocal) ? this.variableValueLocal : true;
            } else if (newValue === 'null') {
              this.variableValueLocal = '`null`';
            } else if (newValue === 'code') {
              this.variableValueLocal = ''
            }

            this.$emit('update:valueType', newValue);

            this.$nextTick(() => this.validateVariableValue(this.variableValueLocal, newValue));
          }
        }
      },

      data () {
        return {
          variableTypeOptions: ['string', 'number', 'boolean', 'null', 'code'],
          isInvalidVariableName: false,
          isInvalidVariableValue: false,
          errorText: 'Property name is required.',
          valueErrorText: 'Value is required.',
          isTextInput: true,
          variableSelectOptions: []
        }
      },

      watch: {
        variables(v) {
          this.$root.$emit('validateVariable');
        }
      },

      methods: {
        checkNumber () {
          if (/await|this/.test(this.variableValue)) return true

          this.valueErrorText =  _.isFunction(this.variableValue.slice) && this.variableValue.slice(1,-1).trim() === '' ? 'Value is required.' : 'Invalid number'

          if (_.isFunction(this.variableValue.slice) && /^\s+$/.test(this.variableValue.slice(1,-1))) {
            this.valueErrorText = 'Value is required.'
          }

          return false
        },

        afterHandleTypeChange (type) {
          return new Promise(resolve => {
            setTimeout(() => {
              const showText = _.get(this, '$refs.variableValueInput.showText')
              if (type === 'code' || (type !== 'code' && !showText)) {
                this.toggleInputMode()
              }
              resolve()
            }, 100) 
          })
        },

        async handleTypeChange (type) {
          this.valueTypeLocal = type

          if (type === 'number') this.checkNumber()

          await this.afterHandleTypeChange()
        },

        validateVariableName (varName, isFromEmit = false) {
          this.isInvalidVariableName = false;

          if (_.isEmpty(_.trim(varName.label))) {
            this.errorText = 'Property name is required.';
            this.isInvalidVariableName = true;
          } else {
            let variables = _.clone(this.variables);
            let duplicates = _.filter(variables, elem => _.isEqual(elem.variableName.type, varName.type)).map(item => item.variableName.label)
                                    .filter((item, index, arr) => arr.lastIndexOf(item) == index && arr.indexOf(item)!= index);

            if (duplicates.indexOf(varName.label) > -1) {
              this.errorText = 'Choose another property name.';
              this.isInvalidVariableName = true;
            }
          }

          if (!_.isEmpty(_.trim(varName.label)) && ! _.chain(this.mergeFields).reduce((acc, item) => acc.concat(item.variables), []).some(['variableName', varName.label]).value()) {
              this.errorText = `Selected value isn't available.`;
              this.isInvalidVariableName = true;
            }

          if(!isFromEmit) this.$root.$emit('validateVariable', this._uid);
        },

        populateSelect() {
          const findType = (variables, label) => {
            const item = variables.find(item => item.stepLabel === label && item.type)

            return _.get(item, 'type', null)
          }

          this.variableSelectOptions = this.mergeFields.reduce((acc, fields) => {

            const variables = fields.variables

            if (variables.length > 0) {
              acc.push({
                groupLabel: _.get(fields, 'variables[0].type'),
                items: variables.reduce((acc2, variable) => {
                  if (variable && !/.+\.$/.test(variable.variableName)) {
                    acc2.push({
                      label: variable.variableName,
                      value: {
                        label: variable.variableName, 
                        type: variable.type || findType(variables, variable.stepLabel),
                      }
                    })
                  }
                  return acc2;
                }, [])
              })
            }
            return acc
          }, [])
        },

        validateVariableValue (value, valueType) {
          if (this.isInvalidVariableValue) this.isInvalidVariableValue = false;
          let type = valueType ? valueType : this.valueType;

          if (type === 'number' && !this.$refs.variableValueInput.mergeFieldInUse) {
            const isNumber =_.isFinite(_.toNumber(value.slice(1,-1)));
            if (!_.trim(value.slice(1,-1)).length) {
              this.valueErrorText = 'Value is required.';
              this.isInvalidVariableValue = true;
              return false;
            } else if (!isNumber) {
              this.valueErrorText = 'Invalid number';
              this.isInvalidVariableValue = true;
              return false;
            }
          }

          if (!validators.jsExpression(value)) {
            this.valueErrorText = 'Please enter a valid JavaScript expression.'
            this.isInvalidVariableValue = true;
            return false;
          }

          return true;
        },
        toggleInputMode () {
          if(_.get(this, '$refs.variableValueInput.toggleMode')) this.$refs.variableValueInput.toggleMode()
        }
      },

      created () {
        this.$emit("update:additionalMergeFields", this.mergeFields);
      },

      mounted () {
        this.populateSelect();

        if(!this.isNew && _.isEmpty(this.variableName)){
          this.valueErrorText = 'Value is required.';
          this.isInvalidVariableName = true;
        }


        if (this.valueType === 'code') {
          this.toggleInputMode()
        }

        if (this.valueType === 'string' && this.variableValueLocal === '') {
          this.variableValueLocal = '``'
        }

        if (this.valueType === 'number') {
          this.checkNumber()
        }

        this.$root.$on('validateVariable', itemId => {
          if (itemId !== this._uid) this.validateVariableName(this.variableName, true);
        });

        if (this.$refs.variableValueInput) this.isTextInput = this.$refs.variableValueInput.displayTextInput;

        if (this.$parent.$parent.mountedFlag) {
          this.validateVariableName(this.variableName || '');
        } else {
          this.$parent.$parent.$emit('update:mountedFlag', true);
        }
      },

      template: `<%= variableItem %>`
    }
  },

  watch: {
    variables: {
      handler (newValue) {
        const currentIndexStep = _.findIndex(this.steps, item => item.id === this.stepId);

        if(currentIndexStep !== -1) {
          const outputExample = {};
          _.forEach(newValue, item => {
            _.set(outputExample, `${item.variableName.name}`, item.variableValue);
          });
          this.steps[currentIndexStep]['outputExample'] = outputExample;
        }
        this.$emit('input', newValue);

        this.isSharedOrGlobal()
      },
      deep: true
    }
  },

  created () {
    this.populateSelect()

    this.isSharedOrGlobal()
  },

  async mounted () {
    await this.$nextTick();
    if (!_.isArray(this.value) || this.value.length === 0) this.$refs.variablesOrList.addItem();
  },

  updated () {
	  this.isSharedOrGlobal()
	},

  data () {
    return {
      variables: this.value,
      newVariable () {
        return {
          variableName: '',
          variableValue: '',
          valueType: 'string'
        };
      },
      isSharedOrGlobalMergeFieldSelected: false
    }  
  },

  methods: {
    isSharedOrGlobal () {
      const arrOfVariableSelectOptions = this.variableSelectOptions.reduce((acc, item) => [...acc, ...item.items], [])

      this.isSharedOrGlobalMergeFieldSelected = this.variables.some(item =>
  	   // _.get(item, 'variableName.type') === 'shared' || 
  	   // _.get(item, 'variableName.type') === 'global' ||
  	    _.get(_.find(arrOfVariableSelectOptions, ['label', _.get(item, 'variableName.label')]), 'value.type') === 'shared' ||
  	    _.get(_.find(arrOfVariableSelectOptions, ['label', _.get(item, 'variableName.label')]), 'value.type') === 'global' 
  	  )
    },

    populateSelect() {
      const findType = (variables, label) => {
      const item = variables.find(item => item.stepLabel === label && item.type)
        return _.get(item, 'type', null)
      }

      this.variableSelectOptions = this.mergeFields.reduce((acc, fields, i) => {
        const variables = fields.variables;

        if (variables.length > 0) {
          acc.push({
            groupLabel: _.get(fields, 'variables[0].type'),
            items: variables.reduce((acc2, variable) => {
              if (variable && !/.+\.$/.test(variable.variableName)) {
                acc2.push({
                  label: variable.variableName,
                  value: {
                    label: variable.variableName,
                    type: variable.type || findType(variables, variable.stepLabel),
                  }
                })
              }
              return acc2;
            }, [])
          });
        }

        return acc;
      }, []);

      // this.checkSettedMergeFieldExistance()
    },

    // checkSettedMergeFieldExistance () {
    //   const items = _.get(this, 'variables')
    //   let varsOptions = _.get(this, 'variableSelectOptions')
    //   varsOptions = varsOptions.reduce((acc, item) => [...acc, ...item.items], [])

    //   if (items && varsOptions) {
    //     const _variables = items.reduce((acc, item) => {
    //       if (
    //           varsOptions.find(field => {
    //             return (
    //               _.get(field, 'label') ===
    //               _.get(item, 'variableName.label', '')
    //             )
    //           })
    //       ) {
    //           acc.push(item);
    //       } else {
    //         const _item = _.cloneDeep(item)
    //         // _item.variableName = ''
    //         acc.push(_item);
    //       }
    //       return acc;
    //   }, []);

    //   this.variables = _variables;
    //   }
    // }
  },

  props: {
    value: {
      type: Array
    },
    additionalMergeFields:{
      type: Array,
      default () {
        return [];
      }
    },
    schema: ()=>{},
    mountedFlag: {
      type: Boolean,
      default: false
    },
    v: {
      type: Object,
      default() {
        return {
          schema: {
            variables: [],
            additionalMergeFields:[]
          }
        }
      }
    },
    isInvalidChangeDataStep: {
      type: Boolean,
      default: false
    },
    changeDataStepErrors: {
      type: Array,
      default () {
        return [];
      }
    },
    steps: '',
    stepId: '',
    mergeFields: {
      type: Array,
      default () {
        return [];
      }
    },
    isNew:{
      type: Boolean,
      default: false
    },
    readonly: {
      type: Boolean,
      default: false
    }
  },

  computed: {
    changeDataStepErrorsLocal: {
      get() {
        return this.changeDataStepErrors;
      },
      set(newValue) {
        this.$emit('update:changeDataStepErrors', newValue);
      }
    }
  }
}

