stepTemplates.template:
const HOURS = 24;
const MINUTES = 60;
const SECONDS = 60;
const MILISECONDS = 1000;
const CHAT_MAX_LENGTH = 100;
const RANDOM_BYTES_KEY = 32;
const RANDOM_BYTES_IV = 16;
const CHAT_PREFIX = '__rwc';
const START_SESSION = 'startSession'
const MESSAGE_RECEIVED = 'MESSAGE_RECEIVED'
const ALL_MESSAGES_RECEIVED = 'ALL_MESSAGES_RECEIVED'
const UPDATE_CALLBACK = 'UPDATE_CALLBACK'
const SEND_DELAY = 100
const defaultTimeout = HOURS * MINUTES * SECONDS * MILISECONDS; // 24 hours
// eslint-disable-next-line
const uuid = require('uuid');
let chat = (chatId || '').toLowerCase().replace(/\?.*/gi, '');
// eslint-disable-next-line
const crypto = require('crypto');
const axios = require('axios');
const Promise = require('bluebird');

if (isAppsProviderAvailable && !_.trim(appName, '`')) {
  throw new Error(`App name is required`);
}

// eslint-disable-next-line
if (!chat.length || /[\\, \:, \^, \~, \?, \", \<, \>, \|, \., \/, \&, \%, \', \#, \[, \], \+, \=, \, \, \{, \\, \s]/.test(chat)) {
  throw new Error(`Chat name is required and should not contain symbols: \:^~?<>|&%.'#[]+={} and the space characters`);
}

if (chat.length > CHAT_MAX_LENGTH){
  throw new Error(`Chat name must be less than ${CHAT_MAX_LENGTH} symbols`);
}

// http get start chat
this.triggers.on({
  name: `http/get/${CHAT_PREFIX}/${chat}`,
  params: {
    method: 'get',
    path: chat
  }
}, async event => {
  const headers = event.params.request.headers
  await this.eventManager.callbackResolve(
    this.takeCallback(), {
      result: {
        code: 200,
        body: {
          headers,
          chatConfig: {
            typingIndicatorTime: lookAndFeelBotAnswerDelay.input,
            appId
          },
          chatStyle: {
            theme: lookAndFeelTheme,
            title: _.trim(lookAndFeelTitle, '`'),
            description: _.trim(lookAndFeelDescription, '`'),
            allowRestoreSession: lookAndFeelAllowRestoreSession,
            allowToViewHistory: lookAndFeelAllowToViewHistory,
            allowStartNewSession: lookAndFeelAllowStartNewSession,
            headerImage: lookAndFeelHeaderImage,
            botIcon: lookAndFeelBotIcon,
            useCustomCss: lookAndFeelUseCustomCss,
            customStyles: lookAndFeelCustomStyles,
            typingIndicatorTime: lookAndFeelBotAnswerDelay.input,
            isCustomMessageTimeFormat: lookAndFeelIsCustomMessageTimeFormat,
            messageTimeFormat: lookAndFeelCustomTimeFormat,
            useGlobalCommands,
            globalCommandsCustom: _.map(globalCommandsCustom, el => {
              const element = el
              element.code = element.code ? element.code.toString() : undefined
              return element
            }),
            closeEventType,
            closeEventCode: closeEventCode ? closeEventCode.toString() : null,
            closeEventCommand,
            globalCommandsList,
            customizeHeader,
            headerHtml,
            headerCss,
            headerJs: headerJs ? headerJs.toString() : null,
            useFooter,
            footerHtml,
            footerCss,
            footerJs: footerJs ? footerJs.toString() : null,
            showFooterAt,
            customizeBrowserTab,
            defaultTabTitle,
            faviconSourceImage,
            changeTabTitle,
            tabTitle,
            tabTitleInterval,
            extendHead,
            extendHeadHtml,
            addMfHeadVars,
            additionalContent: {
              type: additionalContentType,
              additionalContentCardPosition,
              additionalHeroVideoPosition
