on: /^before\/[^\/]+\/end/;
async () => {
  // conversation is under user manual control, don't send event automatically
  if (manualConversation) return;

  if (this.id !== 'main' && !this.id.includes('chatHookEvent')) return;

  const dataOut = await this.get(thisStep.dataOut);

  if (!dataOut) return
  if (dataOut.rwc_conversation.isFlowEndedSent) return

  const callback = dataOut.rwc_conversation.callback;

  if (dataOut.rwc_conversation.preventSendSessionEnd) return;

  // if session ended not in main thread need to remove subscription to UPDATE_CALLBACK
  this.triggers.off({
    name: `ws/chat/UPDATE_CALLBACK/${dataOut.rwc_conversation.sessionId}`
  })

  if (callback ) {
    delete callback.used;
    dataOut.rwc_conversation.isFlowEndedSent = true
    await this.set(thisStep.dataOut, dataOut)
    await this.eventManager.callbackResolve(callback, {
      type: 'flow_ended'
    })
  } else {
    // this.log.error('Before session end hook error: can`t find conversation')
  }
}

this.triggers.on({
  name: `ws/chat/close-session/${chat}`,
}, async ({params}) => {
  const {sessionId} = params

  await this.session.start({
    key: sessionId
  });
  this.end()
})

this.triggers.on({
    name: `ws/chat/${START_SESSION}/${chat}`
  }, async event => {
  const { beginningSessionId, previousSessionId, deviceId, browserFingerprint, queryParams, headers, rwcUrl, pageData, userHeaders, userTimezone, context, customVariables } = event.params;
  await this.session.start({
    timeout: defaultTimeout,
    reporting: {
      settingsKey: 'session',
      startedBy: 'Visitor',
      sessionType: 'RWCv2',
      ...beginningSessionId && { beginningSessionId },
      ...previousSessionId && { previousSessionId }
    }
  });

  const sessionHash = uuid.v4();

  const sessionId = slugid.nice()
  const realSessionId = this.session.key

  await this.eventManager.callbackResolve(this.takeCallback(), {sessionId, realSessionId});

  const callback = event.params;

  const outputData = {
    rwc_conversation: {
      callback,
      sessionId,
      chatName: chat,
      userId: browserFingerprint, // TODO replace with browserFingerprint
      realSessionId,
      deviceId,
      appId,
      accountId: this.config.accountId,
    },
    sessionId: realSessionId,
    accountId: this.config.accountId,
    rwcUrl,
    answerDelay: lookAndFeelBotAnswerDelay.input,
    browserFingerprint: browserFingerprint,
    chatConfig: stepVariable,
