      allowRestoreSession: lookAndFeelAllowRestoreSession,
      allowStartNewSession: lookAndFeelAllowStartNewSession,
      headerImage: lookAndFeelHeaderImage,
      useCustomCss: lookAndFeelUseCustomCss,
