    },
    queryParams,
    headers,
    userHeaders,
    deviceId,
    appId,
    pageData,
    userTimezone,
    context,
    customVariables
  };

  rwcContext.set('last', { 
    value: { name: thisStep.dataOut.name },
    name: thisStep.dataOut.name,
    type: thisStep.dataOut.type,
    flowId: this.config.flowId,
    botId: this.config.botId,
  });

  await rwcContext.save();

  return this.exitStep('next', outputData);
});



return this.triggers.otherwise(() => {
  return this.exitFlow()
});

