data.toJson:
//function that describe how to serialize wildcard data 
function generateProperty(property, key){
  return property ? `${property}.${key}` : key
}

function toJson(data, property = '') {
    if (_.isArray(data)) {
        return `[${_.map(data, d => toJson(d, generateProperty(property, '[]'))).join(',')}]`;
    }
    if (_.isObject(data)) {
        return `{${_.map(data, (value, key) => `${key}: ${toJson(value, generateProperty(property, key))}`).join(',')}}`;
    }
    if (/\$\{.+?\}/.test(data)) return data
    if(['chatId'].includes(property)) return data
    return JSON.stringify(data);
}

return _.mapValues(data, toJson);

data.validators:
{
  chatId: {
    custom(value){
       if (!value) return true
        const localValue = 
            value[0] === '`' && value[value.length - 1] === '`' 
            ? value.slice(1, -1)
            : value;

        const valueWithoutMergeFields = localValue.replace(/\${(?:[^{}]+|{(?:[^{}]+|{(?:[^{}]+|{(?:[^{}]+|{[^}]*})*})*})*})+}/gm, '');
        return localValue.length > 0 && (!(/[\\\:\^\~\/\$\(\)\"\<\>\|\%\.\,\#\[\]\+\\\\`\'\s\{\}]/i.test(valueWithoutMergeFields)) || _.isEmpty(valueWithoutMergeFields));
    },
    required (value) {
      return _.trim(value, '` "\'')
    },
    hasUppercaseLetters(value) {
      const uppercaseLetters = _.filter(_.split(value, ''), letter => { 
        return letter === _.toUpper(letter) && _.toLower(letter) !== _.toUpper(letter)
      });

      return !Boolean(_.size(uppercaseLetters));
    },
    moreThanLimit (value) {
      if (!value) return true
       const localValue = 
            value[0] === '`' && value[value.length - 1] === '`' 
            ? value.slice(1, -1)
            : value;
      return localValue.length <= 100
    }
  }
}

