data.componentLogic:
{
  props : {
    value : {
      type : String,
      default : ''
    },
    chatId: String,
    flow: Object,
    isNew : Boolean,
    isFlowActive: Boolean,
    isAppUpdated: Boolean,
    isAppCreated: Boolean,
    isAppsProviderAvailable: Boolean,
    readonly: {
      type : Boolean 
    },
    mergeFields: {
      type: Array
    },
    steps: {
      type: Array,
      default: () => {
          return []
      }
    },
    stepId: {
      type : String,
      default : ''
    }
  },
  computed: {
    getAppUrl () {
      return `https://${this.toUnicode((this.value || '').replace('https://', ''))}`
    }
  },
  methods: {
    open(){
      const win = window.open(this.value, '_blank');
      win.focus();
    },
    copy(){
      document.querySelector('#copy-url-app input').select()
      document.execCommand("copy");
    },
    decodePunycodeString (input) {
        const base = 36;
        const tMin = 1;
        const tMax = 26;
        const skew = 38;
        const damp = 700;
        const initialN = 128;
        const initialBias = 72;
        const delimiter = '-';
      	const output = [];
      	const baseMinusTMin = base - tMin;
        const floor = Math.floor;
        const stringFromCharCode = String.fromCharCode;
      	const inputLength = input.length;
      	const maxInt = 2147483647;
      	let i = 0;
      	let n = initialN;
      	let bias = initialBias;
      	let basic = input.lastIndexOf(delimiter);
      	const basicToDigit = function(codePoint) {
        	if (codePoint - 0x30 < 0x0A) {
        		return codePoint - 0x16;
        	}
        	if (codePoint - 0x41 < 0x1A) {
        		return codePoint - 0x41;
        	}
        	if (codePoint - 0x61 < 0x1A) {
        		return codePoint - 0x61;
        	}
        	return base;
        };
        const adapt = function(delta, numPoints, firstTime) {
        	let k = 0;
        	delta = firstTime ? floor(delta / damp) : delta >> 1;
        	delta += floor(delta / numPoints);
        	for (/* no initialization */; delta > baseMinusTMin * tMax >> 1; k += base) {
        		delta = floor(delta / baseMinusTMin);
        	}
        	return floor(k + (baseMinusTMin + 1) * delta / (delta + skew));
        };
      	if (basic < 0) {
      		basic = 0;
      	}
      	for (let j = 0; j < basic; ++j) {
      		if (input.charCodeAt(j) >= 0x80) {
      			error('not-basic');
      		}
      		output.push(input.charCodeAt(j));
      	}
      	for (let index = basic > 0 ? basic + 1 : 0; index < inputLength; /* no final expression */) {
      		let oldi = i;
      		for (let w = 1, k = base; /* no condition */; k += base) {
      			if (index >= inputLength) {
      				error('invalid-input');
      			}
      			const digit = basicToDigit(input.charCodeAt(index++));
      			if (digit >= base || digit > floor((maxInt - i) / w)) {
      				error('overflow');
      			}
      			i += digit * w;
      			const t = k <= bias ? tMin : (k >= bias + tMax ? tMax : k - bias);
      			if (digit < t) {
      				break;
      			}
      			const baseMinusT = base - t;
      			if (w > floor(maxInt / baseMinusT)) {
      				error('overflow');
      			}
      			w *= baseMinusT;
      		}
      		const out = output.length + 1;
      		bias = adapt(i - oldi, out, oldi == 0);
      		if (floor(i / out) > maxInt - n) {
      			error('overflow');
      		}
      		n += floor(i / out);
      		i %= out;
      		output.splice(i++, 0, n);
      	}
      	return String.fromCodePoint(...output);
      },
      toUnicode (input) {
        const regexPunycode = /^xn--/;
      	return this.mapDomain(input, (string) => {
      		return regexPunycode.test(string)
      			? this.decodePunycodeString(string.slice(4).toLowerCase())
      			: string;
      	});
      },
      mapDomain(string, fn) {
      	const parts = string.split('@');
      	const regexNonASCII = /[^\0-\x7E]/; // non-ASCII chars
        const regexSeparators = /[\x2E\u3002\uFF0E\uFF61]/g; // RFC 3490 separators
        function map(array, fn) {
        	const result = [];
        	let length = array.length;
        	while (length--) {
        		result[length] = fn(array[length]);
        	}
        	return result;
        }
      	let result = '';
      	if (parts.length > 1) {
      		result = parts[0] + '@';
      		string = parts[1];
      	}
      	string = string.replace(regexSeparators, '\x2E');
      	const labels = string.split('.');
      	const encoded = map(labels, fn).join('.');
      	return result + encoded;
      }
  }
}

