data.toJson:
//function that describe how to serialize wildcard data 
function generateProperty(property, key){
  return property ? `${property}.${key}` : key
}

function convertMedias(medias) {
  let filteredMedias = _.filter(medias, media => (_.has(media, 'linkUrl') && media.linkUrl.length && media.linkUrl !== "``") || _.has(media, 'location'));
  let finalMedias = _.map(filteredMedias, media => {
    return media.linkUrl ? `{ location: ${media.linkUrl}, size: ${_.get(media, 'data.Size')} }` : `{ location: ${JSON.stringify(media.location)}, size: ${media.size} }`
  })

  return finalMedias.length ? `[${finalMedias}]` : `[]`;
}

function toJson(data, property = '', path) {
    if ((/medias/g).test(property)) return convertMedias(data)
    if (_.isArray(data)) {
        return `[${_.map(data, d => toJson(d, generateProperty(property, '[]'))).join(',')}]`;
    }
    if (_.isObject(data)) {
        return `{${_.map(data, (value, key) => `${key}: ${toJson(value, generateProperty(property, key))}`).join(',')}}`;
    }
    if (/\$\{.+?\}/.test(data)) return data;
    return JSON.stringify(data);
}

return _.mapValues(data, toJson);

data.componentLogic:
{
  props : {
    value : {
      type : String,
      default : ''
    },
    lookAndFeelTheme: {
      type: String,
      default: 'light'
    },
    hideChatLoader: {
      type: Boolean,
      default: false
    },
    chatId: String,
    flow: Object,
    isNew : Boolean,
    isFlowActive: Boolean,
    isAppUpdated: Boolean,
    isAppCreated: Boolean,
    savedChatUrl: String,
    isAppsProviderAvailable: Boolean,
    readonly: {
      type : Boolean 
    },
    mergeFields: {
      type: Array
    },
    steps: {
      type: Array,
      default: () => {
          return []
      }
    },
    stepId: {
      type : String,
      default : ''
    }
  },
  watch: {
    chatId: {
      handler (nv, ov) {
        this.updateRWCURL(nv);
      },
      immediate: true
    },
    lookAndFeelTheme (nv, ov) {
      if (nv === ov) return;
      this.updateRWCURL(this.chatId);
    },
    hideChatLoader(nv, ov) {
      if (nv === ov) return;
      this.updateRWCURL(this.chatId);
    }
  },
  computed: {
    getLoaderTheme() {
      if(this.hideChatLoader) return `?loader=hidden`;
      if(_.trim(this.lookAndFeelTheme, '`') === `Auto-detect`) return ``;
      return '?loader=' + _.toLower(_.trim(this.lookAndFeelTheme, '`'));
    }
  },
  methods: {

    updateRWCURL(nv) {
      const accountSlugId = this.encodeUuid(this.flow.accountId);
        let chatValue = (nv || '``').toLowerCase()
        chatValue = _.trim(chatValue, '`');
        if (this.savedChatUrl === `${this.flow.chatUiUrl}/${accountSlugId}/${chatValue}${this.getLoaderTheme}`) return
        this.$emit('input', `${this.flow.chatUiUrl}/${accountSlugId}/${chatValue}${this.getLoaderTheme}`);
    },

    encodeUuid (uuid_) {
      const bytes   = this.parseUuid(uuid_);
      const base64 = this._arrayBufferToBase64(bytes)
      const slug = base64.replace(/\+/g, '-').replace(/\//g, '_').substring(0, 22);
      return slug;
    },
    _arrayBufferToBase64( buffer ) {
      let binary = '';
      const bytes = new Uint8Array( buffer );
      const len = bytes.byteLength;
      for (let i = 0; i < len; i++) {
          binary += String.fromCharCode( bytes[ i ] );
      }
      return window.btoa( binary );
    },
    parseUuid(s, buf, offset) {
      var _byteToHex = [];
      var _hexToByte = {};
      for (var i = 0; i < 256; i++) {
        _byteToHex[i] = (i + 0x100).toString(16).substr(1);
        _hexToByte[_byteToHex[i]] = i;
      }
      var i = (buf && offset) || 0;
      var ii = 0;

      buf = buf || [];
      s.toLowerCase().replace(/[0-9a-f]{2}/g, function(oct) {
        if (ii < 16) { // Don't overflow!
          buf[i + ii++] = _hexToByte[oct];
        }
      });

      // Zero out remaining bytes if string was short
      while (ii < 16) {
        buf[i + ii++] = 0;
      }

      return buf;
    },
    open(){
      const win = window.open(this.value, '_blank');
      win.focus();
    },
    copy(){
      document.querySelector('#copy-url input').select()
      document.execCommand("copy");
    }
  }
}

