{
  data () {
    return {
      codeData: null
    }
  },
    computed : {
        getQrLink () {
          if (!this.appId) return ''
          const params = {
            id: this.appId,
            lightColor: 'f6f6f6ff',
            darkColor: '000000ff',
            download: true
          }
          const queryString = Object.entries(params).map(([key, val]) => `${key}=${val}`).join('&');
          const url = this.flow.gatewayUrl('2042/app/qr', this.flow.providersAccountId()) + '?' + queryString
          return url
        }
    },
    watch: {
      appId: {
        handler (nv) {
          if (nv) {
            this.fetchCode()
          }
        },
        immediate: true
      }
    },
    props : {
        isAppUpdated: Boolean,
        isAppCreated: Boolean,
        isFlowActive: Boolean,
        isAppsProviderAvailable: Boolean,
        appId: String,
        isNew : Boolean,
        readonly: {
            type : Boolean 
        },
        mergeFields: {
            type: Array
        },
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        flow: Object,
        stepId: {
            type : String,
            default : ''
        }
    },
    methods: {
      async fetchCode () {
        const params = {
          id: this.appId,
          lightColor: 'f6f6f6ff',
          darkColor: '000000ff'
        }
        const queryString = Object.entries(params).map(([key, val]) => `${key}=${val}`).join('&');
        const url = this.flow.gatewayUrl('2042/app/qr', this.flow.providersAccountId()) + '?' + queryString
        const response = await this.$http.get(url)
        if (response.ok) {
          this.codeData = response.data
        }
      }
    }
}

