          this.allowFileUpdate = false;
        }
      },
      medias: {
        deep: true,
        handler(nv) {
          if(!nv.name) {
            this.allowFileUpdate = false;
          }
        } 
      }
    },
    computed : {
        authToken () { return this.$settings.token },
        medias : {
            get () {
              return this.value;
            },
            set (value) {
              let res = _.isArray(value) ? value[0] : value;
              if (_.isEqual(this.value, res)) return;
              this.$emit('input', res);
            }
        },
        usedUrlInputs () {
          if (!_.get(this, 'value', []).length) return false;
          return this.value.every(el => el.linkUrl);
        },
        isEmpty () {
          const isFilesNotEmpty = _.get(this, 'value', []).length && this.value.some(el => el.key || el.linkUrl.slice(1, -1))
          return !Boolean(isFilesNotEmpty || (this.messageText || '``').slice(1, -1))
        }
    },
    props : {
        value : {
            type : String,
            default : ''
        },
        isNew : Boolean,
        inputComponent: String,
        messageText: String,
        readonly: {
            type : Boolean 
        },
