            type: Array
        },
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        v : {
          type: Object,
          default() {
            return {schema: {}};
          }
        },
        stepId: {
            type : String,
            default : ''
        }
    },
    methods: {
      // media methods begin
      getValue (key, el) {
        return _.get(el || this.value, key);
      },
      setLinkUrlValue (val) {
        this.medias.linkUrl = val
      },
      openModal (ref) {
        this.$refs[ref].open();
      },
      removeSelectedItem () {
        this.medias = {}
      },
      addMedia () {
        this.openModal('select-media');
      },
      addNewUrl () {
        return {
          linkUrl: "``",
          vforkey: uuid.v4()
        }
      },
      changeMediaToUrl () {
        const _medias = this.addNewUrl();
        this.medias = _medias
      }
    }
}

