{
    data () {
      return {
        previewWindow: null,
        allowedFileTypes: ['jpg', 'JPG', 'jpeg', 'JPEG', 'svg', 'SVG', 'png', 'PNG', 'heic', 'HEIC', 'tiff', 'TIFF', 'tif', 'TIF'],
        maxFiles: 1,
        colors: { r: 255, g: 0, b: 0 },
        allowFileUpdate: false
      };
    },
    components: {
      'color-picker': libs.vueColor.Swatches
    },
    computed : {
        authToken () { return this.$settings.token },
        embedLogo : {
            get () {
              return this.value;
            },
            set (value) {
              let res = value;
              if (_.isEqual(this.value, res)) return;

              res.forEach(el => {
                if (!el.vforkey) {
                  el.vforkey = uuid.v4();
                }
                return el;
              }) // add key to prevent trigger change by or-list

              this.$emit('input', res);
            }
        },
        usedUrlInputs () {
          if (!_.get(this, 'value', []).length) return false;
          return this.value.every(el => el.linkUrl);
        }
    },
    mounted () {
      this.colors = {hex: this.widgetColor || '#42c3f8'}
    },
    watch: {
      colors: {
        handler (nv) {
          if (this.widgetColor === _.get(nv, 'hex')) return
          this.$emit('update:widgetColor', _.get(nv, 'hex') || '#42c3f8')
        },
        deep: true
      }
    },
    props : {
        value : {
            type : String,
            default : ''
        },
        isNew : Boolean,
        widgetColor: String,
        readonly: {
            type : Boolean 
        },
        mergeFields: {
            type: Array
        },
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        v : {
          type: Object,
          default() {
            return {schema: {}};
          }
        },
        stepId: {
            type : String,
            default : ''
        }
    },
    methods: {
      // media methods begin
      getValue (key, el) {
        return _.get(el || this.value, key);
      },
      updateMedias (field, value) {
        if(!this.allowFileUpdate) return;

        const medias = _.cloneDeep(this.embedLogo);
        medias.forEach(el => {
          if (el.vforkey === field.vforkey) {
            el.linkUrl = field.linkUrl
            el.vforkey = field.vforkey
          }
          return el
        })
        this.embedLogo = medias;

        if(this.embedLogo[0].location) {
          this.allowFileUpdate = false;
        }
      },
      openModal (ref) {
        this.$refs[ref].open();
      },
      removeSelectedItem (key) {
        const _medias = this.value
        _medias.splice(key, 1);
        medias = _medias
        // this.setValue('medias', _medias);
      },
      addMedia () {
        if (_.has(_.get(this.value, '[0]'), 'linkUrl')) {
          const _medias = this.value;
          _medias.push(this.addNewUrl());
          this.embedLogo = _medias
          // this.setValue('medias', _medias);
        } else {
          this.openModal('select-media');
        }

      },
      addNewUrl () {
        return {
          linkUrl: "``",
          vforkey: uuid.v4()
        }
      },
      changeMediaToUrl () {
        const _medias = [this.addNewUrl()];
        this.embedLogo = _medias
        // this.setValue('medias', _medias);
      }
    }
}

