  let finalMedias = medias.linkUrl ? `{ location: ${medias.linkUrl}, size: ${_.get(medias, 'data.Size')} }` : `{ location: ${JSON.stringify(medias.location)}, size: ${medias.size} }`
  return finalMedias.length ? `[${finalMedias}]` : `[]`;
}

function toJson(data, property = '', path) {
  return convertMedias(data)
}

return _.mapValues(data, toJson);

