data.validators:
{
  globalCommandsCustom: {
    $each: {
      emptyLabel (obj) {
        return _.trim(obj.label, '`')
      },
      emptyName (obj) {
        if (obj.type === 'command') {
          return _.trim(obj.commandName, '`')
        } else {
          return true
        }
      },
      validName (obj) {
        if (obj.type === 'command') {
          const name = _.trim(obj.commandName, '`');
          return Boolean(name.match(/^[a-zA-Z0-9]*$/));
        } else {
          return true
        }
      },
      uniqName(obj, items, context) {
        if (obj.type !== 'command') return true;

        const closeEventCommandName = _.trim(this.schema.closeEventCommand, '`')

        const name = _.trim(obj.commandName, '`');

        if(closeEventCommandName === name) return false;

        return _.filter(items, item => {
          const itemCommandName = _.trim(item.commandName, '`');
          return itemCommandName === name && item.type === 'command';
        }).length < 2;
      },
      emptyIcon (obj) {
        return _.trim(obj.icon, '`')
      },
      emptyLink (obj) {
        if (obj.type === 'button' && obj.buttonType === 'link') {
          return _.trim(obj.link, '`')
        } else {
          return true
        }
      },
      invalidLink(obj) {
        if (obj.type === 'button' && obj.buttonType === 'link') {
          return _.startsWith(_.trim(obj.link, '`'), 'http')
        } else {
          return true
        }
      },
      emptyCode (obj) {
        if (obj.type === 'button' && obj.buttonType === 'code') {
          return _.trim(obj.code, '`')
        } else {
          return true
        }
      }
    }
  }
}

