data.toJson:
//function that describe how to serialize wildcard data 
function generateProperty(property, key){
  return property ? `${property}.${key}` : key
}

function convertMedias(medias) {
  let filteredMedias = _.filter(medias, media => (_.has(media, 'linkUrl') && media.linkUrl.length && media.linkUrl !== "``") || _.has(media, 'location'));
  let finalMedias = _.map(filteredMedias, media => {
    return media.linkUrl ? `{ location: ${media.linkUrl}, size: ${_.get(media, 'data.Size')} }` : `{ location: ${JSON.stringify(media.location)}, size: ${media.size} }`
  })

  return finalMedias.length ? `[${finalMedias}]` : `[]`;
}

function toJson(data, property = '', path) {
    if ((/lookAndFeelCustomStyles/g).test(property)) return convertMedias(data)
    if (_.isArray(data)) {
        return `[${_.map(data, d => toJson(d, generateProperty(property, '[]'))).join(',')}]`;
    }
    if (_.isObject(data)) {
        return `{${_.map(data, (value, key) => `${key}: ${toJson(value, generateProperty(property, key))}`).join(',')}}`;
    }
    if (/\$\{.+?\}/.test(data)) return data;
    return JSON.stringify(data);
}

return _.mapValues(data, toJson);

data.validators:
{
  lookAndFeelCustomStyles: {
    custom (value) {
      const urlRegex = /https?:\/\/(www\.)?[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{2,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)/g
      const isFilesNotEmpty = (value || []).length && (value || []).some(el => el.key || (el.linkUrl.slice(1, -1) && (el.linkUrl.match(urlRegex) || el.linkUrl.match(/\$\{/)) ))
      return Boolean(isFilesNotEmpty)
    },
    empty (obj) {
      return !_.isEmpty(obj)
    }
  }
}

data.componentLogic:
{
    data () {
      return {
        allowedFileTypes: ['css', 'CSS'],
        maxFiles: 10,
        urlRegex: /https?:\/\/(www\.)?[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{2,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)/g,
        mergeFieldRegex: /\$\{/gi,
        allowFileUpdate: false
      };
    },
    computed : {
        authToken () { return this.$settings.token },
        medias : {
            get () {
              return this.value;
            },
            set (value) {
              let res = value;
              if (_.isEqual(this.value, res)) return;

              res.forEach(el => {
                if (!el.vforkey) {
                  el.vforkey = uuid.v4();
                }
                return el;
              }) // add key to prevent trigger change by or-list

              this.$emit('input', res);
            }
        },
        usedUrlInputs () {
          if (!_.get(this, 'value', []).length) return false;
          return this.value.every(el => el.linkUrl);
        },
        isEmpty () {
          const isFilesNotEmpty = _.get(this, 'value', []).length && this.value.some(el => el.key || el.linkUrl.slice(1, -1))
          return !Boolean(isFilesNotEmpty)
        }
    },
    props : {
        value : {
            type : String,
            default : ''
        },
        isNew : Boolean,
        useCustomCss: Boolean,
        readonly: {
            type : Boolean 
        },
        mergeFields: {
            type: Array
        },
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        v : {
          type: Object,
          default() {
            return {schema: {}};
          }
        },
        stepId: {
            type : String,
            default : ''
        }
    },
    methods: {
      // media methods begin
      isLinkUrlInvalid (url) {
        const exactText = (url || '').slice(1, -1)
        if (/\$\{/.test(exactText)) return false
        return exactText && !exactText.match(this.urlRegex)
      },
      getErrorText (url) {
        const exactText = (url || '').slice(1, -1)
        if (!exactText) return 'The URL is required'
        if (!exactText.match(this.urlRegex)) return 'The URL has invalid format'
      },
      getValue (key, el) {
        return _.get(el || this.value, key);
      },
      updateMedias (field, value) {
        const medias = _.cloneDeep(this.medias);
        medias.forEach(el => {
          if (el.vforkey === field.vforkey) {
            el.linkUrl = field.linkUrl
            el.vforkey = field.vforkey
          }
          return el
        })
        this.medias = medias;
      },
      openModal (ref) {
        this.$refs[ref].open();
      },
      removeSelectedItem (key) {
        const _medias = this.value
        _medias.splice(key, 1);
        medias = _medias
        // this.setValue('medias', _medias);
      },
      addMedia () {
        if (_.has(_.get(this.value, '[0]'), 'linkUrl')) {
          const _medias = this.value;
          _medias.push(this.addNewUrl());
          this.medias = _medias
          // this.setValue('medias', _medias);
        } else {
          this.openModal('select-media');
        }

      },
      addNewUrl () {
        return {
          linkUrl: "``",
          vforkey: uuid.v4()
        }
      },
      changeMediaToUrl () {
        const _medias = [this.addNewUrl()];
        this.medias = _medias
        // this.setValue('medias', _medias);
      }
    }
}

