function resolve(thread) {
      switch (this.output.selectStyle) {
        case 'inherited':
          return _.get(this.store, this.output.value.from);
        case 'step':
          return _.get(this.store, `__steps.${this.output.value.targetStepId}`);
        case 'dataOut':
          return thread.mergeFields[this.output.value.dataOutName].get();
        default:
          return this.output.value;
      }
    }

