  step.data.widgetColor = '#409fec';}

hooks.before-save-flow:
/*
// $flow - flow helpers
// _ - lodash library
// axios - axios library
// env - environment variables
// sdk - onereach sdk
// uuid - uuid library
// 
// flowMeta - meta information of flow.data.meta
// stepTemplate - step template
// step - step on treeisAppsProviderAvailable
*/
let chatId = (_.trim(step.data.chatId, '`') || '').toLowerCase().replace(/\?.*/gi, '');
const appName = _.trim(step.data.appName, '`');
if (step.data.isAppsProviderAvailable && !appName){
  throw { errorType: 'CUSTOM', message: 'Saving failed: App name is required'};
  return
}
if (!chatId) {
  throw { errorType: 'CUSTOM', message: 'Saving failed: Chat ID is required'};
}
if (/[\\, \:, \^, \~, \?, \", \<, \>, \|, \., \/, \&, \%, \', \#, \[, \], \+, \=, \, \", \{, \\, \s]/.test(chatId)) {
  throw { errorType: 'CUSTOM', message: 'Saving failed: Chat ID should not contain symbols: \:^~?<>|&%.\'#[]+="{} and the space characters'};
}
if (chatId.length > 100) {
  throw { errorType: 'CUSTOM', message: 'Saving failed: Chat ID must be less than 100 symbols'};
}
if (
  // comment this conditions due to issue on first save without changes while 
  // flow source was inserted from another account
  // ((_.trim(step.data.appName, '`') !== _.trim(step.data.savedAppName, '`')) || 
  // (step.data.savedAppColor !== step.data.appColor) || 
  // (step.data.previousChatId !== step.data.chatId)) && 
  step.data.isAppsProviderAvailable
  ) {
  const headers = {
    headers: {
      Authorization: $flow.token,
      'Content-Type' : 'application/json;charset=UTF-8'
    }
  }
  const stripedName = _.trim(step.data.appName.replace(/(<([^>]+)>)/gi, ""));
  const data = {
    "color": step.data.appColor.replace('#', ''),
    "isActive": true,
    "name": _.trim(stripedName, '`'),
    "url": step.data.chatUrl
  }
  if (step.data.appId) {
    data.id = step.data.appId
  }
  // create an app
  const url = $flow.gatewayUrl('2042/app', $flow.providersAccountId())
  let method = step.data.isAppCreated ? 'patch' : 'post'

  if (step.data.savedFlowId !== $flow.id) {
    delete data.id
    step.data.isAppCreated = false
    method = 'post'
  }

  return axios[method](url, data, headers)
    .then(response => {
      const { shareUrl, shareShortUrl, name, color, id } = response.data
      step.data.savedAppName = name
      step.data.savedFlowId = $flow.id
      step.data.appUrl = shareShortUrl
      step.data.savedAppColor = `#${color}`
      step.data.appId = id
      step.data.isAppCreated = true
    })
    .catch(e => {
      delete data.id
      return axios['post'](url, data, headers)
        .then(response => {
          const { shareUrl, shareShortUrl, name, color, id } = response.data
          step.data.savedAppName = name
          step.data.appUrl = shareShortUrl
          step.data.savedAppColor = `#${color}`
          step.data.appId = id
          step.data.isAppCreated = true
        })
        .catch(e => {
          throw new Error(e)
        })
    })
}

