stepTemplates.template:
const PROVIDER_VERSION = '2020'
const PUSH_REPLY_TYPE_TEXT = 'text'
const PUSH_RESULT_ERROR = 'error'
const STEP_EXIT_NEXT = 'next'
const NOTIFICATION_TITLE = 'Onereach.ai Chat'
const NOTIFICATION_CTX_TYPE = 'request_response'
//---------------- report Transcript Event ----------------
const report = ({
  message, 
  medias, 
  transcriptId, 
  previousTranscriptId, 
  botIdentifier, 
  endUserIdentifier,
  fromType,
  fromIdentifierType,
  reportingSettingsKey,
  fromId,
  fromIdentifier,
  toType,
  toIdentifierType,
  toIdentifier
}) => {
  const reportingObject = {
    botIdentifier,
    message,
    previousTranscriptId,
    eventId              : transcriptId,
    channel              : 'RWC',
    reportingSettingsKey,
    media                : _.map(medias, fileObj => ({fileUrl: fileObj.location, fileType : 'File'})),
    fromId               : this.config.flowId,
    fromType,
    fromIdentifierType,
    toType,
    toIdentifier,
    toIdentifierType,
    fromIdentifier
  };

  this.reporter.reportTranscriptEvent(reportingObject);
};
//----------------------------------------------------------

const mapDataToClient = (inputComponent) => {
  const componentName = _.trim(inputComponent, '`')
  const isHasMessage = ['card', 'carousel'].indexOf(componentName) === -1
  const isHasMedias = ['textFiles'].indexOf(componentName) !== -1
  let showComponentInMessage = [
    'schedule',
    'checkbox', 
    'datepicker', 
    'dropdown', 
    'menu', 
    'radio', 
    'timepicker', 
    'attachments', 
    'geolocation',
    'slider',
    'signature',
    'video'
  ].indexOf(componentName) !== -1

  const componentMap = {
    schedule: {
      name: 'rwc-scheduler',
      buttonLabel: scheduleButtonLabel,
      options: scheduleOptionItems,
      allowMultiselect: scheduleAllowMultiselect,
      multiselectQuantity: scheduleMultiselectQuantity,
      workingHours: workingHours,
      disableByFunction: scheduleDisableByFunction,
      disableByMf: scheduleDisableByMf,
      disableByManually: scheduleDisableByManually,
      disableFunction: scheduleDisableFunction ? scheduleDisableFunction.toString() : null,
      disableMf: scheduleDisableMf,
      disableManual: scheduleDisableManual
    },
    record: {
      name: 'rwc-record',
      allowedMediaTypes: recordMultimediaOptions
    },
    carousel: {
      name: 'rwc-carousel',
      options: _.map(carouselOptions, card => {
        const hasModal = _.get(card, 'hasModal') || false;

        if(!hasModal)
          return _.omit(card, ['modalType', 'childWindowType', 'additionalContentLink', 'html', 'showModalButtonInUserResponse'])

        const modalType = _.get(card, 'modalType');
        const childWindowType = _.get(card, 'childWindowType');

        if(hasModal && modalType === 'child-window' && childWindowType === 'url')
          return _.omit(card, ['html'])

        return card;
      }),
      hasOtherOption: carouselHasAlternative,
      submitBtnLabel: carouselSubmitBtnLabel,
      cancelBtnLabel: carouselCancelBtnLabel,
      otherOptionData: carouselAlternative,
      multiple: carouselMultiselect
    },
    checkbox: {
      name: 'rwc-checkbox-group',
      options: checkboxOptions,
      defaultValue: checkboxDefault
    },
    credit: {
      name: 'rwc-credit',
      buttonLabel: creditBtnLabel,
      cancelButtonLabel: creditCancelBtnLabel,
      termsCheckboxLabel: creditTermsLabel,
      termsCheckboxLink: creditTerms
    },
    datepicker: {
      name: 'rwc-datepicker',
      outFormat: datepickerFormat,
      startDay: datepickerStartsFrom,
      isBlockedDates: datepickerBlockDates,
      isSuggestedDates: datepickerSuggestedDates,
      blockedOptions: datepickerBlockedOptions,
      suggestedOptions: datepickerSuggestedOptions,
      blockedFunction: datepickerBlockedFunc ? datepickerBlockedFunc.toString() : undefined,
      suggestedFunction: datepickerSuggestedFunc ? datepickerSuggestedFunc.toString() : undefined,
      blockedMergefield: datepickerBlockedMergefield,
      suggestedMergefield: datepickerSuggestedMergefield
    },
    dropdown: {
      name: 'rwc-dropdown',
      options: dropdownOptions,
      multiple: dropdownMultiple,
      dropdownPlaceholder: dropdownPlaceholder
    },
    video: {
      name: 'rwc-video',
      startTime: videoStartTime,
      startTimeTimestring: videoStartTimeTimestring,
      autoplay: videoAutoplay,
      button: videoBtnLabel,
      video: videoSrc,
      showInThread: videoShowInThread,
      poster: videoPoster
    },
    menu: {
      name: 'rwc-menu',
      options: optionListOptions
    },
    buttons: {
      name: 'rwc-buttons-list',
      options: optionsButtons
    },
    slider: {
      name: 'rwc-slider',
      min: sliderMin,
      max: sliderMax,
      step: sliderStep,
      unit: sliderUnit,
      unitPosition: sliderUnitPosition,
      mode: sliderType,
      useCustomLabels: sliderUseCustomLabels,
      labelFunction: sliderCustomFunction ? sliderCustomFunction.toString() : undefined,
      options: sliderCustomLabels,
      defaultValue: sliderDefaultValue,
      defaultValueStart: sliderDefaultStart,
      defaultValueEnd: sliderDefaultEnd
    },
    signature: {
      name: 'rwc-signature',
      btnLabel: signatureBtn
    },
    radio: {
      name: 'rwc-radio-group',
      options: radioOptions,
      defaultValue: radioDefault
    },
    rate: {
      name: 'rwc-rate',
      ratePlaceholder: ratePlaceholder,
      showInput: rateInput,
      ratingSubmitLabel,
      ratingDiscardLabel
    },
    textFiles: {
      name: 'or-textbox',
      placeholder: textPlaceholder,
      textInputType,
      textInputCustomPattern
    },
    timepicker: {
      name: 'rwc-timepicker',
      timeFormat: timepickerFormat
    },
    attachments: {
      name: 'rwc-attachment',
      isMulti: attachmentsNumber,
      filesValidation: attachmentsAllowChosen,
      isPrivate: isPrivateFiles,
      allowedFileTypes: attachmentsAllowedTypes
    },
    geolocation: {
      name: 'rwc-location',
      buttonLabel: geolocationBtn,
      defaultLat: geolocationLat,
      defaultLng: geolocationLng,
      useDefault: geolocationSetDefault
    },
    custom: {
      name: 'custom',
      vars: customVariables,
      templateType: customTemplateType,
      hideAfterSubmit: hideCustomComponentAfterSubmit,
      showBotIcon: showBotIcon,
      autoSubmit: autoSubmitCustomTemplate,
      css: customTemplateCompiledCSS,
      cssWrapperClassName: customTemplateCSSWrapperClassName,
      scripts: customTempalateScripts,
      ...(_.trim(customTemplateType, '`') === `vue` && { 
        vueTemplate: customTemplateVueHtmlTemplate,
        vueScript: customTemplateVueScript
      }),
      ...(_.trim(customTemplateType, '`') === `html` && {
        html: customTemplateHtmlCode,
        javascript: customTemplateJavaScriptCode,
      }),
    }
  }

  const answerParams = _.mapValues(componentMap[componentName], (el) => { 
    return el
  });

  const answerComponent = {
    component: componentMap[componentName].name,
    vBind: answerParams,
    showComponentInMessage
  }
  return { answerComponent, isHasMessage, isHasMedias }
}

const timestring = require('timestring');
const SEND_DELAY = 100;
const UPDATE_CALLBACK = 'UPDATE_CALLBACK';
const ALL_MESSAGES_RECEIVED = 'ALL_MESSAGES_RECEIVED';
const GET_MESSAGES_FROM_HISTORY = 'GET_MESSAGES_FROM_HISTORY';
const uuid = require('uuid');

const conversation = await rwcContext.resolve();

const conversationData = await this.mergeFieldsInstance.get({
  botId: conversation.botId,
  name: conversation.value.name,
  type: conversation.type,
  path: 'rwc_conversation'
});

if (!conversationData) throw new Error('The conversation is empty. Most possible that invalid conversation selected')

const {
  sessionId,
  callback,
  chatName,
  realSessionId,
  userId,
  appId,
  deviceId,
  accountId
} = conversationData;

if (accountId) {
  this.triggers.config({
    target: accountId
  })
}

const axios = require('axios');
const Promise = require('bluebird');

// get last transcript id from MF
const lastTranscriptId = await this.mergeFieldsInstance.get({
  botId: conversation.botId,
  name: conversation.value.name,
  type: conversation.type,
  path: 'rwc_conversation.transcriptId'
});

const transcriptRequestId = uuid.v4();
const transcriptResponseId = uuid.v4();

// patching conversation transcriptId
await this.mergeFieldsInstance.set(
  {
    botId: conversation.botId,
    name: conversation.value.name,
    type: conversation.type,
    path: 'rwc_conversation.transcriptId'
  }, 
  transcriptRequestId
);

const sendMessage = async (message) => {
  if (_.get(message, 'answer.notSendMessage')) return;
  message.time = Date.now();
  if (!message.id) {
    message.id = uuid.v4();
  }

  await new Promise(resolve => setTimeout(resolve, SEND_DELAY));
  await this.eventManager.callbackResolve(callback, message);
  delete callback.used;
  await new Promise(resolve => setTimeout(resolve, SEND_DELAY));
}

const getUploadUrl = async (pathes) => {
  const localPathes = pathes
  const headers = {
    headers: {
      Authorization: this.config.authorization,
      'Content-Type' : 'application/json;charset=UTF-8'
    }
  }

  const urls = []

  for (let i = 0; i < pathes.length; i += 1) {
    try {
      const fileAlreadyExist = await axios.get(`${this.helpers.filesApiUrl}/public/${this.config.accountId}/RWC/${chatName}/${realSessionId}/${localPathes[i].name}`)
      if (fileAlreadyExist) {
        localPathes[i].name = localPathes[i].name.replace(/\.([0-9a-z]+)(?:[\?#]|$)/i, function(match) {
          return `__${Date.now()}${match}`
        });
      }
    } catch (e) {}
  }

  const isPublic = _.isUndefined(isPrivateFiles) ? true : !isPrivateFiles;


  for (let i = 0; i < localPathes.length; i += 1) {
    urls.push(
      axios.post(`${this.helpers.filesApiUrl}/api/url?isPublic=${isPublic}&accountId=${this.config.accountId}`, {key: `RWC/${chatName}/${realSessionId}/${localPathes[i].name}`, contentType: pathes[i].type, maxFileSize: null}, headers)
    )
  }

  return Promise.all(urls);
}

const rwcThreadId = `rwc__${this.session.key}`

if (this.id === 'main') {
  // .local means subscription relates only to this session
  // and won't be overwritten by same subscription in the subflow
  this.triggers.local({
    name: `chat/return_to_thread/${sessionId}`,
  }, async ({params}) => {
    const { answerComponent, isHasMessage, isHasMedias } = mapDataToClient(inputComponent)

  if (_.trim(inputComponent, '`') === `custom`) {
    if(_.trim(customTemplateType, '`') === `vue`) {
      const result = await answerComponent.vBind.vueScript();
      answerComponent.vBind.vueScript = JSON.stringify(result, function(key, value) {
        if (typeof value === 'function') {
          return _.toString(value)
        } else {
          return value;
        }
      });

      const showComponentInMessageLocal = _.get(result, 'showComponentInMessage');

      answerComponent.showComponentInMessage = _.isUndefined(showComponentInMessageLocal) ? showCustomComponentInMessage : showComponentInMessageLocal 
    } else {
      answerComponent.showComponentInMessage = showCustomComponentInMessage;
    }
  }

  const event = {
    stepId: savedStepId,
    id: uuid.v4(),
    message: isHasMessage ? textMessage : '',
    medias: isHasMedias ? medias : [],
    useCustomAnswer,
    userAnswerFunction: userAnswerFunction ? userAnswerFunction.toString() : null,
    userAnswerFunctionParams: dataOutput,
    answerComponent,
    additionalContent: {
      additionalContentCardChangeType,
      additionalHtml,
      additionalCss,
      additionalScript: additionalScript ? additionalScript.toString() : '',
      additionalCardvariables,
      videoSrc: additionalContentVideo
    },
    resended: true
  }

  if (autoSuggestion.input) {
    event.suggestions = _.isEmpty(autoSuggestion.options) ? autoSuggestionOptionsList : autoSuggestion.options
    event.isSuggestionInput = autoSuggestion.input
    event.placeholder = textPlaceholder
    event.suggestionsType = autoSuggestionType
    event.suggestionsFromMergeField = autoSuggestionOptionsMergeField
  }

  // if (answerComponent.component !== 'or-textbox') {
  //   answerComponent.vBind.allowMask = false
  // }

  if (!_.isEmpty(event)) {
    await sendMessage(event)
  }
})
}

if (this.id === rwcThreadId) {

  this.triggers.on({
    name: `ws/chat/${UPDATE_CALLBACK}/${sessionId}`,
    refresh: true
  }, async ({params}) => {

    if (params.callback) {
      if (this.config.botId === conversation.botId) {
        await this.mergeFields[conversation.value.name].set(params.callback, 'rwc_conversation.callback')
      } else {
        await this.mergeFieldsInstance.set({botId: conversation.botId, name: conversation.value.name, type: conversation.type, path: 'rwc_conversation.callback'}, params.callback);
      }
      // await this.mergeFields[conversation.value.name].set(params.callback, 'rwc_conversation.callback');
      return
    }

    const resultQueue = await this.emitSync({
      name : 'rwc/queue_provider',
      target: this.helpers.providersAccountId,
      params : {
        sessionId: params.sessionId,
        type: 'sync',
        method: 'get'
      }
    })

    const resultHistory = await this.emitSync({
      name : 'rwc/history_provider',
      target: this.helpers.providersAccountId,
      params : {
        sessionId: params.sessionId,
        type: 'sync',
        method: 'get',
        lastMessageId: params.lastMessageId
      }
    })

    await this.eventManager.callbackResolve(
      this.takeCallback(), 
      {
        status: 'OK', 
        messagesFromQueue: resultQueue,
        messagesFromHistory: resultHistory
      })
  })

  return this.triggers.otherwise(() => this.exitFlow())
}

this.process.newThread(rwcThreadId, thread => {
  thread.currentStep = this.currentStep
  thread.local.background = true
  thread.activate()
})


  this.triggers.once({
    name: `ws/chat/response/${sessionId}`,
    refresh: true
  }, async ({params}) => {

    // if (params._eventName === 'rwc_continue_main_thread') {
    //   return this.exitStep('next', {})
    // }

    await sendMessage({
      answer: params
    });

    let medias = params.filePath ? typeof params.filePath === 'string' ? params.filePath : params.filePath.map(el => ({location: el})) : null
    medias = medias ? medias : params.image ? [{location: params.image}] : []

    let message = params.message
    if (params.location) {
      const arr = [];
      for (let key in params.location) {
          if (params.location.hasOwnProperty(key)) {
              arr.push(key + ':' + params.location[key]);
          }
      };
      message = arr.join(', ');
    }

    report({
      ...params,
      message,
      medias,
      transcriptId         : transcriptResponseId, 
      botIdentifier        : chatName,
      previousTranscriptId : lastTranscriptId, 
      fromIdentifier       : userId, 
      toIdentifier         : chatName,
      fromType             : 'Visitor',
      fromIdentifierType   : 'BrowserFingerprint',
      reportingSettingsKey : 'responseTranscript',
      toType               : 'Bot',
      toIdentifierType     : 'RWCChatId'
    });

    // this.mergeFields[conversation.value.name].set(transcriptResponseId, 'rwc_conversation.transcriptId')
    if (this.config.botId === conversation.botId) {
      await this.mergeFields[conversation.value.name].set(transcriptResponseId, 'rwc_conversation.transcriptId')
    } else {
      await this.mergeFieldsInstance.set({botId: conversation.botId, name: conversation.value.name, type: conversation.type, path: 'rwc_conversation.transcriptId'}, transcriptResponseId);
    }

    await this.eventManager.callbackResolve(
      this.takeCallback(), {
        received: true
      })

      if (params.answerType === 'text-leg') {
       return this.exitStep('manual response', {
         userReply: params.message,
         userText: params.message,
         requestMessageId: transcriptRequestId,
         responseMessageId: transcriptResponseId,
       });
      }

      delete params.answerComponent
      delete params.callback
      delete params.message

      rwcContext.set('last', conversation);
      await rwcContext.save();

      if (params.leg) {
        return this.exitStep(params.leg, {
          ...params,
          requestMessageId: transcriptRequestId,
          responseMessageId: transcriptResponseId,
        })
      }

      return this.exitStep('next', {
        ...params,
        requestMessageId: transcriptRequestId,
        responseMessageId: transcriptResponseId,
      });
  })
// }

// wait for request to sign urls
this.triggers.once({
  name: `ws/chat/getSignedUrl/${sessionId}`,
  refresh: true
}, async ({params}) => {
  let signedUrls;
  try {
    signedUrls = await getUploadUrl( params );

    await this.eventManager.callbackResolve(
    this.takeCallback(), 
    {
      signedUrls: signedUrls.map(el => el.data)
    })
  } catch (e) {
    await this.eventManager.callbackResolve(
    this.takeCallback(), 
    {
      signedUrls: null,
      status: 'File size is too large' // not really so, need fixes
    })
  }
});

const errorHandler = async event => {
  throw new Error(event.params.textMessage)
};
this.triggers.once({
  name: `ws/chat/response_error/${sessionId}`,
  refresh: true
}, errorHandler)

const sendNotification = async ({ id, message }) => {
  const target = this.helpers.providersAccountId;
  let body
  if (message) {
    body = message.length > 200 ? message.substring(0, 197) + '...' : message
  } else {
    body = ''
  }

  this.emitSync({
    name: `${PROVIDER_VERSION}/app/push/send`,
    target,
    params: {
      appId: appId,
      accountDeviceId: deviceId,
      body: body || 'You have a new message',
      context: {
        type: NOTIFICATION_CTX_TYPE,
        messageId: id
      },
      title: NOTIFICATION_TITLE,
      type: PUSH_REPLY_TYPE_TEXT,
    }
  })
  .then(({result}) => {
    if (_.has(result, PUSH_RESULT_ERROR)) {
      throw result.error
    }

    return this.exitStep(STEP_EXIT_NEXT, {
      notificationId: result.id
    })
  })
  .catch(e => {
    this.log.warn(e)
  })
}

return this.triggers.otherwise(async () => {
  const { answerComponent, isHasMessage, isHasMedias } = mapDataToClient(inputComponent)

  if (_.trim(inputComponent, '`') === `custom`) {
    if(_.trim(customTemplateType, '`') === `vue`) {
      const result = await answerComponent.vBind.vueScript();
      answerComponent.vBind.vueScript = JSON.stringify(result, function(key, value) {
        if (typeof value === 'function') {
          return _.toString(value)
        } else {
          return value;
        }
      });

      const showComponentInMessageLocal = _.get(result, 'showComponentInMessage');

      answerComponent.showComponentInMessage = _.isUndefined(showComponentInMessageLocal) ? showCustomComponentInMessage : showComponentInMessageLocal 
    } else {
      answerComponent.showComponentInMessage = showCustomComponentInMessage;
    }
  }

  const event = {
    stepId: savedStepId,
    id: uuid.v4(),
    message: isHasMessage ? textMessage : '',
    medias: isHasMedias ? medias : [],
    useCustomAnswer,
    userAnswerFunction: userAnswerFunction ? userAnswerFunction.toString() : null,
    userAnswerFunctionParams: dataOutput,
    answerComponent,
    additionalContent: {
      additionalContentCardChangeType,
      additionalHtml,
      additionalCss,
      additionalScript: additionalScript ? additionalScript.toString() : '',
      additionalCardvariables,
      videoSrc: additionalContentVideo
    }
  }


  if (autoSuggestion.input) {
    event.suggestions = _.isEmpty(autoSuggestion.options) ? autoSuggestionOptionsList : autoSuggestion.options
    event.isSuggestionInput = autoSuggestion.input
    event.placeholder = textPlaceholder
    event.suggestionsType = autoSuggestionType
    event.suggestionsFromMergeField = autoSuggestionOptionsMergeField
  }

  // if (answerComponent.component !== 'or-textbox') {
  //   answerComponent.vBind.allowMask = false
  // }


  if (!_.isEmpty(event)) {
    await sendMessage(event)

    if (deviceId) {
      await sendNotification(event)
    }

    // add message to queue
    this.emitAsync({
      name : 'rwc/queue_provider',
      target: this.helpers.providersAccountId,
      params : {
        sessionId: sessionId,
        type: 'async',
        method: 'post',
        message: {
          message: textMessage || '',
          answerComponent,
          id: event.id,
          stepId: event.stepId,
          medias: medias,
          isSuggestionInput: autoSuggestion.input,
          placeholder: textPlaceholder,
          suggestions: autoSuggestion.options,
          time: Date.now()
        }
      }
    })
  }

  report({
    ...event,
    medias               : medias || videoSrc,
    message              : event.message || '',
    botIdentifier        : chatName,
    transcriptId         : transcriptRequestId, 
    previousTranscriptId : lastTranscriptId, 
    fromIdentifier       : chatName, 
    toIdentifier         : userId,
    fromType             : 'Bot',
    fromIdentifierType   : 'RWCChatId',
    reportingSettingsKey : 'requestTranscript',
    toType               : 'Visitor',
    toIdentifierType     : 'BrowserFingerprint'
  });

  this.exitFlow()
});

