data.toJson:
 function toJson(data, key) { 
 if (_.isArray(data)) { 
    return `[${_.map(data, toJson).join(',')}]`;
  }

    if (_.isObject(data)) {
        return `{${_.map(data, (value, key) => `${key}: ${toJson(value, key)}`).join(',')}}`;
    }

    if (/\$\{.+?\}/.test(data)) {

      if(key !== 'html') return data;

      const regex = /\$\{(?:\{??[^\[]*?\})/gm;
      const str = data;
      let m;
      let mergeFields = [];

      while ((m = regex.exec(str)) !== null) {
        if (m.index === regex.lastIndex) {
            regex.lastIndex++;
        }
        m.forEach((match, groupIndex) => {
            mergeFields.push(match)
        });
      }

      const safeString = data.replaceAll(/\$\{(?:\{??[^\[]*?\})/gm, '__$MERGE_FIELD_PLACEHOLDER$__');

      let replacedBackticks = safeString.replaceAll('`', '&#x60;');


      _.forEach(mergeFields, mergeField => {
        replacedBackticks = replacedBackticks.replace('__$MERGE_FIELD_PLACEHOLDER$__', mergeField);
      })

      return `\`${replacedBackticks}\``;
    }

    return JSON.stringify(data);
}

return _.mapValues(data, toJson);

