data.componentLogic:
{
    data() {
      return {
        loading: false,
        flatTemplatesList: [],
        customTemplates: []
      }
    },

    created() {
      eventHub.$on('lib-templates-list::refresh', this.refreshTemplatesList);
    },

    beforeDestroy() {
      eventHub.$off('lib-templates-list::refresh', this.refreshTemplatesList);
    },

    mounted() {
      this.refreshTemplatesList();
    },

    computed : {
        localValue : {
            get () { return this.value; },
            set (value) { 
              if(this.value === value) return;

              this.$emit('input', value); 

              this.setTemplateSources(value);
              eventHub.$emit('lib-templates-list::selected', _.find(this.flatTemplatesList, { value }));
            }
        },
        canRevertChanges () {
          if(!this.localValue || _.size(this.flatTemplatesList) === 0) return false;
          const valueInList = _.find(this.flatTemplatesList, { value: this.value });

          return Boolean(valueInList);
        }
    },


    methods: {

      openModal (modalRef) {
        if(!this.$refs[modalRef]) return;
          this.$refs[modalRef].open();
      },

      closeModal (modalRef) {
        if(!this.$refs[modalRef]) return;
        this.$refs[modalRef].close();
      },

      async revertTemplateChanges () {
        if(!this.value) return;

        await this.setTemplateSources(this.localValue);
        this.closeModal('confirmRevertModal');
      },

      transformTemplatesToGroups(templates) {
        const groupedObj = _.groupBy(templates, 'type');

        return _.map(_.toPairs(groupedObj), ([ key, value ]) => ({ groupLabel: key, items: _.orderBy(value, ['label'], ['asc']) }));
      },

      async refreshTemplatesList(templateId = null) {
        this.loading = true;
        const files = await this.getTemplates();

        const templates = await this.getTemplatesMeta(files);

        this.flatTemplatesList = templates;

        this.customTemplates = this.transformTemplatesToGroups(templates);

        if(templateId) {
          this.localValue = templateId;
        }

        this.loading = false;
        eventHub.$emit('lib-templates-list::refresh-finished', this.flatTemplatesList);
        if(this.localValue) {
          eventHub.$emit('lib-templates-list::selected', _.find(this.flatTemplatesList, { value: this.localValue })); 
        }
      },

      async getTemplates () {
        return await this.$flow.api.files.ls({ isPublic: true, prefix: 'RWC_custom_templates/' })
      },

      async getTemplatesMeta (templateKeys) {
          const promises = [];
          const keys = [];

          _.forEach(templateKeys, ({ key }) => {
            promises.push(fetch(`${this.$flow.filesApiUrl}/public/${this.$flow.accountId}/${key}meta.json`));
            keys.push(_.last(_.split(key, '/').slice(0, -1)));
          });

          const results = await Promise.all(promises);


          const data = _.map(results, result => result.json());
          const templateMetas = await Promise.all(data);

          return _.map(templateMetas, (meta, index) => ({ value: keys[index], ...meta }));

      },

      async setTemplateSources (key) {
        const templateMeta = _.find(this.flatTemplatesList, ({ value }) => value === key);

        if(!templateMeta) return;

        this.updateTemplateType(templateMeta.type);
        this.updateCustomVars(templateMeta.vars);
        this.updateScripts(templateMeta.scripts);

        this.updateControls({ 
          hideCustomComponentAfterSubmit: templateMeta.hideCustomComponentAfterSubmit, 
          showCustomComponentInMessage: templateMeta.showCustomComponentInMessage, 
          autoSubmitCustomTemplate: templateMeta.autoSubmitCustomTemplate, 
          showBotIcon: templateMeta.showBotIcon
        });


        if(templateMeta.type === `html`) {
          const [ html, css, js ] = await this.getHtmlTemplateFiles(key);
          console.log(html, css, js);
          this.updateCustomTemplateSources({ html, css, js });
          return;
        }

        if(templateMeta.type === `vue`) {
          console.log(await this.getVuetemplateFiles(key));
          let [ vueTemplate, vueScript, css ] = await this.getVuetemplateFiles(key);

          if(!vueTemplate) vueTemplate = '';
          if(!css) css = '';

          this.updateCustomTemplateSources({ vueTemplate, vueScript, css });
          return;
        }

        throw new Error(`Unsupported template type: ${templateMeta.type}`);
      },

      updateControls({ hideCustomComponentAfterSubmit = false, showCustomComponentInMessage = true, autoSubmitCustomTemplate = true, showBotIcon = true }) {
        if(!_.isBoolean(hideCustomComponentAfterSubmit)) {
          this.$emit('update:hideCustomComponentAfterSubmit', false);
        } else {
          this.$emit('update:hideCustomComponentAfterSubmit', hideCustomComponentAfterSubmit);
        }

        if(!_.isBoolean(showCustomComponentInMessage)) {
          this.$emit('update:showCustomComponentInMessage', false);
        } else {
          this.$emit('update:showCustomComponentInMessage', showCustomComponentInMessage);
        }

        if(!_.isBoolean(autoSubmitCustomTemplate)) {
          this.$emit('update:autoSubmitCustomTemplate', false);
        } else {
          this.$emit('update:autoSubmitCustomTemplate', autoSubmitCustomTemplate);
        }

        if(!_.isBoolean(showBotIcon)) {
          this.$emit('update:showBotIcon', false);
        } else {
          this.$emit('update:showBotIcon', showBotIcon);
        }

      },

      async getHtmlTemplateFiles(key) {
        const link = `${this.$flow.filesApiUrl}/public/${this.$flow.accountId}/RWC_custom_templates/${key}`;
        this.loading = true;

        try {
          const promises = [];
          promises.push(fetch(`${link}/index.html`));
          promises.push(fetch(`${link}/index.css`));
          promises.push(fetch(`${link}/index.js`));

          const results = await Promise.all(promises);

          const data = _.map(results, result => result.text());
          return await Promise.all(data);
        } finally {
          this.loading = false; 
        }
      },

      async getVuetemplateFiles (key) {
        const link = `${this.$flow.filesApiUrl}/public/${this.$flow.accountId}/RWC_custom_templates/${key}`;
        this.loading = true;

        try {
          const promises = [];
          promises.push(fetch(`${link}/index.vue`));
          promises.push(fetch(`${link}/index.js`));
          promises.push(fetch(`${link}/index.css`));

          const results = await Promise.allSettled(promises);

          const data = [];

          for(let response of results) {
              response = response.value();
              // console.log('response ->', response);
              if(response.status === 200) {
                  const responseData = await response.text();
                  data.push(responseData);
              } else {
                  data.push(null);
              }
          }

          return data;
        } finally {
          this.loading = false; 
        }
      },

      isError (string) {
        const parser = new DOMParser();
        const dom = parser.parseFromString(string, "application/xml");

        return dom.documentElement.nodeName === `Error`;
      },

      updateScripts (scripts) {
        if(!_.isArray(scripts) || !_.size(scripts)) { 
          this.$emit('update:customTempalateScripts', []);
          return; 
        }

        this.$emit('update:customTempalateScripts', scripts);
      },

      updateCustomVars (vars) {
        if(!_.isArray(vars) || !_.size(vars)) {
          this.$emit('update:customVariables', []);
          return;
        }

        this.$emit('update:customVariables', vars);
      },

      updateShowBotIcon(showBotIcon) {
        this.$emit('update:showBotIcon', showBotIcon);
      },

      updateTemplateType (type) {
        if(!type) throw new Error('Type of template should be present');
        this.$emit('update:customTemplateType', `\`${type}\``);
      },

      updateCustomTemplateSources({ html = ``, css = ``, js = ``, vueTemplate = ``, vueScript = `` }) {
        if(html && !this.isError(html))  { 
          this.$emit('update:customTemplateHtmlCode', html); 
        } else { this.$emit('update:customTemplateHtmlCode', ``) }

        if(js && !this.isError(js)) {
          this.$emit('update:customTemplateJavaScriptCode', js); 
        } else { this.$emit('update:customTemplateJavaScriptCode', ``); }

        if(css && !this.isError(css)) {
          this.$emit('update:customTemplateCSSCode', css);
        } else { this.$emit('update:customTemplateCSSCode', ``); }

        if(vueTemplate && !this.isError(vueTemplate)) {
          this.$emit('update:customTemplateVueHtmlTemplate', vueTemplate);
        } else { this.$emit('update:customTemplateVueHtmlTemplate', ``); }

        if(vueScript && !this.isError(vueScript)) {
          this.$emit('update:customTemplateVueScript', vueScript);
        } else { this.$emit('update:customTemplateVueScript', ``); }
      }
    },

    props : {
        value : {
            type : String,
            default : ''
        },
        customTemplateHtmlCode: {
          type: String,
          default: ``
        },
        customTemplateJavaScriptCode: {
          type: String,
          default: ``
        },
        customTemplateCSSCode: {
          type: String,
          default: ``
        },
        customTemplateVueHtmlTemplate: {
          type: String,
          default: ``
        },
        customTemplateVueScript: {
