        },
        hideCustomComponentAfterSubmit: {
          type: Boolean,
          default: false
        },
        showCustomComponentInMessage: {
          type: Boolean,
          default: true
        },
        autoSubmitCustomTemplate: {
          type: Boolean,
          default: true
        },
        isNew : Boolean,
        readonly: {
