data.toJson:
//function that describe how to serialize wildcard data 
function generateProperty(property, key){
  return property ? `${property}.${key}` : key
}

function convertMedias(medias) {
  let filteredMedias = _.filter(medias, media => (_.has(media, 'linkUrl') && media.linkUrl.length && media.linkUrl !== "``") || _.has(media, 'location'));
  let finalMedias = _.map(filteredMedias, media => {
    return media.linkUrl ? `{ location: ${media.linkUrl}, size: ${_.get(media, 'data.Size')} }` : `{ location: ${JSON.stringify(media.location)}, size: ${media.size} }`
  })

  return finalMedias.length ? `[${finalMedias}]` : `[]`;
}

function toJson(data, property = '', path) {
    if ((/medias/g).test(property)) return convertMedias(data)
    if (_.isArray(data)) {
        return `[${_.map(data, d => toJson(d, generateProperty(property, '[]'))).join(',')}]`;
    }
    if (_.isObject(data)) {
        return `{${_.map(data, (value, key) => `${key}: ${toJson(value, generateProperty(property, key))}`).join(',')}}`;
    }
    if (/\$\{.+?\}/.test(data)) return data;
    return JSON.stringify(data);
}

return _.mapValues(data, toJson);

