          if(this.compiling && !this.error)
            return 'info';

          if(!this.compiling && !this.error)
            return 'success';

          return 'error';
        }
    },

    data() {
      return {
        compiling: false,
        error: false,
        showAlert: false
      }
    },

    async mounted() {
      await this.$nextTick();
      await this.compileSCSS();
    },

    methods: {
      generateCSSWrapperClassName() {
        const uniqId = `${uuid.v4()}`.replaceAll('-', '')
        const className = `custom-component-${uniqId}`;
        this.$emit('update:customTemplateCSSWrapperClassName', className);
        return className;
      },

      async compileSCSS() {
        if(this.readonly) return;
        this.showAlert = true;
        this.error = false;

        if(!this.customTemplateCSSWrapperClassName) {
          this.generateCSSWrapperClassName();
        }

        await this.$nextTick();

        const styles = `.${this.customTemplateCSSWrapperClassName} { ${this.value} }`;

      try {
        this.compiling = true;

         const compiledCSS = await this.$flow.api.toolbox.compileStyles({ 
          styles,
          options: {}
        });

        if(compiledCSS.compiled.status !== 0) {
          this.error = true;
        }

        const result = _.get(compiledCSS, 'compiled.text') || '';

        this.$emit('update:customTemplateCompiledCSS', result);
      } catch(e) {
        console.error(e);
        this.error = true;
      } finally {
        this.compiling = false;
      }

      }
    },

    props : {
        value : {
            type : String,
