{
    data() {
      return {
        loading: false,
        templateLabel: ``,
        hasChanges: false,
        templates: [],
        selectedTemplate: null
      }
    },

    created() {
      eventHub.$on('lib-templates-list::refresh-finished', this.stopLoading);
      eventHub.$on('lib-templates-list::selected', this.handleTemplateSelection);
    },

    beforeDestroy() {
      eventHub.$off('lib-templates-list::refresh-finished', this.stopLoading);
      eventHub.$off('lib-templates-list::selected', this.handleTemplateSelection);
    },

    computed: {
      canDeleteTemplateFromLib () {
        return _.trim(this.customTemplateId, ' `"\'')
      },
      labelUniq () {
        if(!this.templateLabel) return true;

        const templateWidthSameLabel = _.find(this.templates, ({ label }) => {
          return _.toLower( _.trim(label)) === _.toLower(_.trim(this.templateLabel))
        });

        return !Boolean(templateWidthSameLabel);
      },
      labelUniqOrSame () {
        if(!this.templateLabel) return true;

        const selectedLabel = _.get(this.selectedTemplate, 'label') || ``;

        const templateWidthSameLabel = _.find(this.templates, ({ label }) => {
          if(_.toLower( _.trim(label)) === _.toLower(_.trim(selectedLabel))) return false;

          return _.toLower( _.trim(label)) === _.toLower(_.trim(this.templateLabel));
        });

        return !Boolean(templateWidthSameLabel);
      }
    },

    methods: {
      stopLoading (templates = []) {
        this.closeModal('confirmPublishModal');
        this.closeModal('confirmDeleteModal');
        this.closeModal('confirmEditModal');

        this.hasChanges = false;
        this.loading = false;

        this.templates = templates;
      },

      handleTemplateSelection (selectedTemplate = {}) {
        this.selectedTemplate = selectedTemplate;
      },

      async publishTemplate () {
        this.loading = true;

        const normalizedTemplateLabel = _.trim(this.templateLabel, ' `"\'');
        const templateId = `${_.snakeCase(normalizedTemplateLabel)}_${uuid.v4()}`;

        const templateType = _.trim(this.customTemplateType, ' `"\'');
        const prefix = `RWC_custom_templates/${templateId}/`;
        const meta = this.getTemplateMetaFile({ 
          type: templateType,
          label: normalizedTemplateLabel,
          vars:  this.customVariables,
          scripts: this.customTempalateScripts,
          autoSubmitCustomTemplate: this.autoSubmitCustomTemplate,
          showCustomComponentInMessage: this.showCustomComponentInMessage,
          hideCustomComponentAfterSubmit: this.hideCustomComponentAfterSubmit
        });

        if(templateType === `vue`) {
          const { vueScript, vueTemplate, css } = this.getTemplateFiles(`vue`);
          try {
            const promises = [];
            promises.push(this.$flow.api.files.uploadFile({ fileModel: { file: vueScript, type: 'text/javascript', name: 'index.js' }, isPublic: true, prefix }));
            promises.push(this.$flow.api.files.uploadFile({ fileModel: { file: vueTemplate, type: 'text/html', name: 'index.vue' }, isPublic: true, prefix }));
            promises.push(this.$flow.api.files.uploadFile({ fileModel: { file: css, type: 'text/css', name: 'index.css' }, isPublic: true, prefix}));
            promises.push(this.$flow.api.files.uploadFile({ fileModel: { file: meta, type: 'application/json', name: 'meta.json' }, isPublic: true, prefix }));

            await Promise.all(promises)
          } catch(e) {
            console.error(e)
          }
        }

        if(templateType === `html`) {
          const { html, js, css } = this.getTemplateFiles (`html`);
          try {
            const promises = [];
            promises.push(this.$flow.api.files.uploadFile({ fileModel: { file: html, type: 'text/html', name: 'index.html' }, isPublic: true, prefix}));
            promises.push(this.$flow.api.files.uploadFile({ fileModel: { file: js, type: 'text/javascript', name: 'index.js' }, isPublic: true, prefix}));
            promises.push(this.$flow.api.files.uploadFile({ fileModel: { file: css, type: 'text/css', name: 'index.css' }, isPublic: true, prefix}));
            promises.push(this.$flow.api.files.uploadFile({ fileModel: { file: meta, type: 'application/json', name: 'meta.json' }, isPublic: true,  prefix}));

            await Promise.all(promises);
          } catch(e) {
            console.error(e)
          }
        }

        eventHub.$emit('lib-templates-list::refresh', templateId);
      },

      getTemplateMetaFile ({ type = `vue`, label = ``, vars = [], scripts = [], hideCustomComponentAfterSubmit = false, showCustomComponentInMessage = true, autoSubmitCustomTemplate = true }) {
        const metaObject = { 
          label, 
          type, 
          vars, 
          scripts,
          autoSubmitCustomTemplate,
          showCustomComponentInMessage,
          hideCustomComponentAfterSubmit,
          showBotIcon: this.showBotIcon,
          lastModified: Date.now()
        };
        return new Blob([JSON.stringify(metaObject, null, 2)], { type: 'text/json', name: `meta.json` });

      },

      getTemplateFiles (type) {
        if(type === `html`) {
          const html = new Blob([this.customTemplateHtmlCode], { type: 'text/html' });
          const js = new Blob([this.customTemplateJavaScriptCode], { type: 'text/javascript' });
          const css = new Blob([this.customTemplateCSSCode], { type: 'text/css' });

          return { html, js, css }
        }

        if(type === `vue`) {
          const vueScript = new Blob([this.customTemplateVueScript], { type: 'text/javascript' });
          const vueTemplate = new Blob([this.customTemplateVueHtmlTemplate], { type: 'text/html' });
          const css = new Blob([this.customTemplateCSSCode], { type: 'text/css' });

          return { vueScript, vueTemplate, css }
        }

        throw new Error('No such type');
      },

      async getFilesList (type) {
        const templateId = _.get(this.selectedTemplate, 'value') || ``;
        return await this.$flow.api.files.ls({ isPublic: true, prefix: `RWC_custom_templates/${templateId}/` })
      },

      async deleteFromLib (withRefresh = true) {
        this.loading = true;
        const files = await this.getFilesList();

        const promises = _.map(files, ({ key }) => this.$flow.api.files.delete(key, true));

        await Promise.all(promises);

        this.$emit('update:customTemplateId', ``);
        if(withRefresh) eventHub.$emit('lib-templates-list::refresh');
        this.hasChanges = false;
        this.localValue = ``;
      },

      async editTemplate() {
        await this.deleteFromLib(false);
        await this.publishTemplate();
      },

      openModal(ref) {
        if(!this.$refs[ref]) return;

        if(ref === 'confirmPublishModal' && _.get(this.selectedTemplate, 'label')) {
          this.templateLabel = `${_.get(this.selectedTemplate, 'label') || ''} (1)`;
        }

        if(ref === 'confirmEditModal') {
          this.templateLabel = _.get(this.selectedTemplate, 'label') || '';
        }

        this.$refs[ref].open(); 
      },

      closeModal (ref) {
      if(!this.$refs[ref]) return;
       this.$refs[ref].close(); 
      },
    },

    watch: {
        customTemplateHtmlCode(nv , ov) {
          if(_.isEqual(nv, ov) || _.trim(this.customTemplateType, ' `"\'')!== 'html') return;
          this.hasChanges = true;
        },
        customTemplateJavaScriptCode(nv , ov) {
          if(_.isEqual(nv, ov) || _.trim(this.customTemplateType, ' `"\'') !== 'html') return;
          this.hasChanges = true;
        },
        customTemplateCSSCode(nv , ov) {
          if(_.isEqual(nv, ov) || _.trim(this.customTemplateType, ' `"\'') !== 'html') return;
          this.hasChanges = true;
        },
        customVariables(nv , ov) {
          if(_.isEqual(nv, ov)) return;
          this.hasChanges = true;
        },
        customTempalateScripts(nv , ov) {
          if(_.isEqual(nv, ov)) return;
          this.hasChanges = true;
        },
        hideCustomComponentAfterSubmit(nv , ov) {
          if(_.isEqual(nv, ov)) return;
          this.hasChanges = true;
        },
        showCustomComponentInMessage(nv , ov) {
          if(_.isEqual(nv, ov)) return;
          this.hasChanges = true;
        },
        autoSubmitCustomTemplate(nv , ov) {
          if(_.isEqual(nv, ov) || this.customTemplateType !== 'html') return;
          this.hasChanges = true;
        },
        customComponent(nv , ov) {
          if(_.isEqual(nv, ov) || _.trim(this.customTemplateType, ' `"\'') !== 'vue') return;
          this.hasChanges = true;
        },
        customTemplateType(nv , ov) {
          if(_.isEqual(nv, ov)) return;
          this.hasChanges = true;
        },
        showBotIcon (nv, ov) {
          if(_.isEqual(nv, ov)) return;
          this.hasChanges = true;
        }
    },

    props : {
