data.componentLogic:
{
    computed : {
        computedValue : {
            get () { return this.value; },
            set (value) { this.$emit('input', value); }
        }
    },
    props : {
        value : {
            type : String,
            default : ''
        },
        inputComponent: String,
        isNew : Boolean,
        readonly: {
            type : Boolean 
        },
        mergeFields: {
            type: Array
        },
        v: Object,
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        stepId: {
            type : String,
            default : ''
        }
    },
    methods: {
      getErrorLabel (index) {
        if (!this.getValue(`schema.dropdownOptions.$each[${index}].emptyLabel`, this.v)) {
          return 'Option label is required.'
        } 
        if (!this.getValue(`schema.dropdownOptions.$each[${index}].labelOutOfLength`, this.v)) {
          return 'Limit is exceeded, text will be truncated'
        }
      },
      getValue (key, val) {
        return _.get(val || this, key)
      },
      addNewItem () {
        this.computedValue.push({
          label: '``', 
          value: '``',
          vforkey: uuid.v4()
        }) 
      },
      newItemMethod () {
        return {
          label: '``', 
          value: '``',
          vforkey: uuid.v4()
        }
      },
      isValueUniq (index) {
          return this.getValue(`schema.dropdownOptions.$each[${index}].notUniqValues`, this.v)
      },
      isValueEmpty (index) {
        return this.getValue(`schema.dropdownOptions.$each[${index}].emptyValue`, this.v)
      },
      isValueInvalid (index) {
        return !this.isValueUniq(index) || !this.isValueEmpty(index);
      },
      getValueErrorMessage(index) {
        if(!this.isValueEmpty(index)) return `Value is required.`
        if(!this.isValueUniq(index)) return `Value must be unique.`

        return ``;
      }
    }
}

