    computed : {
        computedValue : {
            get () { return this.value; },
            set (value) { this.$emit('input', value); }
        }
    },
    watch: {
      options: {
        handler(nv, ov) {
          this.populateOptions()
        },
        deep: true
      }
    },
    props : {
        value : {
            type : String,
            default : ''
        },
        inputComponent: String,
        options: Array,
        isNew : Boolean,
        readonly: {
            type : Boolean 
        },
        mergeFields: {
            type: Array
        },
        steps: {
            type: Array,
            default: () => {
                return []
            }
        },
        stepId: {
            type : String,
            default : ''
        }
    },
    methods: {
      onValueSelect (e) {
        if (this.computedValue === e.value) {
          this.$refs.selectExpression.value = null
          this.computedValue = null
        }
      },
      async populateOptions () {
        await this.$nextTick()
        if (!this.$refs.selectExpression || !this.options) return

        this.$refs.selectExpression.localOptions = [];
        const optionLabels = this.options.map(el => _.trim(el.label, '`'))

        this.options.map(el => {
          if (!el || el.label === '``') return;
          this.$refs.selectExpression.addOption(_.trim(el.label, '`'));
        })

        this.$refs.selectExpression.addOption('`No option`');

        const labels = this.$refs.selectExpression.localOptions.map(el => el.label)
        this.$refs.selectExpression.localOptions = this.$refs.selectExpression.localOptions.map(el => {
          el.custom = false
          return el
        })
        if (labels.indexOf(this.computedValue) !== -1) {
          this.$refs.selectExpression.value = this.value
        } else {
          this.$refs.selectExpression.value = '`No option`'
        }
      }
    }
}

